import React, { Component } from 'react'
import axios from 'axios'
import jQuery from 'jquery'
import { withRouter } from 'react-router-dom'
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";
import Select from 'react-select';

class modifInventaire extends Component {
    constructor(props) {
        super(props);
        this.state = {
            idInventaire: '',
            numeroInventaire: '',
            dateInventaire: '',
            emplacements: [],
            selectedEmplacement: ''
        }
        this.modifnumeroInventaire = this.modifnumeroInventaire.bind(this);
        this.modif = this.modif.bind(this)
    }

    componentDidMount() {
        let id_inventaire = this.props.location.state.id_inventaire;
        axios.get(process.env.REACT_APP_API_URL + 'Back_agora/GetInventaireById.php?id=' + id_inventaire).then(res => {
            console.log(res)
            this.setState({
                idInventaire: res.data[0].id_inventaire,
                numeroInventaire: res.data[0].numeroInventaire,
                dateInventaire: new Date(res.data[0].dateInventaire),
                selectedEmplacement: res.data[1],

            })
        })
        axios.get(process.env.REACT_APP_API_URL + 'Back_agora/EmplacementsSelect.php').then(res => {
            console.log(res);
            this.setState({ emplacements: res.data });
        });
    }

    modifnumeroInventaire(event) {
        this.setState({
            numeroInventaire: event.target.value
        });
    }

    modifDateInventaire = d => {
        this.setState({
            dateInventaire: d
        });
    };

    modifEmplacement = selectedEmplacement => {
        this.setState({ selectedEmplacement });
        console.log(`Emplacement selected:`, selectedEmplacement);
    };

    modif() {

        let dateInventaire = this.state.dateInventaire
        let month_one = '' + (dateInventaire.getMonth() + 1);
        let day_one = '' + dateInventaire.getDate();
        let year_one = dateInventaire.getFullYear();
        if (month_one.length < 2)
            month_one = '0' + month_one;
        if (day_one.length < 2)
            day_one = '0' + day_one;
        let convertDateInventaire = [year_one, month_one, day_one].join('-');
        console.log(convertDateInventaire)

        jQuery.ajax({
            url: process.env.REACT_APP_API_URL + 'Back_agora/UpdInventaire.php?id=' + this.state.idInventaire,
            type: 'POST',
            data: {
                numeroInventaire: this.state.numeroInventaire,
                dateInventaire: convertDateInventaire,
                idEmplacement: this.state.selectedEmplacement.value
            },
            dataType: 'json',
            success: (code_html, statut) => {
                if (code_html.Result == "OK") {
                    this.props.history.push('/Inventaires');
                }
                else if (code_html.Result == "KO") {
                    alert("erreur,l'un de vos champs est vide!!");
                }
            }
        });
    }

    render() {
        return (
            <div id="page-wrapper" style={{ minHeight: '263px' }}>
                <div className="container-fluid">
                    <div className="row bg-title">
                        <div className="col-lg-12">
                            <h4 className="page-title">Modification inventaire</h4>
                        </div>
                        {/* /.col-lg-12 */}
                    </div>
                    {/* /.row */}
                    <div class="row">
                        <div class="col-sm-12">
                            <div class="white-box">
                                <div class="row">
                                    <div class="col-md-6">
                                        <div class="form-group">
                                            <label>Numero</label>
                                            <input type="number" class="form-control" value={this.state.numeroInventaire} onChange={this.modifnumeroInventaire.bind(this)} style={{ borderRadius: '5px' }} />
                                        </div>
                                    </div>
                                    <div class="col-md-6">
                                        <div class="form-group">
                                            <label>Date debut</label>
                                            <DatePicker class="form-control ddate" style={{ border: 'none' }}
                                                selected={this.state.dateInventaire}
                                                onChange={this.modifDateInventaire}
                                            />
                                        </div>
                                    </div>
                                    <div class="col-md-6">
                                        <div class="form-group">
                                            <label>Emplacement</label>
                                            <Select
                                                value={this.state.selectedEmplacement}
                                                isClearable={true}
                                                isSearchable={true}
                                                onChange={this.modifEmplacement}
                                                options={this.state.emplacements}
                                            />
                                        </div>
                                    </div>
                                </div>
                                <div style={{ display: 'flex', justifyContent: 'center', marginBottom: '10px' }}>
                                    <button style={{ width: '15%', borderRadius: '5px' }} type="button" className="btn btn-success" onClick={this.modif.bind(this)}>Confirmer</button>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        );
    }
}

export default withRouter(modifInventaire);
