import React, { Component } from "react";
import jQuery from "jquery";
import { Link, BrowserRouter as Router, Route, Switch, withRouter, HashRouter, Redirect } from 'react-router-dom'

class AjoutNatureNote extends Component {
  constructor(props) {
    super(props);
    this.state = {
      natureNote: "",
    };
  }


  addNatureNote = (event) => {
    this.setState({
      natureNote: event.target.value,
    });
  };

  ajout = () => {
    if (this.state.natureNote != "") {
      var userPermissions = JSON.parse(localStorage.getItem("userPermissions"));
      var anneeScolaire = userPermissions[0].anneeScolaire
      jQuery.ajax({
        url:
          process.env.REACT_APP_API_URL +
          "Back_mediatraining/AjoutNatureNote.php",
        type: "POST",
        data: {
          natureNote: this.state.natureNote,
          anneeScolaire: anneeScolaire,
        },
        dataType: "json",
        success: (code_html, statut) => {
          if (code_html.Result == "OK") {
            this.props.history.push("/NatureNote");
          } else if (code_html.Result == "KO") {
            alert("erreur,l'un de vos champs est vide!!");
          }
        },
      });
    }
  };

  render() {
    return (
      <div id="page-wrapper" style={{ minHeight: "263px", marginLeft: "0px" }}>
        <div className="container-fluid">
          <div className="row bg-title">
            <div className="col-lg-12">
              <h4 className="page-title">Ajout nature note</h4>
            </div>
          </div>
          {/* /.row */}
          <div className="row">
            <div className="col-sm-12">
              <div className="white-box">
                <h3>Rapport des notes :</h3>
                <div className="row">
                  <div className="col-md-6">
                    <div className="form-group">
                      <label>Note</label>
                      <input
                        type="text"
                        className="form-control"
                        placeholder="Nature note"
                        onChange={this.addNatureNote}
                        style={{
                          borderRadius: "5px",
                          border: "solid 1px #B3B3B3",
                        }}
                      />
                    </div>
                    <div
                      style={{
                        display: "flex",
                        justifyContent: "center",
                        marginBottom: "10px",
                      }}
                    >
                      <button
                        style={{ borderRadius: "5px" }}
                        type="button"
                        className="btn btn-success"
                        onClick={this.ajout}
                      >
                        Confirmer
                      </button>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default withRouter(AjoutNatureNote);
