import React, { Component, Fragment } from 'react'
import axios from 'axios'
import jQuery from 'jquery'
import { withRouter } from 'react-router-dom'
import Select from 'react-select';
import { RadioGroup, Radio } from 'react-radio-group'
import Checkbox from 'rc-checkbox';
import 'rc-checkbox/assets/index.css';

class InscripCycle extends Component {
    constructor(props) {
        super(props);
        this.state = {
            listConnaissance: [],
            numInscripCycle: '',
            numCarteAdh: '',
            listTypeFormat: [{ value: 1, label: 'Societé' }, { value: 2, label: 'Particulier' }],
            selectedTypeFiche: 2,
            nbrPersonne: 0,
            matFisc: '',
            nbrPersTenu: 0,
            societe: '',
            responsable: '',
            mobileResp: '',
            age: '',
            lieuResid: [],
            seletedLieuResid: '',
            listNivBase: [],
            selectedNivBase: '',
            facebook: '',
            fonction: '',
            selectedTypeFormat: 2,
            motivation: '',
            numIdentite: '',
            prenom: '',
            nom: '',
            disabled: false,
            connaissChoisi: [],
            listCycle: [],
            selectedCycle: [],
            email: '',
            tel: '',
            fax: '',
            photoVerso: null,
            nbrHeure: '',
            ajoutInscripCycle: false,
            carteFid: '',
            tenu: '',
            propCycle: '',

        }
    }

    componentDidMount() {
        var userPermissions = JSON.parse((localStorage.getItem('userPermissions')));
        var anneeScolaire = userPermissions[0].anneeScolaire
        jQuery.ajax({
            url: process.env.REACT_APP_API_URL + 'Back_mediatraining/NumInscripCycle.php',
            type: 'POST',
            data: {
                anneeScolaire: anneeScolaire
            },
            dataType: 'json',
            success: (code_html, statut) => {
                this.setState({
                    numInscripCycle: "00000" + (parseInt(code_html) + 1).toString()
                })
            }
        });
        jQuery.ajax({
            url: process.env.REACT_APP_API_URL + 'Back_mediatraining/NumAdheCycle.php',
            type: 'POST',
            data: {
                anneeScolaire: anneeScolaire
            },
            dataType: 'json',
            success: (code_html, statut) => {
                this.setState({
                    numCarteAdh: "00" + (parseInt(code_html) + 1).toString(),
                    carteFid: "00" + (parseInt(code_html) + 1).toString()
                })
            }
        });
        axios.get(process.env.REACT_APP_API_URL + 'Back_mediatraining/SelectResid.php').then(res => {
            this.setState({
                lieuResid: res.data
            })
        })
        axios.get(process.env.REACT_APP_API_URL + 'Back_mediatraining/SelectNivBase.php').then(res => {
            console.log(res)
            this.setState({
                listNivBase: res.data
            })
        })
        axios.get(process.env.REACT_APP_API_URL + 'Back_mediatraining/SelectConnaissance.php').then(res => {
            console.log(res)
            this.setState({
                listConnaissance: res.data
            })
        })
        jQuery.ajax({
            url: process.env.REACT_APP_API_URL + 'Back_mediatraining/SelectDetailCycle.php',
            type: 'POST',
            data: {
                anneeScolaire: anneeScolaire
            },
            dataType: 'json',
            success: (code_html, statut) => {
                this.setState({
                    listCycle: code_html
                })
            }
        });
    }

    addType = (selectedTypeFiche) => {
        this.setState({ selectedTypeFiche: selectedTypeFiche });
    }

    addCarteFid = (e) => {
        console.log('Checkbox checked:', (e.target.checked));
        if (e.target.checked === true) {
            this.setState({
                carteFid: ''
            });
        }
        else {
            var numCarteFid = "00" + (parseInt(this.state.numCarteAdh) + 1).toString()
            this.setState({
                carteFid: numCarteFid
            });
        }
    }

    addTenu = (e) => {
        console.log('Checkbox checked:', (e.target.checked));
        if (e.target.checked === true) {
            this.setState({
                tenu: true
            });
        }
        else {
            this.setState({
                tenu: false
            });
        }
    }

    addNbrPersonne = (event) => {
        this.setState({
            nbrPersonne: event.target.value
        });
    }

    addMatFisc = (event) => {
        this.setState({
            matFisc: event.target.value
        });
    }

    addNbrPersTenu = (event) => {
        this.setState({
            nbrPersTenu: event.target.value
        });
    }

    addSociete = (event) => {
        this.setState({
            societe: event.target.value
        });
    }

    addResponsable = (event) => {
        this.setState({
            responsable: event.target.value
        });
    }

    addMobile = (event) => {
        this.setState({
            mobileResp: event.target.value
        });
    }

    addAge = (event) => {
        this.setState({
            age: event.target.value
        });
    }

    addLieuResid = (seletedLieuResid) => {
        this.setState({ seletedLieuResid: seletedLieuResid });
    }

    addNivBase = selectedNivBase => {
        this.setState({ selectedNivBase });
    };

    addEmail = (event) => {
        this.setState({
            email: event.target.value
        });
    }

    addTel = (event) => {
        this.setState({
            tel: event.target.value
        });
    }

    addFax = (event) => {
        this.setState({
            fax: event.target.value
        });
    }

    addFacebook = (event) => {
        this.setState({
            facebook: event.target.value
        });
    }

    addFct = (event) => {
        this.setState({
            fonction: event.target.value
        });
    }

    addPhotoVerso(e) {
        this.setState({ photoVerso: e.target.files })
    }

    addTypeFormat = (selectedTypeFormat) => {
        this.setState({ selectedTypeFormat: selectedTypeFormat });
    }

    addMotivation = (event) => {
        this.setState({
            motivation: event.target.value
        });
    }

    addNumIdentite = (event) => {
        this.setState({
            numIdentite: event.target.value
        });
    }

    addPrenom = (event) => {
        this.setState({
            prenom: event.target.value
        });
    }

    addNom = (event) => {
        this.setState({
            nom: event.target.value
        });
    }

    addConnaiss = (e, el) => {
        console.log('Checkbox checked:', (e.target.checked));
        if (e.target.checked === true) {
            let element = el.value
            this.setState({
                connaissChoisi: [...this.state.connaissChoisi, element]
            });
        }
        else {
            let element = el.value
            let indice = this.state.connaissChoisi.indexOf(element)
            this.state.connaissChoisi.splice(indice, 1)
        }
    }

    addCycle = selectedCycle => {
        this.setState({ selectedCycle });
    };

    addNbrHeure = (event) => {
        this.setState({
            nbrHeure: event.target.value
        });
    }

    addPropCycle = (event) => {
        this.setState({
            propCycle: event.target.value
        });
    }

    ajoutInscripCycle = () => {
        var userPermissions = JSON.parse((localStorage.getItem('userPermissions')));
        var anneeScolaire = userPermissions[0].anneeScolaire
        var userId = userPermissions[0].idUser
        // Test si photo vide
        var pathPhotoVerso = '';
        if (this.state.photoVerso != null) {
            pathPhotoVerso = "images/Photo_eleves_cycles/" + this.state.photoVerso[0].name
        }
        var detailCycles = []
        for (var y = 0; y < this.state.selectedCycle.length; y++) {
            detailCycles.push(this.state.selectedCycle[y].value)
        }
        detailCycles = detailCycles.toString()
        var conaissanceChoix = this.state.connaissChoisi.toString()
        // if (this.state.selectedTypeFiche == 1) {
        //     jQuery.ajax({
        //         url: process.env.REACT_APP_API_URL + 'Back_mediatraining/AddInscripCycle.php',
        //         type: 'POST',
        //         data: {
        //             numIdentite: this.state.numIdentite,
        //             prenom: this.state.prenom,
        //             numInscripCycle: this.state.numInscripCycle,
        //             numCarteFid: this.state.carteFid,
        //             matFisc: this.state.matFisc,
        //             nbrPersonne: this.state.nbrPersonne,
        //             nbrPersTenu: this.state.nbrPersTenu,
        //             nom: this.state.societe,
        //             responsable: this.state.responsable,
        //             mobileResp: this.state.mobileResp,
        //             age: this.state.age,
        //             lieuResidId: this.state.seletedLieuResid.value,
        //             niveauBase: this.state.selectedNivBase.value,
        //             email: this.state.email,
        //             tel: this.state.tel,
        //             fax: this.state.fax,
        //             facebook: this.state.facebook,
        //             fctActu: this.state.fonction,
        //             pathPhoto: pathPhotoVerso,
        //             typeFormat: this.state.selectedTypeFormat,
        //             typeFiche: this.state.selectedTypeFiche,
        //             nbrHeure: this.state.nbrHeure,
        //             motivation: this.state.motivation,
        //             connaissChoisi: conaissanceChoix,
        //             cycleChoisi: detailCycles,
        //             anneeScolaire: anneeScolaire,
        //             userId: userId
        //         },
        //         dataType: 'json',
        //         success: (code_html, statut) => {
        //             if (code_html.Result == "OK") {
        //                 this.setState({
        //                     ajoutInscripCycle: true
        //                 });
        //             }
        //             else {
        //                 alert("prbl lors de l'insertion")
        //             }
        //         }
        //     });
        // }
        // else if (this.state.selectedTypeFiche == 2) {
        //     var persAvecTenu = 0
        //     if (this.state.tenu == true) {
        //         var persAvecTenu = 1
        //     }
        //     jQuery.ajax({
        //         url: process.env.REACT_APP_API_URL + 'Back_mediatraining/AddInscripCycle.php',
        //         type: 'POST',
        //         data: {
        //             numInscripCycle: this.state.numInscripCycle,
        //             numCarteFid: this.state.carteFid,
        //             numIdentite: this.state.numIdentite,
        //             prenom: this.state.prenom,
        //             nom: this.state.nom,
        //             age: this.state.age,
        //             nbrPersonne: 1,
        //             nbrPersTenu: persAvecTenu,
        //             lieuResidId: this.state.seletedLieuResid.value,
        //             niveauBase: this.state.selectedNivBase.value,
        //             email: this.state.email,
        //             tel: this.state.tel,
        //             fax: this.state.fax,
        //             facebook: this.state.facebook,
        //             fctActu: this.state.fonction,
        //             pathPhoto: pathPhotoVerso,
        //             typeFormat: this.state.selectedTypeFormat,
        //             typeFiche: this.state.selectedTypeFiche,
        //             motivation: this.state.motivation,
        //             connaissChoisi: conaissanceChoix,
        //             cycleChoisi: detailCycles,
        //             anneeScolaire: anneeScolaire,
        //             userId: userId
        //         },
        //         dataType: 'json',
        //         success: (code_html, statut) => {
        //             if (code_html.Result == "OK") {
        //                 alert('insertion cv')
        //                 this.setState({
        //                     ajoutInscripCycle: true
        //                 });
        //             }
        //             else {
        //                 alert("prbl lors de l'insertion")
        //             }
        //         }
        //     });
        // }
        // // Test si photo vide
        // if (this.state.photoVerso != null) {
        //     let files = this.state.photoVerso;
        //     console.warn('data files', files)
        //     let reader = new FileReader();
        //     reader.readAsDataURL(files[0])
        //     reader.onload = (e) => {
        //         console.log(e)
        //         jQuery.ajax({
        //             url: process.env.REACT_APP_API_URL + 'Back_mediatraining/UploadphotoInscripCycle.php',
        //             type: 'POST',
        //             data: {
        //                 file: e.target.result,
        //                 nomfile: this.state.photoVerso[0].name
        //             },
        //             dataType: 'json',
        //             success: (code_html, statut) => {
        //             }
        //         }
        //         )
        //     }
        // }
    }

    render() {
        return (
            <div id="page-wrapper" style={{ minHeight: '263px', marginLeft: '0px' }}>
                <div className="container-fluid">
                    <div className="row bg-title">
                        <div className="col-lg-12">
                            <h4 className="page-title">Inscription cycle</h4>
                        </div>
                        {/* /.col-lg-12 */}
                    </div>
                    {/* /.row */}
                    <div className="row">
                        <div className="col-sm-12">
                            <div className="white-box">
                                <div className="row">
                                    <div className="col-md-6">
                                        <div className="form-group">
                                            <label>Inscription N°</label>
                                            <input type="text" className="form-control" disabled value={this.state.numInscripCycle} style={{ borderRadius: '5px', border: 'solid 1px', borderColor: '#B3B3B3' }} />
                                        </div>
                                    </div>
                                    {this.state.carteFid != '' ?
                                        (<div className="col-md-6">
                                            <div className="form-group">
                                                <label>Carte d'adhesion N°</label>
                                                <input type="text" className="form-control" disabled value={this.state.numCarteAdh} style={{ borderRadius: '5px', border: 'solid 1px', borderColor: '#B3B3B3' }} />
                                            </div>
                                        </div>) : (null)}
                                </div>
                                <div className="row">
                                    <div className="col-md-6">
                                        <RadioGroup
                                            name="typeFormat"
                                            selectedValue={this.state.selectedTypeFiche}
                                            onChange={this.addType}>
                                            <div>
                                                {this.state.listTypeFormat.map(el =>
                                                    <label style={{ paddingRight: '10px' }}>
                                                        <Radio value={el.value} />{el.label}
                                                    </label>
                                                )}
                                            </div>
                                        </RadioGroup>
                                    </div>
                                </div>
                                <div className="row">
                                    <div className="col-md-6">
                                        <div className="form-group">
                                            <label>Carte fidélité</label>
                                            <span style={{ display: 'flex', flexWrap: 'wrap', padding: '10px', borderRadius: '5px', border: 'solid 1px #B3B3B3' }}>
                                                <label>
                                                    <Checkbox
                                                        onChange={(e) => this.addCarteFid(e)}
                                                        disabled={this.state.disabled}
                                                    />
                                                    Sans carte fidélité
                                                </label>
                                            </span>
                                        </div>
                                    </div>
                                    <div className="col-md-6">
                                        <div className="form-group">
                                            <label>Tenu</label>
                                            <span style={{ display: 'flex', flexWrap: 'wrap', padding: '10px', borderRadius: '5px', border: 'solid 1px #B3B3B3' }}>
                                                <label>
                                                    <Checkbox
                                                        onChange={(e) => this.addTenu(e)}
                                                        disabled={this.state.disabled}
                                                    />
                                                    Avec tenu
                                                </label>
                                            </span>
                                        </div>
                                    </div>
                                </div>
                                {this.state.selectedTypeFiche == 1 ?
                                    (<div className="row">
                                        <div className="col-md-6">
                                            <div className="form-group">
                                                <label>Matricule fiscale</label>
                                                <input type="text" className="form-control" placeholder={"Matricule Fiscale"} onChange={this.addMatFisc} style={{ borderRadius: '5px', border: 'solid 1px', borderColor: '#B3B3B3' }} />
                                            </div>
                                        </div>
                                        <div className="col-md-6">
                                            <div className="form-group">
                                                <label>Nombre de personne</label>
                                                <input type="number" className="form-control" onChange={this.addNbrPersonne} style={{ borderRadius: '5px', border: 'solid 1px', borderColor: '#B3B3B3' }} />
                                            </div>
                                        </div>
                                        <div className="col-md-6">
                                            <div className="form-group">
                                                <label>Nombre de personne avec tenu</label>
                                                <input type="number" className="form-control" onChange={this.addNbrPersTenu} style={{ borderRadius: '5px', border: 'solid 1px', borderColor: '#B3B3B3' }} />
                                            </div>
                                        </div>
                                        <div className="col-md-6">
                                            <div className="form-group">
                                                <label>Société</label>
                                                <input type="text" className="form-control" placeholder={"Société"} onChange={this.addSociete} style={{ borderRadius: '5px', border: 'solid 1px', borderColor: '#B3B3B3' }} />
                                            </div>
                                        </div>
                                        <div className="col-md-6">
                                            <div className="form-group">
                                                <label>Responsable</label>
                                                <input type="text" className="form-control" placeholder={"Responsable"} onChange={this.addResponsable} style={{ borderRadius: '5px', border: 'solid 1px', borderColor: '#B3B3B3' }} />
                                            </div>
                                        </div>
                                        <div className="col-md-6">
                                            <div className="form-group">
                                                <label>Mobile</label>
                                                <input type="number" className="form-control" onChange={this.addMobile} style={{ borderRadius: '5px', border: 'solid 1px', borderColor: '#B3B3B3' }} />
                                            </div>
                                        </div>
                                        <div className="col-md-6">
                                            <div className="form-group">
                                                <label>Age</label>
                                                <input type="number" className="form-control" onChange={this.addAge} style={{ borderRadius: '5px', border: 'solid 1px', borderColor: '#B3B3B3' }} />
                                            </div>
                                        </div>
                                        <div className="col-md-6">
                                            <div className="form-group">
                                                <label>Lieu résidence</label>
                                                <Select
                                                    isClearable={true}
                                                    isSearchable={true}
                                                    onChange={this.addLieuResid}
                                                    options={this.state.lieuResid}
                                                />
                                            </div>
                                        </div>
                                        <div className="col-md-6">
                                            <div className="form-group">
                                                <label>Niveau inscription</label>
                                                <Select
                                                    isClearable={true}
                                                    isSearchable={true}
                                                    onChange={this.addNivBase}
                                                    options={this.state.listNivBase}
                                                />
                                            </div>
                                        </div>
                                        <div className="col-md-6">
                                            <div className="form-group">
                                                <label>Email</label>
                                                <input type="text" className="form-control" placeholder="Email" onChange={this.addEmail} style={{ borderRadius: '5px', border: 'solid 1px', borderColor: '#B3B3B3' }} />
                                            </div>
                                        </div>
                                        <div className="col-md-6">
                                            <div className="form-group">
                                                <label>Tel</label>
                                                <input type="number" className="form-control" onChange={this.addTel} style={{ borderRadius: '5px', border: 'solid 1px', borderColor: '#B3B3B3' }} />
                                            </div>
                                        </div>
                                        <div className="col-md-6">
                                            <div className="form-group">
                                                <label>Fax</label>
                                                <input type="number" className="form-control" onChange={this.addFax} style={{ borderRadius: '5px', border: 'solid 1px', borderColor: '#B3B3B3' }} />
                                            </div>
                                        </div>
                                        <div className="col-md-6">
                                            <div className="form-group">
                                                <label>Facebook</label>
                                                <input type="text" className="form-control" placeholder={"Facebook"} onChange={this.addFacebook} style={{ borderRadius: '5px', border: 'solid 1px', borderColor: '#B3B3B3' }} />
                                            </div>
                                        </div>
                                        <div className="col-md-6">
                                            <div className="form-group">
                                                <label>Fonction actuelle</label>
                                                <input type="text" className="form-control" placeholder={"Fonction actuelle"} onChange={this.addFct} style={{ borderRadius: '5px', border: 'solid 1px', borderColor: '#B3B3B3' }} />
                                            </div>
                                        </div>
                                        <div className="col-md-6">
                                            <div className="form-group">
                                                <label>Photo verso </label>
                                                <input type="file" className="form-control" name="file" onChange={(e) => this.addPhotoVerso(e)} style={{ borderRadius: '5px', border: 'solid 1px', borderColor: '#B3B3B3' }} />
                                            </div>
                                        </div>
                                        <div className="col-md-6">
                                            <div className="form-group">
                                                <label>Motivation</label>
                                                <input type="text" className="form-control" onChange={this.addMotivation} style={{ borderRadius: '5px', border: 'solid 1px', borderColor: '#B3B3B3' }} />
                                            </div>
                                        </div>
                                        <div className="col-md-6">
                                            <label>Type Formation</label>
                                            <RadioGroup
                                                name="typeFormat"
                                                selectedValue={this.state.selectedTypeFormat}
                                                onChange={this.addTypeFormat}>
                                                <div>
                                                    <label>
                                                        <Radio value={1} />Indviduelle
                                                </label>
                                                    <label>
                                                        <Radio value={2} />Cycle
                                                </label>
                                                </div>
                                            </RadioGroup>
                                        </div>
                                        {this.state.selectedTypeFormat == 1 ?
                                            (<div className="col-md-6">
                                                <div className="form-group">
                                                    <label>Nombre d'heures</label>
                                                    <input type="number" className="form-control" onChange={this.addNbrHeure} style={{ borderRadius: '5px', border: 'solid 1px', borderColor: '#B3B3B3' }} />
                                                </div>
                                            </div>) : (null)}
                                    </div>) :
                                    (<div className="row">
                                        <div className="col-md-6">
                                            <div className="form-group">
                                                <label>Numéro Identité</label>
                                                <input type="number" className="form-control" placeholder={"Numéro identité"} onChange={this.addNumIdentite} style={{ borderRadius: '5px', border: 'solid 1px', borderColor: '#B3B3B3' }} />
                                            </div>
                                        </div>
                                        <div className="col-md-6">
                                            <div className="form-group">
                                                <label>Prénom</label>
                                                <input type="text" className="form-control" placeholder={"Prenom"} onChange={this.addPrenom} style={{ borderRadius: '5px', border: 'solid 1px', borderColor: '#B3B3B3' }} />
                                            </div>
                                        </div>
                                        <div className="col-md-6">
                                            <div className="form-group">
                                                <label>Nom</label>
                                                <input type="text" className="form-control" placeholder={"Nom"} onChange={this.addNom} style={{ borderRadius: '5px', border: 'solid 1px', borderColor: '#B3B3B3' }} />
                                            </div>
                                        </div>
                                        <div className="col-md-6">
                                            <div className="form-group">
                                                <label>Age</label>
                                                <input type="number" className="form-control" onChange={this.addAge} style={{ borderRadius: '5px', border: 'solid 1px', borderColor: '#B3B3B3' }} />
                                            </div>
                                        </div>
                                        <div className="col-md-6">
                                            <div className="form-group">
                                                <label>Lieu résidence</label>
                                                <Select
                                                    isClearable={true}
                                                    isSearchable={true}
                                                    onChange={this.addLieuResid}
                                                    options={this.state.lieuResid}
                                                />
                                            </div>
                                        </div>
                                        <div className="col-md-6">
                                            <div className="form-group">
                                                <label>Niveau inscription</label>
                                                <Select
                                                    isClearable={true}
                                                    isSearchable={true}
                                                    onChange={this.addNivBase}
                                                    options={this.state.listNivBase}
                                                />
                                            </div>
                                        </div>
                                        <div className="col-md-6">
                                            <div className="form-group">
                                                <label>Email</label>
                                                <input type="text" className="form-control" placeholder="Email" onChange={this.addEmail} style={{ borderRadius: '5px', border: 'solid 1px', borderColor: '#B3B3B3' }} />
                                            </div>
                                        </div>
                                        <div className="col-md-6">
                                            <div className="form-group">
                                                <label>Tel</label>
                                                <input type="number" className="form-control" onChange={this.addTel} style={{ borderRadius: '5px', border: 'solid 1px', borderColor: '#B3B3B3' }} />
                                            </div>
                                        </div>
                                        <div className="col-md-6">
                                            <div className="form-group">
                                                <label>Fax</label>
                                                <input type="number" className="form-control" onChange={this.addFax} style={{ borderRadius: '5px', border: 'solid 1px', borderColor: '#B3B3B3' }} />
                                            </div>
                                        </div>
                                        <div className="col-md-6">
                                            <div className="form-group">
                                                <label>Facebook</label>
                                                <input type="text" className="form-control" placeholder={"Facebook"} onChange={this.addFacebook} style={{ borderRadius: '5px', border: 'solid 1px', borderColor: '#B3B3B3' }} />
                                            </div>
                                        </div>
                                        <div className="col-md-6">
                                            <div className="form-group">
                                                <label>Fonction actuelle</label>
                                                <input type="text" className="form-control" placeholder={"Fonction actuelle"} onChange={this.addFct} style={{ borderRadius: '5px', border: 'solid 1px', borderColor: '#B3B3B3' }} />
                                            </div>
                                        </div>
                                        <div className="col-md-6">
                                            <div className="form-group">
                                                <label>Photo verso </label>
                                                <input type="file" className="form-control" name="file" onChange={(e) => this.addPhotoVerso(e)} style={{ borderRadius: '5px', border: 'solid 1px', borderColor: '#B3B3B3' }} />
                                            </div>
                                        </div>
                                        <div className="col-md-6">
                                            <div className="form-group">
                                                <label>Motivation</label>
                                                <input type="text" className="form-control" onChange={this.addMotivation} style={{ borderRadius: '5px', border: 'solid 1px', borderColor: '#B3B3B3' }} />
                                            </div>
                                        </div>
                                        <div className="col-md-6">
                                            <label>Type Formation</label>
                                            <RadioGroup
                                                name="typeFormat"
                                                selectedValue={this.state.selectedTypeFormat}
                                                onChange={this.addTypeFormat}>
                                                <div>
                                                    <label>
                                                        <Radio value={1} />Indviduelle
                                                </label>
                                                    <label>
                                                        <Radio value={2} />Cycle
                                                </label>
                                                </div>
                                            </RadioGroup>
                                        </div>
                                        {this.state.selectedTypeFormat == 1 ?
                                            (<div className="col-md-6">
                                                <div className="form-group">
                                                    <label>Nombre d'heures</label>
                                                    <input type="number" className="form-control" onChange={this.addNbrHeure} style={{ borderRadius: '5px', border: 'solid 1px', borderColor: '#B3B3B3' }} />
                                                </div>
                                            </div>) : (null)}
                                    </div>
                                    )}
                                <div className="row">
                                    <div className="col-md-6">
                                        <label>Comment avez-vous connu notre centre de formation ?</label>
                                        <span style={{ display: 'flex', flexWrap: 'wrap', padding: '10px', borderRadius: '5px', border: 'solid 1px #B3B3B3' }}>
                                            {this.state.listConnaissance.map(el =>
                                                <label>
                                                    <Checkbox
                                                        onChange={(e) => this.addConnaiss(e, el)}
                                                        disabled={this.state.disabled}
                                                    />
                                                    {el.label}
                                                </label>
                                            )}
                                        </span>
                                    </div>
                                </div>
                                <div style={{ paddingTop: '5px' }} className="row">
                                    <div className="col-md-6">
                                        <div className="form-group">
                                            <label>Cycles</label>
                                            <Select
                                                isMulti={true}
                                                isClearable={true}
                                                isSearchable={true}
                                                onChange={this.addCycle}
                                                options={this.state.listCycle}
                                                // formatGroupLabel={formatGroupLabel}
                                            />
                                        </div>
                                    </div>
                                    {this.state.selectedTypeFormat == 1 ?
                                        (<div className="col-md-6">
                                            <div className="form-group">
                                                <label>Proposition de cycle</label>
                                                <input type="text" className="form-control" onChange={this.addPropCycle} style={{ borderRadius: '5px', border: 'solid 1px', borderColor: '#B3B3B3' }} />
                                            </div>
                                        </div>) : (null)}
                                </div>
                                {this.state.ajoutInscripCycle == true ?
                                    (<p>reglement</p>
                                    ) : (null)}
                                {this.state.ajoutInscripCycle == false ?
                                    (<div style={{ display: 'flex', justifyContent: 'center', marginBottom: '10px' }}>
                                        <button style={{ width: '15%', borderRadius: '5px' }} type="button" className="btn btn-success" onClick={this.ajoutInscripCycle}>Confirmer</button>
                                    </div>) : (<div style={{ display: 'flex', justifyContent: 'center', marginBottom: '10px' }}>
                                        <button style={{ width: '15%', borderRadius: '5px' }} type="button" className="btn btn-success" onClick={this.ajoutRegInscripCycle}>Confirmer</button>
                                    </div>)}
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        );
    }
}

export default withRouter(InscripCycle);
