import React, { Component } from 'react'
import axios from 'axios'
import jQuery from 'jquery'
import { withRouter } from 'react-router-dom'
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";
import Select from 'react-select';
import Checkbox from 'rc-checkbox';
import 'rc-checkbox/assets/index.css';
import { Fragment } from 'react';
import { Button, Modal, ModalHeader, ModalBody, ModalFooter } from 'reactstrap';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import { Slide, Zoom, Flip, Bounce } from 'react-toastify';
import { RadioGroup, Radio } from 'react-radio-group'

class AjoutCycle extends Component {
    constructor(props) {
        super(props);
        this.state = {
            nomCycle: '',
            nbrHeure: '',
            tarifCycle: '',
            tarifAvecCarte: '',
            anneScol: '',
            mois: [{ label: 'Janvier', value: '01' }, { label: 'Février', value: '02' }, { label: 'Mars', value: '03' }, { label: 'Avril', value: '04' }, { label: 'Mai', value: '05' }, { label: 'Juin', value: '06' }, { label: 'Juillet', value: '07' }, { label: 'Aout', value: '08' }, { label: 'Septembre', value: '09' }, { label: 'Octobre', value: '10' }, { label: 'November', value: '11' }, { label: 'Decembre', value: '12' }],
            enseignants: [],
            selectedEnseignent: '',
            arrayCycle: [{ selectedMois: '', dateD: '', dateF: '', selectedCour: '', enseignant: [{ selectedEnseignent: '' }] }],
            indexLignCycleASupprimer: '',
            modal: false
        }
    }

    componentDidMount() {
        var userPermissions = JSON.parse((localStorage.getItem('userPermissions')));
        var anneeScolaire = userPermissions[0].anneeScolaire
        jQuery.ajax({
            url: process.env.REACT_APP_API_URL + 'Back_mediatraining/GetAnneeScolaire.php',
            type: 'POST',
            data: {
                anneeScolaire: anneeScolaire
            },
            dataType: 'json',
            success: (code_html, statut) => {
                console.log(code_html)
                this.setState({
                    anneScol: code_html.scolaire
                });
            }
        });
        axios.get(process.env.REACT_APP_API_URL + 'Back_mediatraining/SelectCours.php').then(res => {
            console.log(res)
            this.setState({
                cours: res.data
            })
        })
        axios.get(process.env.REACT_APP_API_URL + 'Back_mediatraining/SelectEnseignants.php').then(res => {
            console.log(res)
            this.setState({
                enseignants: res.data
            })
        })
    }

    addNomCycle = (event) => {
        this.setState({
            nomCycle: event.target.value
        });
    }

    addNbrHeure = (event) => {
        this.setState({
            nbrHeure: event.target.value
        });
    }

    addTarif = (event) => {
        this.setState({
            tarifCycle: event.target.value
        });
    }

    addTarifAvecCarte = (event) => {
        this.setState({
            tarifAvecCarte: event.target.value
        });
    }

    addType = (selectedType) => {
        this.setState({ selectedType: selectedType });
    }

    addMois = (selectedMois, index) => {
        let selectedMonth = selectedMois;
        this.setState({
            arrayCycle: this.state.arrayCycle.map((el, id) => (id === index ? Object.assign(el, { selectedMois: selectedMonth }) : el))
        });
    }

    addDateD = (d, index) => {
        let dateD = d;
        this.setState({
            arrayCycle: this.state.arrayCycle.map((el, id) => (id === index ? Object.assign(el, { dateD: dateD }) : el))
        });
    }

    addDateF = (d, index) => {
        let dateF = d;
        this.setState({
            arrayCycle: this.state.arrayCycle.map((el, id) => (id === index ? Object.assign(el, { dateF: dateF }) : el))
        });
    }

    addHorraireCours = (selectedCour, index) => {
        let selectedHorraireCour = selectedCour;
        this.setState({
            arrayCycle: this.state.arrayCycle.map((el, id) => (id === index ? Object.assign(el, { selectedCour: selectedHorraireCour }) : el))
        });
    }

    addEnseignent = (selectedEnseignent, index) => {
        let selectedEnseign = selectedEnseignent;
        var arrayEnseignent = []
        arrayEnseignent.push(selectedEnseign.value)
        this.setState({
            arrayCycle: this.state.arrayCycle.map((el, id) => (id === index ? Object.assign(el, { selectedEnseignent: arrayEnseignent }) : el))
        });
    }

    additionLignCycle = () => {
        let element = { selectedMois: '', dateD: '', dateF: '', selectedCour: '', enseignant: [{ selectedEnseignent: '' }] }
        this.setState({
            arrayCycle: [...this.state.arrayCycle, element]
        })
    }

    toggleMatFor = (index) => {
        this.setState(prevState => ({
            modal: !prevState.modal,
            indexLignCycleASupprimer: index
        }));
    }

    deleteMatFor = () => {
        this.state.arrayCycle.splice(this.state.indexLignCycleASupprimer, 1)
        this.setState(prevState => ({
            modal: !prevState.modal
        }));
    }

    nonDeleteMatFor = () => {
        this.setState(prevState => ({
            modal: !prevState.modal
        }));
    }

    ajout = () => {
    }

    render() {
        return (
            <div id="page-wrapper" style={{ minHeight: '263px', marginLeft: '0px' }}>
                <div className="container-fluid">
                    <div className="row bg-title">
                        <div className="col-lg-12">
                            <h4 className="page-title">Ajout cycle</h4>
                        </div>
                        {/* /.col-lg-12 */}
                    </div>
                    {/* /.row */}
                    <div class="row">
                        <div class="col-sm-12">
                            <div class="white-box">
                                <div class="row">
                                    <div class="col-md-6">
                                        <div class="form-group">
                                            <label>Cycle</label>
                                            <input style={{ backgroundColor: 'white', borderRadius: '5px', border: 'solid 1px #B3B3B3' }} type="text" class="form-control" placeholder='Nom cycle' onChange={(e) => this.addNomCycle(e)} />
                                        </div>
                                    </div>
                                    <div class="col-md-6">
                                        <div class="form-group">
                                            <label>Heure</label>
                                            <input style={{ backgroundColor: 'white', borderRadius: '5px', border: 'solid 1px #B3B3B3' }} type="text" class="form-control" onChange={(e) => this.addNbrHeure(e)} />
                                        </div>
                                    </div>
                                    <div class="col-md-6">
                                        <div class="form-group">
                                            <label>Tarif cycle</label>
                                            <input style={{ backgroundColor: 'white', borderRadius: '5px', border: 'solid 1px #B3B3B3' }} type="text" class="form-control" onChange={(e) => this.addTarif(e)} />
                                        </div>
                                    </div>
                                    <div class="col-md-6">
                                        <div class="form-group">
                                            <label>Tarif avec carte</label>
                                            <input style={{ backgroundColor: 'white', borderRadius: '5px', border: 'solid 1px #B3B3B3' }} type="text" class="form-control" onChange={(e) => this.addTarifAvecCarte(e)} />
                                        </div>
                                    </div>
                                    <div class="col-md-6">
                                        <RadioGroup
                                            name="type"
                                            selectedValue={this.state.selectedType}
                                            onChange={this.addType}>
                                            <label>
                                                <Radio value="Indviduelle " />Indviduelle
                                                </label>
                                        </RadioGroup>
                                    </div>
                                </div>
                                <div class="row">
                                    <h3 style={{ paddingLeft: '15px' }}>{'Planning Cycle Année Scolaire ' + this.state.anneScol}</h3>
                                    <table style={{ marginTop: '10px' }} class="table table-bordered">
                                        <thead>
                                            <tr>
                                                <th style={{ textAlign: 'center' }} scope="col">Mois</th>
                                                <th style={{ textAlign: 'center' }} scope="col">Date Debut</th>
                                                <th style={{ textAlign: 'center' }} scope="col">Date Fin</th>
                                                <th style={{ textAlign: 'center' }} scope="col">Horraires</th>
                                                <th style={{ textAlign: 'center' }} scope="col">Enseignent</th>
                                                <th style={{ textAlign: 'center' }} scope="col">Supprimer</th>
                                            </tr>
                                        </thead>
                                        {this.state.arrayCycle.map((el, index) =>
                                            <tbody>
                                                <tr>
                                                    <td style={{ textAlign: 'center' }}><Select
                                                        value={el.selectedMois}
                                                        isClearable={true}
                                                        isSearchable={true}
                                                        onChange={(e) => this.addMois(e, index)}
                                                        options={this.state.mois}
                                                    /></td>
                                                    <td style={{ textAlign: 'center' }}><DatePicker class="form-control ddate" style={{ border: 'none' }} selected={el.dateD} onChange={(e) => this.addDateD(e, index)} /></td>
                                                    <td style={{ textAlign: 'center' }}><DatePicker class="form-control ddate" style={{ border: 'none' }} selected={el.dateF} onChange={(e) => this.addDateF(e, index)} /></td>
                                                    <td style={{ textAlign: 'center' }}><Select
                                                        value={el.selectedCour}
                                                        isClearable={true}
                                                        isSearchable={true}
                                                        onChange={(e) => this.addHorraireCours(e, index)}
                                                        options={this.state.cours}
                                                    /></td>
                                                    <td style={{ textAlign: 'center' }}><Select
                                                        value={el.selectedEnseignent}
                                                        isClearable={true}
                                                        isSearchable={true}
                                                        onChange={(e) => this.addEnseignent(e, index)}
                                                        options={this.state.enseignants}
                                                    /></td>
                                                    <td style={{ textAlign: 'center' }}><button className='buttonSupprimer' onClick={() => this.toggleLign(index)}><i style={{ color: 'white' }} class="fas fa-trash-alt"></i></button></td>
                                                </tr>
                                            </tbody>
                                        )}
                                    </table>
                                </div>
                                <div style={{ display: 'flex', justifyContent: 'center', marginBottom: '10px' }}>
                                    <button style={{ width: '15%', borderRadius: '5px' }} type="button" className="btn btn-success" onClick={this.ajout}>Confirmer</button>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        );
    }
}

export default withRouter(AjoutCycle);
