import React, { Component, Fragment } from 'react'
import { Link, BrowserRouter as Router, Route, Switch, withRouter, HashRouter, Redirect } from 'react-router-dom'
import DataTable from 'react-data-table-component';
import axios from 'axios'
import jQuery from 'jquery'
import Select from 'react-select';

class EtudiantsDiplome extends Component {
    constructor(props) {
        super(props);
        this.state = {
            data: [],
            columns: [
                {
                    name: 'Etudiant',
                    selector: 'nomComplet',
                    sortable: false,
                    center: true,
                    style: { width: '40%' }
                },
                {
                    name: 'PFF',
                    cell: row => row.etatPff == "Validé" ? (<p><i style={{ color: 'green' }} className="fas fa-check-circle"></i>{row.etatPff}</p>) : (<p><i style={{ color: 'red' }} className="far fa-times-circle"></i>{row.etatPff}</p>),
                    ignoreRowClick: true,
                    allowOverflow: true,
                    button: true,
                    width: '20%'
                },
                {
                    name: 'Etat',
                    cell: row => row.etatDipl == "Disponible" ? (<p><i style={{ color: 'green' }} className="fas fa-check-circle"></i>{row.etatDipl}</p>) : (<p><i style={{ color: 'red' }} className="far fa-times-circle"></i>{row.etatDipl}</p>),
                    ignoreRowClick: true,
                    allowOverflow: true,
                    button: true,
                    width: '20%'
                },
                {
                    name: 'Delivre',
                    cell: row => row.delivDipl == "Délivré" ? (<p><i style={{ color: 'green' }} className="fas fa-check-circle"></i>{row.delivDipl}</p>) : (<p><i style={{ color: 'red' }} className="far fa-times-circle"></i>{row.delivDipl}</p>),
                    ignoreRowClick: true,
                    allowOverflow: true,
                    button: true,
                    width: '20%'
                }
            ],
            nombreTotalElement: 0,
            filterElement: '',
            mycurrentPage: 1,
            resetFirstPage: false,
            nbrEltperPage: 5,
            modal: false,
            pending: true,
            listEleve: [],
            selectedIdEleve: '',
            listGroup: [],
            selectedIdGroup: ''
        }
