import React, { Component, Fragment } from 'react'
import axios from 'axios'
import jQuery from 'jquery'
import { withRouter } from 'react-router-dom'
import Select from 'react-select';
import { RadioGroup, Radio } from 'react-radio-group'
import Checkbox from 'rc-checkbox';
import 'rc-checkbox/assets/index.css';
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";
import { Button, Modal, ModalHeader, ModalBody, ModalFooter } from 'reactstrap';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import { Slide, Zoom, Flip, Bounce } from 'react-toastify';

const groupStyles = {
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'space-between',
};
const groupBadgeStyles = {
    backgroundColor: '#EBECF0',
    borderRadius: '2em',
    color: '#172B4D',
    display: 'inline-block',
    fontSize: 12,
    fontWeight: 'normal',
    lineHeight: '1',
    minWidth: 1,
    padding: '0.16666666666667em 0.5em',
    textAlign: 'center',
};

const formatGroupLabel = data => (
    <div style={groupStyles}>
        <span>{data.label}</span>
        <span style={groupBadgeStyles}>{data.options.length}</span>
    </div>
);

class InscripCycle extends Component {
    constructor(props) {
        super(props);
        this.state = {
            listTypeApp: [{ value: 1, label: 'Eléve deja inscrit a une formation' }, { value: 2, label: 'Nouveau éleve' }],
            selectedTypeApp: 2,
            listApprenant: [],
            selectedApprenant: '',
            listConnaissance: [],
            numInscripCycle: '',
            numCarteAdh: '',
            listTypeFormat: [{ value: 1, label: 'Societé' }, { value: 2, label: 'Particulier' }],
            selectedTypeFiche: 2,
            nbrPersonne: 0,
            matFisc: '',
            nbrPersTenu: 0,
            societe: '',
            responsable: '',
            mobileResp: '',
            age: '',
            lieuResid: [],
            seletedLieuResid: '',
            listNivBase: [],
            selectedNivBase: '',
            facebook: '',
            fonction: '',
            selectedTypeFormat: 2,
            motivation: '',
            numIdentite: '',
            prenom: '',
            nom: '',
            disabled: false,
            connaissChoisi: [],
            listCycle: [],
            selectedCycle: [],
            email: '',
            tel: '',
            fax: '',
            photoVerso: null,
            nbrHeure: 0,
            blockReglement: false,
            carteFid: '',
            tenu: '',
            propCycle: '',
            reglInsCycle: [],
            societeCoch: false,
            tva: '',
            numCarteFid: '',
            mntCarte: '',
            mntTenu: '',
            nomCycle: '',
            dateDCycle: '',
            dateFCycle: '',
            horaire: '',
            mntTotalPayer: 0,
            listModPay: [],
            tabModPayFormat: [{ modPay: '', mntModPay: '', numCheque: '', echeanceChec: new Date(), dateEncaiss: new Date(), titulaireChec: '', banque: '' }],
            tabModPayCycle: [{ modPay: '', mntModPay: '', numCheque: '', echeanceChec: new Date(), dateEncaiss: new Date(), titulaireChec: '', banque: '' }],
            indexLignASupprimer: '',
            modalFormat: false,
            modalCycle: false,
            datePaiement: new Date(),
            mntCycleSansTva: '',
            selectedPayCycle: 2,
            arrayRegl: [],
            reglEleve: [],
            mntFraisTotalPayer: 0,
            mntIncripTotalPayer: 0,
            selectedBolPayFormat: 2,
            inforEleve: '',
            fraisReglEleve: []
        }
    }

    componentDidMount() {
        var userPermissions = JSON.parse((localStorage.getItem('userPermissions')));
        var anneeScolaire = userPermissions[0].anneeScolaire
        jQuery.ajax({
            url: process.env.REACT_APP_API_URL + 'Back_mediatraining/NumInscripCycle.php',
            type: 'POST',
            data: {
                anneeScolaire: anneeScolaire
            },
            dataType: 'json',
            success: (code_html, statut) => {
                this.setState({
                    numInscripCycle: "00000" + (parseInt(code_html) + 1).toString()
                })
            }
        });
        jQuery.ajax({
            url: process.env.REACT_APP_API_URL + 'Back_mediatraining/NumAdheCycle.php',
            type: 'POST',
            data: {
                anneeScolaire: anneeScolaire
            },
            dataType: 'json',
            success: (code_html, statut) => {
                this.setState({
                    numCarteAdh: "00" + (parseInt(code_html) + 1).toString(),
                    carteFid: "00" + (parseInt(code_html) + 1).toString()
                })
            }
        });
        axios.get(process.env.REACT_APP_API_URL + 'Back_mediatraining/SelectResid.php').then(res => {
            this.setState({
                lieuResid: res.data
            })
        })
        axios.get(process.env.REACT_APP_API_URL + 'Back_mediatraining/SelectNivBase.php').then(res => {
            console.log(res)
            this.setState({
                listNivBase: res.data
            })
        })
        axios.get(process.env.REACT_APP_API_URL + 'Back_mediatraining/SelectConnaissance.php').then(res => {
            console.log(res)
            this.setState({
                listConnaissance: res.data
            })
        })
        jQuery.ajax({
            url: process.env.REACT_APP_API_URL + 'Back_mediatraining/SelectDetailCycle.php',
            type: 'POST',
            data: {
                anneeScolaire: anneeScolaire
            },
            dataType: 'json',
            success: (code_html, statut) => {
                this.setState({
                    listCycle: code_html
                })
            }
        });
    }

    addTypeApp = (selectedTypeApp) => {
        var userPermissions = JSON.parse((localStorage.getItem('userPermissions')));
        var anneeScolaire = userPermissions[0].anneeScolaire
        if (selectedTypeApp) {
            this.setState({ selectedTypeApp: selectedTypeApp }, () => {
                jQuery.ajax({
                    url: process.env.REACT_APP_API_URL + 'Back_mediatraining/SelectEleves.php',
                    type: 'POST',
                    data: {
                        anneeScolaire: anneeScolaire
                    },
                    dataType: 'json',
                    success: (code_html, statut) => {
                        this.setState({
                            listApprenant: code_html
                        })
                    }
                });
            });
        }
    }

    addApprenant = (selectedApprenant) => {
        var userPermissions = JSON.parse((localStorage.getItem('userPermissions')));
        var anneeScolaire = userPermissions[0].anneeScolaire
        this.setState({ selectedApprenant: selectedApprenant }, () => {
            if (selectedApprenant != null) {
                jQuery.ajax({
                    url: process.env.REACT_APP_API_URL + 'Back_mediatraining/GetInforApprenant.php?id=' + selectedApprenant.value,
                    type: 'POST',
                    data: {
                        anneeScolaire: anneeScolaire
                    },
                    dataType: 'json',
                    success: (code_html, statut) => {
                        this.setState({
                            numIdentite: code_html.numIdentite,
                            nom: code_html.nom,
                            prenom: code_html.prenom,
                            age: code_html.age,
                            selectedNivBase: code_html.niveau,
                            email: code_html.email,
                            tel: code_html.tel
                        })
                    }
                })
            };
        })
    }

    addType = (selectedTypeFiche) => {
        this.setState({ selectedTypeFiche: selectedTypeFiche });
    }

    addCarteFid = (e) => {
        console.log('Checkbox checked:', (e.target.checked));
        if (e.target.checked === true) {
            this.setState({
                carteFid: ''
            });
        }
        else {
            var numCarteFid = "00" + (parseInt(this.state.numCarteAdh) + 1).toString()
            this.setState({
                carteFid: numCarteFid
            });
        }
    }

    addTenu = (e) => {
        console.log('Checkbox checked:', (e.target.checked));
        if (e.target.checked === true) {
            this.setState({
                tenu: true
            });
        }
        else {
            this.setState({
                tenu: false
            });
        }
    }

    addNbrPersonne = (event) => {
        this.setState({
