        });
    }

    addBanqueFormat = (event, indexModPay) => {
        let banque = event.target.value;
        this.setState({
            tabModPayFormat: this.state.tabModPayFormat.map((el, id) => (id === indexModPay ? Object.assign(el, { banque: banque }) : el))
        });
    }

    toggleLignFormat = (index) => {
        this.setState(prevState => ({
            modalFormat: !prevState.modalFormat,
            indexLignASupprimer: index
        }));
    }

    deleteLignFormat = () => {
        this.state.tabModPayFormat.splice(this.state.indexLignASupprimer, 1)
        this.setState(prevState => ({
            modalFormat: !prevState.modalFormat
        }));
    }

    nonDeleteLignFormat = () => {
        this.setState(prevState => ({
            modalFormat: !prevState.modalFormat
        }));
    }

    // mode paiement cycle

    additionLignCycle = () => {
        let element = { modPay: '', mntModPay: '', numCheque: '', echeanceChec: new Date(), dateEncaiss: new Date(), titulaireChec: '', banque: '' }
        this.setState({
            tabModPayCycle: [...this.state.tabModPayCycle, element]
        })
    }

    addModPayCycle = (selectedModPay, indexModPay) => {
        if (selectedModPay != null) {
            let modPay = selectedModPay;
            this.setState({
                tabModPayCycle: this.state.tabModPayCycle.map((el, id) => (id === indexModPay ? Object.assign(el, { modPay: modPay }) : el))
