import React, { Component } from 'react'
import { Link, BrowserRouter as Router, Route, Switch, withRouter } from 'react-router-dom'
import axios from 'axios'
import jQuery from 'jquery'

class DiplomeEtat extends Component {
    constructor(props) {
        super(props);
        this.state = {
            eleveDipl: []
        }
    }

    componentDidMount() {
        if (JSON.parse((localStorage.getItem('userPermissions'))) != null) {
            var userPermissions = JSON.parse((localStorage.getItem('userPermissions')));
            var anneeScolaire = userPermissions[0].anneeScolaire
            jQuery.ajax({
                url: process.env.REACT_APP_API_URL + 'Back_mediatraining/EtudiantsDipl.php',
                type: 'POST',
                data: {
                    anneeScolaire: anneeScolaire
                },
                dataType: 'json',
                success: (code_html, statut) => {
                    this.setState({
                        eleveDipl: code_html
                    }, () => {
                        var long = this.state.eleveDipl.length
                        var etatDipl = document.getElementsByClassName('etatDipl')
                        var arrayEtatDipl = []
                        var delivDipl = document.getElementsByClassName('delivDipl')
                        var arrayDelivDipl = []
                        //
                        for (var i = 0; i < long; i++) {
                            arrayEtatDipl.push(this.state.eleveDipl[i].etatDipl)
                            arrayDelivDipl.push(this.state.eleveDipl[i].delivDipl)
                        }
                        //
                        for (var j = 0; j < long; j++) {
                            if (arrayEtatDipl[j] == 1) {
                                etatDipl[j].checked = true
                            }
                            else if (arrayEtatDipl[j] == 0) {
                                etatDipl[j].checked = false
                            }
                            if (arrayDelivDipl[j] == 1) {
                                delivDipl[j].checked = true
                            }
                            else if (arrayDelivDipl[j] == 0) {
                                delivDipl[j].checked = false
                            }
                        }
                    });
                }
            });
        }
        else {
            document.location = '/'
        }
    }

    modif = () => {
        var long = this.state.eleveDipl.length
        //
        var etatDipl = new Array(long).fill(0);
        var delivDipl = new Array(long).fill(0);
        ///
        var diplEtat = document.getElementsByClassName('etatDipl')
        var diplDeliv = document.getElementsByClassName('delivDipl')
        var longeur = this.state.eleveDipl.length
        var array = []
        //
        for (var i = 0; i < longeur; i++) {
            if (diplEtat[i].checked) {
                etatDipl[i] = 1
            }
            else if (diplEtat[i].checked === false) {
                etatDipl[i] = 0
            }
            if (diplDeliv[i].checked) {
                delivDipl[i] = 1
            }
            else if (diplDeliv[i].checked === false) {
                delivDipl[i] = 0
            }
            var objet = { idDe: this.state.eleveDipl[i].idDe, etatDipl: etatDipl[i], delivDipl: delivDipl[i] }
            array.push(objet)
        }
        jQuery.ajax({
            url: process.env.REACT_APP_API_URL + 'Back_mediatraining/EtatDiplomeEtudiant.php',
            type: 'POST',
            data: {
                array: array
            },
            dataType: 'json',
            success: (code_html, statut) => {
                if (code_html.Result === "OK") {
                    this.props.history.push('/Menu');
                }
                else if (code_html.Result === "KO") {
                    alert("erreur,l'un de vos champs est vide!!");
                }
            }
        });
    }

    render() {
        return (
            <div id="page-wrapper" style={{ minHeight: '263px', marginLeft: '0px' }}>
                <div className="container-fluid">
                    <div className="row bg-title">
                        <div className="col-lg-12">
                            <h4 className="page-title">Etat diplome</h4>
                        </div>
                        {/* /.col-lg-12 */}
                    </div>
                    {/* /.row */}
                    <div className="row">
                        <div className="col-sm-12">
                            <div className="white-box">
                                <div style={{ display: 'flex', justifyContent: 'flex-start', alignItems: 'center', marginBottom: '10px', marginRight: '20px' }}>
                                    <Link to="/EtudiantsDiplome"> <button style={{ background: '#DC143C', border: '1px solid #DC143C', borderRadius: '5px' }} type="button" className="btn btn-info"> <i style={{ paddingRight: '5px' }} className="fas fa-arrow-circle-left"></i>Retour</button></Link>
                                </div>
                                <div className="row">
                                    <div className="table-responsive" style={{ textAlign: 'center' }}>
                                        <table className="table align-items-center table-flush">
                                            <thead className="thead-light">
                                                <tr>
                                                    <th style={{ textAlign: 'center' }} scope="col">Etudiant</th>
                                                    <th style={{ textAlign: 'center' }} scope="col">Etat</th>
                                                    <th style={{ textAlign: 'center' }} scope="col">Delivr√©</th>
                                                </tr>
                                            </thead>
                                            {this.state.eleveDipl.map(el =>
                                                <tbody>
                                                    <tr>
                                                        <td>
                                                            {el.nomComplet}
                                                        </td>
                                                        <td>
                                                            <input className='etatDipl' type="checkbox" />
                                                        </td>
                                                        <td>
                                                            <input className='delivDipl' type="checkbox" />
                                                        </td>
                                                    </tr>
                                                </tbody>
                                            )}
                                        </table>
                                    </div>
                                    <div style={{ display: 'flex', justifyContent: 'center', marginBottom: '10px' }}>
                                        <button style={{ borderRadius: '5px' }} type="button" className="btn btn-success" onClick={this.modif}>Confirmer</button>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        );
    }
}

export default withRouter(DiplomeEtat);
