    ajoutRegInscripCycle = () => {
        if (this.state.selectedBolPayFormat == 1) {
            var sommePaiement = 0
            sommePaiement = parseInt(this.state.mntFraisTotalPayer) + parseInt(this.state.mntIncripTotalPayer) + parseInt(this.state.mntFraisCycleTotalPayer) + parseInt(this.state.reglInsCycle.mntCyclePay)
            var sommeRegFormat = 0
            for (var i = 0; i < this.state.tabModPayFormat.length; i++) {
                sommeRegFormat += parseInt(this.state.tabModPayFormat[i].mntModPay)
            }
            var sommeRegCycle = 0
            for (var i = 0; i < this.state.tabModPayCycle.length; i++) {
                sommeRegCycle += parseInt(this.state.tabModPayCycle[i].mntModPay)
            }
            var sommeTot = sommeRegFormat + sommeRegCycle
            if (sommePaiement !== sommeTot) {
                toast.error('⛔ Veuillez vérifier les sommes saisie !', { containerId: 'A' });
            }
            else {
                var userPermissions = JSON.parse((localStorage.getItem('userPermissions')));
                var anneeScolaire = userPermissions[0].anneeScolaire
                var userId = userPermissions[0].idUser
                // convert date paiement
                let datePaiement = this.state.datePaiement
                let month = '' + (datePaiement.getMonth() + 1);
                let day = '' + datePaiement.getDate();
                let year = datePaiement.getFullYear();
                if (month.length < 2)
                    month = '0' + month;
                if (day.length < 2)
                    day = '0' + day;
                var convertDatePaiement = [year, month, day].join('-');
                // I) Cycle convertir date echeance et encaissement cheque si le mode de paiement => cheque
                var convertDateEcheChec = ''
                var convertDateEncaissChec = ''
                for (var i = 0; i < this.state.tabModPayCycle.length; i++) {
                    if (this.state.tabModPayCycle[i].modPay.value == 2) {
                        // convertir date echeance cheque
                        var dateEcheChec = this.state.tabModPayCycle[i].echeanceChec
                        let monthOne = '' + (dateEcheChec.getMonth() + 1);
                        let dayOne = '' + dateEcheChec.getDate();
                        let yearOne = dateEcheChec.getFullYear();
                        if (monthOne.length < 2)
                            monthOne = '0' + monthOne;
                        if (dayOne.length < 2)
                            dayOne = '0' + dayOne;
                        convertDateEcheChec = [yearOne, monthOne, dayOne].join('-');
                        // convertir date encaissement cheque
                        var dateEncaissChec = this.state.tabModPayCycle[i].dateEncaiss
                        let monthTwo = '' + (dateEncaissChec.getMonth() + 1);
                        let dayTwo = '' + dateEncaissChec.getDate();
                        let yearTwo = dateEncaissChec.getFullYear();
                        if (monthTwo.length < 2)
                            monthTwo = '0' + monthTwo;
                        if (dayTwo.length < 2)
                            dayTwo = '0' + dayTwo;
                        convertDateEncaissChec = [yearTwo, monthTwo, dayTwo].join('-');
                    }
                }
                // II) Formation convertir date echeance et encaissement cheque si le mode de paiement => cheque
                var formatDateEcheChec = ''
                var formatDateEncaissChec = ''
                for (var i = 0; i < this.state.tabModPayFormat.length; i++) {
                    if (this.state.tabModPayFormat[i].modPay.value == 2) {
                        // convertir date echeance cheque
                        var echeChec = this.state.tabModPayFormat[i].echeanceChec
                        let monthThree = '' + (echeChec.getMonth() + 1);
                        let dayThree = '' + echeChec.getDate();
                        let yearThree = echeChec.getFullYear();
                        if (monthThree.length < 2)
                            monthThree = '0' + monthThree;
                        if (dayThree.length < 2)
                            dayThree = '0' + dayThree;
                        formatDateEcheChec = [yearThree, monthThree, dayThree].join('-');
                        // convertir date encaissement cheque
                        var encaissChec = this.state.tabModPayFormat[i].dateEncaiss
                        let monthFour = '' + (encaissChec.getMonth() + 1);
                        let dayFour = '' + encaissChec.getDate();
                        let yearFour = encaissChec.getFullYear();
                        if (monthFour.length < 2)
                            monthFour = '0' + monthFour;
                        if (dayFour.length < 2)
                            dayFour = '0' + dayFour;
                        formatDateEncaissChec = [yearFour, monthFour, dayFour].join('-');
                    }
                }
                //
                jQuery.ajax({
                    url: process.env.REACT_APP_API_URL + 'Back_mediatraining/AddRegInscripCycl.php',
                    type: 'POST',
                    data: {
                        bolPayFormat: this.state.selectedBolPayFormat,
                        inforEleve: this.state.inforEleve,
                        eleveId: this.state.elvId,
                        idInsFormation: this.state.idInsFormation,
                        fraisFormation: this.state.fraisReglEleve,
                        reglFormation: this.state.reglEleve,
                        modPayFormat: this.state.tabModPayFormat,
                        modePaiementFormation: this.state.modePaiementFormation,
                        echeChec: formatDateEcheChec,
                        encaissChec: formatDateEncaissChec,
                        idInscriptionCycle: this.state.idInscriptionCycle,
                        fraisCycle: this.state.fraisCycle,
                        reglInsCycle: this.state.reglInsCycle,
                        modPayCycle: this.state.tabModPayCycle,
                        dateEcheChec: convertDateEcheChec,
                        dateEncaissChec: convertDateEncaissChec,
                        anneeScolaire: anneeScolaire,
                        userId: userId,
                        datePaiement: convertDatePaiement,
                        montantTotalFormat: sommeRegFormat,
                        montantTotalCycle: sommeRegCycle
                    },
