                        }
                    }
                });
            }
        }
        else {
            var sommeFraisCycle = 0
            for (var j = 0; j < this.state.fraisCycle.length; j++) {
                sommeFraisCycle += parseInt(this.state.fraisCycle[j].mntFraisCyclePayer)
            }
            var sommePaiement = 0
            sommePaiement = parseInt(this.state.reglInsCycle.mntCyclePay) + sommeFraisCycle
            var sommeReg = 0
            for (var i = 0; i < this.state.tabModPayCycle.length; i++) {
                sommeReg += parseInt(this.state.tabModPayCycle[i].mntModPay)
            }
            if (sommePaiement !== sommeReg) {
                toast.error('⛔ Veuillez vérifier les sommes saisie !', { containerId: 'A' });
            }
            else {
                var userPermissions = JSON.parse((localStorage.getItem('userPermissions')));
                var anneeScolaire = userPermissions[0].anneeScolaire
                var userId = userPermissions[0].idUser
