import React, { Component } from 'react'
import axios from 'axios'
import jQuery from 'jquery'
import { withRouter } from 'react-router-dom'
import Select from 'react-select';
import { Button, Modal, ModalHeader, ModalBody, ModalFooter } from 'reactstrap';

class EnseignantsGroupes extends Component {
    constructor(props) {
        super(props);
        this.state = {
            listGroupes: [],
            selectedGroup: '',
            listMatieres: [],
            selectedMatiere: '',
            enseignGroup: [{ idEnseignGroup: -1, group: '', matiere: '' }],
            indexEnseignGroupASupprimer: '',
            idEnseignGroupASupprimer: '',
            tabIdEnseignGroupASupprimer: [],
            modal: false
        }
    }

    componentDidMount() {
        if (JSON.parse((localStorage.getItem('userPermissions'))) != null) {
            let idEnseignant = this.props.location.state.idEnseignant;
            axios.get(process.env.REACT_APP_API_URL + 'Back_mediatraining/GetEnseignGroupByEnseignId.php?id=' + idEnseignant).then(res => {
                console.log(res)
                this.setState({
                    selectedGroup: res.data[0].group,
                    selectedMatiere: res.data[0].matiere,
                    enseignGroup: res.data[1]
                })
            })
            axios.get(process.env.REACT_APP_API_URL + 'Back_mediatraining/SelectGroupes.php').then(res => {
                console.log(res)
                this.setState({
                    listGroupes: res.data
                })
            })
            axios.get(process.env.REACT_APP_API_URL + 'Back_mediatraining/SelectMatieres.php').then(res => {
                console.log(res)
                this.setState({
                    listMatieres: res.data
                })
            })
        }
    }

    additionLign = () => {
        let element = { idEnseignGroup: -1, group: '', matiere: '' }
        this.setState({
            enseignGroup: [...this.state.enseignGroup, element]
        })
    }

    updGroup = selectedGroup => {
        this.setState({ selectedGroup })
    };

    updMatiere = selectedMatiere => {
        this.setState({ selectedMatiere })
    };

    toggleEnseignGroup = (idEnseignGroup, indexEnseignGroup) => {
        this.setState(prevState => ({
            modal: !prevState.modal,
            idEnseignGroupASupprimer: idEnseignGroup,
            indexEnseignGroupASupprimer: indexEnseignGroup
        }));
    }

    deleteEnseignGroup = () => {
        let idEnseignGroupASupprimer = this.state.idEnseignGroupASupprimer
        this.setState({
            tabIdEnseignGroupASupprimer: [...this.state.tabIdEnseignGroupASupprimer, idEnseignGroupASupprimer]
        });
        this.state.enseignGroup.splice(this.state.indexEnseignGroupASupprimer, 1)
        this.setState(prevState => ({
            modal: !prevState.modal
        }));
    }

    nonDeleteEnseignGroup = () => {
        this.setState(prevState => ({
            modal: !prevState.modal
        }));
    }

    modif = () => {
        // var userPermissions = JSON.parse((localStorage.getItem('userPermissions')));
        // var userId = userPermissions[0].idUser
        let year = new Date().getFullYear();
        console.log(this.state.enseignGroup)
        console.log(this.state.tabIdEnseignGroupASupprimer)
        jQuery.ajax({
            url: process.env.REACT_APP_API_URL + 'Back_mediatraining/UpdEnseignGroup.php',
            type: 'POST',
            data: {
                enseignGroup: this.state.enseignGroup,
                year: year,
                idEnseignGroupDelete: this.state.tabIdEnseignGroupASupprimer,
                // userId: userId
            },
            dataType: 'json',
            success: (code_html, statut) => {
                if (code_html.Result == "OK") {
                    this.props.history.push('/Enseignants');
                }
                else if (code_html.Result == "KO") {
                    alert("erreur,l'un de vos champs est vide!!");
                }
            }
        });
    }

    render() {
        return (
            <div id="page-wrapper" style={{ minHeight: '263px' }}>
                <div className="container-fluid">
                    <div className="row bg-title">
                        <div className="col-lg-12">
                            <h4 className="page-title"> Groupes par enseignant</h4>
                        </div>
                        {/* /.col-lg-12 */}
                    </div>
                    {/* /.row */}
                    <div class="row">
                        <div class="col-sm-12">
                            <div class="white-box">
                                <div class="row">
                                    <div class="col-md-6">
                                        <div class="form-group">
                                            <label>Enseignant</label>
                                            <input type="text" disabled Value={this.state.nomEnseignant} class="form-control" style={{ backgroundColor: 'white', borderRadius: '5px' }} />
                                        </div>
                                    </div>
                                </div>
                                <div class="row">
                                    <table style={{ width: '100%', marginTop: '10px' }} class="table table-bordered">
                                        <thead>
                                            <tr>
                                                <th style={{ textAlign: 'center' }} scope="col">Groupe</th>
                                                <th style={{ textAlign: 'center' }} scope="col">Matiére</th>
                                                <th style={{ textAlign: 'center', width: '15%' }} scope="col">Suppression</th>
                                            </tr>
                                        </thead>
                                        {this.state.enseignGroup.map((el, index) =>
                                            <tbody>
                                                <tr>
                                                    <td style={{ width: '42%' }}><Select
                                                        value={el.group}
                                                        isClearable={true}
                                                        isSearchable={true}
                                                        onChange={(e) => this.updGroup(e, index)}
                                                        options={this.state.listGroupes}
                                                    />  </td>
                                                    <td style={{ width: '42%' }}><Select
                                                        value={el.matiere}
                                                        isClearable={true}
                                                        isSearchable={true}
                                                        onChange={(e) => this.updMatiere(e, index)}
                                                        options={this.state.listMatieres}
                                                    />  </td>
                                                    <td style={{ textAlign: 'center' }}><button style={{ width: '20%' }} className='buttonSupprimer' onClick={() => this.toggleEnseignGroup(el.idEnseignGroup, index)}><i style={{ color: 'white' }} class="fas fa-trash-alt"></i></button></td>
                                                </tr>
                                            </tbody>
                                        )}
                                    </table>
                                    <div style={{ display: 'flex', justifyContent: 'flex-start', marginLeft: '15px' }}>
                                        <Button style={{ borderRadius: '5px' }} onClick={this.additionLign} color="secondary"><i class="fas fa-plus-circle"></i>Ajouter ligne</Button>
                                    </div>
                                </div>
                                <Modal isOpen={this.state.modal} toggle={this.nonDeleteEnseignGroup} className={this.props.className} fade={false}>
                                    <ModalHeader toggle={this.nonDeleteEnseignGroup}> <div style={{ display: 'flex', justifyContent: 'center', width: '465px' }}><img width='30%' src="./images/deleteModal.png" alt=".." /></div></ModalHeader>
                                    <ModalBody>
                                        <div style={{ fontFamily: 'Montserrat, sans-serif', FontSize: '14px', FontWeight: '700', LineHeight: '18.375px' }}>
                                            <div style={{ display: 'flex', justifyContent: 'center' }}>
                                                <p>Êtes-Vous sûr de vouloir supprimer cette ligne ?</p>
                                            </div>
                                            <div className='hvr-push' style={{ display: 'flex', justifyContent: 'space-around', marginBottom: '15px' }}>
                                                <div><button onClick={() => this.deleteEnseignGroup()} style={{ backgroundColor: '#00FF7F', borderRadius: '5px', color: 'white' }}><i style={{ color: 'white' }} class="far fa-thumbs-up"></i>Oui</button></div>
                                                <div><button onClick={() => this.nonDeleteEnseignGroup()} style={{ backgroundColor: '#D9534F', borderRadius: '5px', color: 'white' }}><i style={{ color: 'white' }} class="far fa-thumbs-down"></i>Non</button></div>
                                            </div>
                                        </div>
                                    </ModalBody>
                                </Modal>
                                <div style={{ display: 'flex', justifyContent: 'center', marginBottom: '10px' }}>
                                    <button style={{ width: '15%', borderRadius: '5px' }} type="button" className="btn btn-success" onClick={this.modif}>Confirmer</button>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        );
    }
}

export default withRouter(EnseignantsGroupes);
