import React, { Component } from 'react'
import axios from 'axios'
import jQuery from 'jquery'
import { withRouter } from 'react-router-dom'
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";
import Select from 'react-select';
import Checkbox from 'rc-checkbox';
import 'rc-checkbox/assets/index.css';
import { Fragment } from 'react';
import { Button, Modal, ModalHeader, ModalBody, ModalFooter } from 'reactstrap';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import { Slide, Zoom, Flip, Bounce } from 'react-toastify';
import { RadioGroup, Radio } from 'react-radio-group'

class ModifCycle extends Component {
    constructor(props) {
        super(props);
        this.state = {
            nomCycle: '',
            nbrHeure: '',
            tarifCycle: '',
            tarifAvecCarte: '',
            // type: ["Individuelle"],
            selectedType: '',
            anneScol: '',
            mois: [{ label: 'Janvier', value: '01' }, { label: 'FÃ©vrier', value: '02' }, { label: 'Mars', value: '03' }, { label: 'Avril', value: '04' }, { label: 'Mai', value: '05' }, { label: 'Juin', value: '06' }, { label: 'Juillet', value: '07' }, { label: 'Aout', value: '08' }, { label: 'Septembre', value: '09' }, { label: 'Octobre', value: '10' }, { label: 'November', value: '11' }, { label: 'Decembre', value: '12' }],
            enseignants: [],
            selectedEnseignent: '',
            arrayCycle: [{ selectedMois: '', dateD: '', dateF: '', selectedCour: '' }],
            indexLignCycleASupprimer: '',
            modal: false,
            indexLignEnseignASupprimer: '',
            indexSousLignEnseignASupprimer: '',
            modalEnseign: false,
        }
    }

    componentDidMount() {
        var userPermissions = JSON.parse((localStorage.getItem('userPermissions')));
        var anneeScolaire = userPermissions[0].anneeScolaire
        let idCycle = this.props.location.state.idCycle;
        axios.get(process.env.REACT_APP_API_URL + 'Back_mediatraining/GetCycleModifById.php?id=' + idCycle).then(res => {
            console.log(res)
            this.setState({
                nomCycle: res.data[0].cycle,
                nbrHeure: res.data[0].heure,
                tarifCycle: res.data[0].tarif,
                tarifAvecCarte: res.data[0].carte,
                arrayCycle: res.data[0].detailCycle
            })
        })
        jQuery.ajax({
            url: process.env.REACT_APP_API_URL + 'Back_mediatraining/GetAnneeScolaire.php',
            type: 'POST',
            data: {
                anneeScolaire: anneeScolaire
            },
            dataType: 'json',
            success: (code_html, statut) => {
                console.log(code_html)
                this.setState({
                    anneScol: code_html.scolaire
                });
            }
        });
        axios.get(process.env.REACT_APP_API_URL + 'Back_mediatraining/SelectCours.php').then(res => {
            console.log(res)
            this.setState({
                cours: res.data
            })
        })
        axios.get(process.env.REACT_APP_API_URL + 'Back_mediatraining/SelectEnseignants.php').then(res => {
            console.log(res)
            this.setState({
                enseignants: res.data
            })
        })
    }

    updNomCycle = (event) => {
        this.setState({
            nomCycle: event.target.value
        });
    }

    updNbrHeure = (event) => {
        this.setState({
            nbrHeure: event.target.value
        });
    }

    updTarif = (event) => {
        this.setState({
            tarifCycle: event.target.value
        });
    }

    updTarifAvecCarte = (event) => {
        this.setState({
            tarifAvecCarte: event.target.value
        });
    }

    updType = (e, el, index) => {
        console.log('Checkbox checked:', (e.target.checked));
        if (e.target.checked === true) {
            let element = el
            this.setState({
                selectedType: element
            });
        }
        else {
            this.setState({
                selectedType: ''
            });
        }
    }

    updMois = (selectedMois, index) => {
        let selectedMonth = selectedMois;
        this.setState({
            arrayCycle: this.state.arrayCycle.map((el, id) => (id === index ? Object.assign(el, { selectedMois: selectedMonth }) : el))
        });
    }

    updDateD = (d, index) => {
        let dateD = d;
        this.setState({
            arrayCycle: this.state.arrayCycle.map((el, id) => (id === index ? Object.assign(el, { dateD: dateD }) : el))
        });
    }

    updDateF = (d, index) => {
        let dateF = d;
        this.setState({
            arrayCycle: this.state.arrayCycle.map((el, id) => (id === index ? Object.assign(el, { dateF: dateF }) : el))
        });
    }

    updHorraireCours = (selectedCour, index) => {
        let selectedHorraireCour = selectedCour;
        this.setState({
            arrayCycle: this.state.arrayCycle.map((el, id) => (id === index ? Object.assign(el, { selectedCour: selectedHorraireCour }) : el))
        });
    }

    additionLign = () => {
        let element = { selectedMois: '', dateD: '', dateF: '', selectedCour: '' }
        this.setState({
            arrayCycle: [...this.state.arrayCycle, element]
        })
    }

    toggleLign = (index) => {
        this.setState(prevState => ({
            modal: !prevState.modal,
            indexLignCycleASupprimer: index
        }));
    }

    deleteLign = () => {
        this.state.arrayCycle.splice(this.state.indexLignCycleASupprimer, 1)
        this.setState(prevState => ({
            modal: !prevState.modal
        }));
    }

    nonDeleteLign = () => {
        this.setState(prevState => ({
            modal: !prevState.modal
        }));
    }

    toggleLignEnseign = (index, indice) => {
        this.setState(prevState => ({
            modalEnseign: !prevState.modalEnseign,
            indexLignEnseignASupprimer: index,
            indexSousLignEnseignASupprimer: indice
        }));
    }

    deleteLignEnseign = () => {
        this.state.arrayCycle[this.state.indexLignEnseignASupprimer].infoEnseignant.splice(this.state.indexSousLignEnseignASupprimer, 1)
        this.setState(prevState => ({
            modalEnseign: !prevState.modalEnseign
        }));
    }

    nonDeleteLignEnseign = () => {
        this.setState(prevState => ({
            modalEnseign: !prevState.modalEnseign
        }));
    }

    ajout = () => {
        // Convert date debut et date fin
        var longeur = this.state.arrayCycle.length
        for (var i = 0; i < longeur; i++) {
            // Convert date debut
            let dateDebut = this.state.arrayCycle[i].dateD
            let monthD = '' + (dateDebut.getMonth() + 1);
            let dayD = '' + dateDebut.getDate();
            let yearD = dateDebut.getFullYear();
            if (monthD.length < 2)
                monthD = '0' + monthD;
            if (dayD.length < 2)
                dayD = '0' + dayD;
            var convertDateD = [yearD, monthD, dayD].join('-');
            this.state.arrayCycle[i].dateD = convertDateD
            // Convert date fin
            let dateFin = this.state.arrayCycle[i].dateF
            let monthF = '' + (dateFin.getMonth() + 1);
            let dayF = '' + dateFin.getDate();
            let yearF = dateFin.getFullYear();
            if (monthF.length < 2)
                monthF = '0' + monthF;
            if (dayF.length < 2)
                dayF = '0' + dayF;
            var convertDateF = [yearF, monthF, dayF].join('-');
            this.state.arrayCycle[i].dateF = convertDateF
        }
        var userPermissions = JSON.parse((localStorage.getItem('userPermissions')));
        var userId = userPermissions[0].idUser
        var anneeScolaire = userPermissions[0].anneeScolaire
        jQuery.ajax({
            url: process.env.REACT_APP_API_URL + 'Back_mediatraining/UpdCycle.php',
            type: 'POST',
            data: {
                nomCycle: this.state.nomCycle,
                nbrHeureCycle: this.state.nbrHeure,
                tarifCycle: this.state.tarifCycle,
                tarifCycleAvecCarte: this.state.tarifAvecCarte,
                typeEduc: this.state.selectedType,
                arrayCycle: this.state.arrayCycle,
                userId: userId,
                anneeScolaire: anneeScolaire
            },
            dataType: 'json',
            success: (code_html, statut) => {
                if (code_html.Result == "OK") {
                    this.props.history.push('/Cycles');
                }
                else if (code_html.Result == "KO") {
                    alert("erreur,l'un de vos champs est vide!!");
                }
            }
        });
    }

    render() {
        return (
            <div id="page-wrapper" style={{ minHeight: '263px', marginLeft: '0px' }}>
                <div className="container-fluid">
                    <div className="row bg-title">
                        <div className="col-lg-12">
                            <h4 className="page-title">Modification cycle</h4>
                        </div>
                        {/* /.col-lg-12 */}
                    </div>
                    {/* /.row */}
                    <div className="row">
                        <div className="col-sm-12">
                            <div className="white-box">
                                <div className="row">
                                    <div className="col-md-6">
                                        <div className="form-group">
                                            <label>Cycle</label>
                                            <input style={{ backgroundColor: 'white', borderRadius: '5px', border: 'solid 1px #B3B3B3' }} type="text" className="form-control" value={this.state.nomCycle} onChange={(e) => this.updNomCycle(e)} />
                                        </div>
                                    </div>
                                    <div className="col-md-6">
                                        <div className="form-group">
                                            <label>Heure</label>
                                            <input style={{ backgroundColor: 'white', borderRadius: '5px', border: 'solid 1px #B3B3B3' }} type="number" className="form-control" value={this.state.nbrHeureCycle} onChange={(e) => this.updNbrHeure(e)} />
                                        </div>
                                    </div>
                                    <div className="col-md-6">
                                        <div className="form-group">
                                            <label>Tarif cycle</label>
                                            <input style={{ backgroundColor: 'white', borderRadius: '5px', border: 'solid 1px #B3B3B3' }} type="number" className="form-control" value={this.state.tarifCycle} onChange={(e) => this.updTarif(e)} />
                                        </div>
                                    </div>
                                    <div className="col-md-6">
                                        <div className="form-group">
                                            <label>Tarif avec carte</label>
                                            <input style={{ backgroundColor: 'white', borderRadius: '5px', border: 'solid 1px #B3B3B3' }} type="number" className="form-control" value={this.state.tarifCycleAvecCarte} onChange={(e) => this.updTarifAvecCarte(e)} />
                                        </div>
                                    </div>
                                </div>
                                <div className="row">
                                    <h3 style={{ paddingLeft: '15px' }}>{'Planning Cycle AnnÃ©e Scolaire ' + this.state.anneScol}</h3>
                                    <table style={{ marginTop: '10px' }} className="table table-bordered">
                                        <thead>
                                            <tr>
                                                <th className="col-md-1" style={{ textAlign: 'center' }} scope="col">Mois</th>
                                                <th className="col-md-1" style={{ textAlign: 'center' }} scope="col">Date Debut</th>
                                                <th className="col-md-1" style={{ textAlign: 'center' }} scope="col">Date Fin</th>
                                                <th className="col-md-1" style={{ textAlign: 'center' }} scope="col">Horraires</th>
                                                <th className="col-md-1" style={{ textAlign: 'center' }} scope="col">Confirmation</th>
                                                <th className="col-md-6" style={{ textAlign: 'center' }} scope="col">Enseignent</th>
                                                <th className="col-md-1" style={{ textAlign: 'center' }} scope="col">Supprimer</th>
                                            </tr>
                                        </thead>
                                        {this.state.arrayCycle.map((el, index) =>
                                            <tbody>
                                                <tr>
                                                    <td style={{ textAlign: 'center' }}><Select
                                                        value={el.selectedMois}
                                                        isClearable={true}
                                                        isSearchable={true}
                                                        onChange={(e) => this.updMois(e, index)}
                                                        options={this.state.mois}
                                                    /></td>
                                                    <td style={{ textAlign: 'center' }}><DatePicker class="form-control ddate" style={{ border: 'none' }}  onChange={(e) => this.updDateD(e, index)} /></td>
                                                    <td style={{ textAlign: 'center' }}><DatePicker class="form-control ddate" style={{ border: 'none' }}  onChange={(e) => this.updDateF(e, index)} /></td>
                                                    <td style={{ textAlign: 'center' }}><Select
                                                        value={el.selectedCour}
                                                        isClearable={true}
                                                        isSearchable={true}
                                                        onChange={(e) => this.updHorraireCours(e, index)}
                                                        options={this.state.cours}
                                                    />
                                                    </td>
                                                    {this.state.arrayCycle[index].selectedChoix == "Oui" ?
                                                        (<Fragment>
                                                            <td style={{ textAlign: 'center' }}>
                                                                <div>
                                                                    <table className="table table-bordered">
                                                                        {el.infoEnseignant.map((ele, indice) =>
                                                                            <tbody>
                                                                                <tr>
                                                                                    <td className="col-md-12" style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
                                                                                        <div className="col-md-12">
                                                                                            <Select
                                                                                                value={ele.selectedEnseignent}
                                                                                                isClearable={true}
                                                                                                isSearchable={true}
                                                                                                onChange={(e) => this.addEnseignent(e, index, indice)}
                                                                                                options={this.state.enseignants}
                                                                                            />
                                                                                        </div>
                                                                                    </td>
                                                                                    <td className="col-md-2">
                                                                                        <input style={{ backgroundColor: 'white', borderRadius: '5px', border: 'solid 1px #B3B3B3' }} value={el.nbrHeureParEnseign} type="number" className="form-control" onChange={(e) => this.addNbrHeureParEnseign(e, index, indice)} />
                                                                                    </td>
                                                                                    <td className="col-md-3">
                                                                                        <input style={{ backgroundColor: 'white', borderRadius: '5px', border: 'solid 1px #B3B3B3' }} value={Number.parseFloat(ele.honoraireParEnseign).toFixed(3)} type="number" className="form-control" onChange={(e) => this.addHonoraireParEnseign(e, index, indice)} />
                                                                                    </td>
                                                                                    <td>
                                                                                        <button style={{ backgroundColor: '#D9534F', borderRadius: '5px' }} onClick={() => this.toggleLignEnseign(index, indice)}><i style={{ color: 'white' }} className="fas fa-trash-alt"></i></button>
                                                                                    </td>
                                                                                </tr>
