import React, { Component } from 'react';
import { Link, BrowserRouter as Router, Route, Switch, withRouter } from 'react-router-dom'

class StatiquePage extends Component {
    constructor(props) {
        super(props);
        this.state = {
            listpermissions: [],
            faveurClient: [],
            faveurDemande: [],
            faveurFeedBackClient: [],
            faveurService: [],
            faveurTravailleur: []
        }
    }

    componentDidMount() {
        var array_permission = JSON.parse((localStorage.getItem('tab_permission')));
        if (array_permission != null) {
            this.setState({ listpermissions: array_permission })
            console.log('*******')
            console.log(array_permission)
            console.log('*******')
            var client = array_permission[0]
            this.setState({ faveurClient: client })
            var demande = array_permission[1]
            this.setState({ faveurDemande: demande })
            var feedBackClient = array_permission[2]
            this.setState({ faveurFeedBackClient: feedBackClient })
            var service = array_permission[3]
            this.setState({ faveurService: service })
            var travailleur = array_permission[4]
            this.setState({ faveurTravailleur: travailleur })
        }
        else {
            this.props.history.push('/');
        }
    }

    render() {
        return (
            <div id="wrapper">
                {/* Navigation */}
                <nav className="navbar navbar-default navbar-static-top" style={{ marginBottom: 0 }}>
                    <div className="navbar-header"> <a className="navbar-toggle hidden-sm hidden-md hidden-lg " href="javascript:void(0)" data-toggle="collapse" data-target=".navbar-collapse"><i className="ti-menu" /></a>
                        <div className="top-left-part"><a className="logo"><i className="glyphicon glyphicon-fire" />&nbsp;<span className="hidden-xs">Snaii</span></a></div>
                        <ul className="nav navbar-top-links navbar-left hidden-xs">
                            <li><a href="javascript:void(0)" className="open-close hidden-xs waves-effect waves-light"><i className="ti-menu icon-arrow-left-circle" /></a></li>
                        </ul>
                        <ul className="nav navbar-top-links navbar-right pull-right">
                            <li>
                                <Link className="profile-pic" to="/Login"> <img src="images/users/logout.png" alt="user-img" width={25} className="img-circle" /><b className="hidden-xs">Logout</b> </Link>
                            </li>
                        </ul>
                    </div>
                    {/* /.navbar-header */}
                    {/* /.navbar-top-links */}
                    {/* /.navbar-static-side */}
                </nav>
                <div className="navbar-default sidebar nicescroll" role="navigation">
                    <div className="sidebar-nav navbar-collapse">
                        <ul className="nav in" id="side-menu">
                            <li className="sidebar-search hidden-sm hidden-md hidden-lg">
                                <div className="input-group custom-search-form">
                                    <input type="text" className="form-control" placeholder="Search..." />
                                    <span className="input-group-btn">
                                        <button className="btn btn-default" type="button"><i className="ti-search" /> </button>
                                    </span>
                                </div>
                            </li>
                            {(this.state.faveurClient.ajouter == 1 || this.state.faveurClient.explorer == 1 || this.state.faveurClient.modifier == 1 || this.state.faveurClient.supprimer == 1) ?
                                (<li>
                                    <Link to="/Clients" className="waves-effect"><i className="fas fa-users" /> Clients </Link>
                                </li>) : (null)}
                            {(this.state.faveurDemande.ajouter == 1 || this.state.faveurDemande.explorer == 1 || this.state.faveurDemande.modifier == 1 || this.state.faveurDemande.supprimer == 1) ?
                                (<li>
                                    <Link to="/Demandes" className="waves-effect"><i className="ti-layout fa-fw" /> Demandes </Link>
                                </li>) : (null)}
                            {(this.state.faveurFeedBackClient.ajouter == 1 || this.state.faveurFeedBackClient.explorer == 1 || this.state.faveurFeedBackClient.modifier == 1 || this.state.faveurFeedBackClient.supprimer == 1) ?
                                (<li>
                                    <Link to="/FeedBackClients" className="waves-effect"><i className="fas fa-edit" /> FeedBackClients </Link>
                                </li>) : (null)}
                            {(this.state.faveurService.ajouter == 1 || this.state.faveurService.explorer == 1 || this.state.faveurService.modifier == 1 || this.state.faveurService.supprimer == 1) ?
                                (<li>
                                    <Link to="/Services" className="waves-effect"><i className="fas fa-concierge-bell" /> Services </Link>
                                </li>) : (null)}
                            {(this.state.faveurTravailleur.ajouter == 1 || this.state.faveurTravailleur.explorer == 1 || this.state.faveurTravailleur.modifier == 1 || this.state.faveurTravailleur.supprimer == 1) ?
                                (<li>
                                    <Link to="/Travailleurs" className="waves-effect"><i className="fas fa-people-arrows" /> Travailleurs </Link>
                                </li>) : (null)}
                        </ul>
                    </div>
                    {/* /.sidebar-collapse */}
                </div>
                {this.props.children}
            </div>
        );
    }
}

export default StatiquePage;
