import React, { Component, Fragment } from 'react'
import { Link, BrowserRouter as Router, Route, Switch, withRouter, HashRouter, Redirect } from 'react-router-dom'
import DataTable, { createTheme } from 'react-data-table-component';
import axios from 'axios'
import jQuery from 'jquery'
import './paginate.css'
import { Button, Modal, ModalHeader, ModalBody, ModalFooter } from 'reactstrap';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import { Slide, Zoom, Flip, Bounce } from 'react-toastify';
import Collapsible from 'react-collapsible';
import Select from 'react-select';
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";

class ReglApprenant extends Component {
    constructor(props) {
        super(props);
        this.state = {
            data: [],
            columns: [
                {
                    name: 'Num inscrip',
                    selector: 'numInscrip',
                    sortable: false,
                    center: true,
                    style: { width: '10%' }
                },
                {
                    name: 'Apprenant',
                    selector: 'apprenant',
                    sortable: false,
                    center: true,
                    style: { width: '22%' }
                },
                {
                    name: 'Type paiement',
                    selector: 'typePaiement',
                    sortable: false,
                    center: true,
                    style: { width: '10%' }
                },
                {
                    name: 'Frais réglé',
                    cell: row => <table style={{ width: '166px' }} class="table table-bordered">
                        <thead>
                            <tr>
                                <th style={{ textAlign: 'center', width: '90px' }} scope="col">Frais</th>
                                <th style={{ textAlign: 'center', width: '65px' }} scope="col">Montant</th>
                            </tr>
                        </thead>
                        {row.reglFrais.map((el, index) =>
                            <tbody>
                                <tr>
                                    <td style={{ textAlign: 'center' }}><p>{el.frais}</p></td>
                                    <td style={{ textAlign: 'center' }}><p>{el.mntPayer}</p></td>
                                </tr>
                            </tbody>
                        )}
                    </table>,
                    ignoreRowClick: true,
                    allowOverflow: true,
                    button: true,
                    width: '25%'
                },
                {
                    name: 'Reglement inscription',
                    cell: row => <div onClick={() => this.reglApprenant(row)} style={{ display: 'flex', justifyContent: 'space-between' }}><div><i style={{ color: '#87B87F' }} class="fas fa-money-check-alt"></i></div><div style={{ marginLeft: '5px' }}><p>Reglement</p></div></div>
                    ,
                    ignoreRowClick: true,
                    allowOverflow: true,
                    button: true,
                    width: '23%'
                },
                {
                    name: 'Recu',
                    cell: row => row.affichRecuPay == 1 ? (<i style={{ color: '#2192FF', fontSize: '20px' }} onClick={() => this.attPay(row)} class="fas fa-file-invoice"></i>) : (null),
                    ignoreRowClick: true,
                    allowOverflow: true,
                    button: true,
                    width: '10%'
                }
            ],
            nombreTotalElement: 0,
            filterElement: '',
            mycurrentPage: 1,
            resetFirstPage: false,
            nbrEltperPage: 5,
            modal: false,
            idRegl: '',
            pending: true,
            reglInscrip: [],
            // search
            listEleve: [],
            selectedIdEleve: '',
            listEtat: [{ value: 0, label: 'Non réglé' }, { value: 1, label: 'réglé' }],
            selectedEtat: '',
            listModPay: [],
            selectedIdModPay: '',
            listGroup: [],
            selectedIdGroup: '',
            dateSearchDebut: '',
            dateSearchFin: ''
        }
        this.newElement = this.newElement.bind(this);
        this.filterSearch = this.filterSearch.bind(this);
    }

    componentDidMount() {
        if (JSON.parse((localStorage.getItem('userPermissions'))) != null) {
            var userPermissions = JSON.parse((localStorage.getItem('userPermissions')));
            var anneeScolaire = userPermissions[0].anneeScolaire
            jQuery.ajax({
                url: process.env.REACT_APP_API_URL + 'Back_mediatraining/ReglementsApp.php',
                type: 'POST',
                data: {
                    query: '',
                    limit: this.state.nbrEltperPage,
                    page: 1,
                    anneeScolaire: anneeScolaire
                },
                dataType: 'json',
                success: (code_html, statut) => {
                    console.log(code_html)
                    this.setState({
                        nombreTotalElement: code_html.total,
                        data: code_html.data,
                        pending: false
                    });
                }
            });
            jQuery.ajax({
                url: process.env.REACT_APP_API_URL + 'Back_mediatraining/SelectEleves.php',
                type: 'POST',
                data: {
                    anneeScolaire: anneeScolaire
                },
                dataType: 'json',
                success: (code_html, statut) => {
                    console.log(code_html)
                    this.setState({
                        listEleve: code_html
                    });
                }
            });
            axios.get(process.env.REACT_APP_API_URL + 'Back_mediatraining/SelectTypesPayEleve.php').then(res => {
                console.log(res)
                this.setState({
                    listModPay: res.data
                })
            })
            jQuery.ajax({
                url: process.env.REACT_APP_API_URL + 'Back_mediatraining/SelectGroupes.php',
                type: 'POST',
                data: {
                    anneeScolaire: anneeScolaire
                },
                dataType: 'json',
                success: (code_html, statut) => {
                    console.log(code_html)
                    this.setState({
                        listGroup: code_html
                    });
                }
            });
        }
    }

    reglApprenant = (row) => {
        this.setState({
            reglInscrip: row.reglInscrip
        }, () => {
            console.log(this.state.reglInscrip.detailRegl)
            this.setState(prevState => ({
                modal: !prevState.modal,
            }));
        });
    }

    toggle = () => {
        this.setState(prevState => ({
            modal: !prevState.modal,
        }));
    }

    attPay = (row) => {
        var idEnteteRegl = row.idEnteteRegl
        var idNiveau = row.idNiveau
        var typePaiement = row.typePaiement
        jQuery.ajax({
            url: process.env.REACT_APP_API_URL + 'Php_client_jasper/Report_jasper_recu_paiement.php',
            type: 'POST',
            data: {
                idEnteteRegl: idEnteteRegl,
                idNiveau: idNiveau,
                typePaiement: typePaiement
            },
            dataType: 'json',
            success: function (code_html, statut) {
                var server_path = code_html.server_path;
                var file_path = code_html.file_path
                var path = server_path + file_path;
                window.open(path, "_blank")
            }
        });
    }

    recuRegl = (ele) => {
        var idDetailScol = ele.idDetailScol
        var totalPay = ele.totalPay
        jQuery.ajax({
            url: process.env.REACT_APP_API_URL + 'Php_client_jasper/Report_jasper_recu_regl.php',
            type: 'POST',
            data: {
                idDetailScol: idDetailScol,
                totalPay: totalPay
            },
            dataType: 'json',
            success: function (code_html, statut) {
                var server_path = code_html.server_path;
                var file_path = code_html.file_path
                var path = server_path + file_path;
                window.open(path, "_blank")
            }
        });
    }

    newElement(page, totalRows) {
        if (this.state.selectedIdEleve != '') {
            var idEleve = this.state.selectedIdEleve.value
        }
        else {
            var idEleve = ''
        }
        if (this.state.selectedEtat != '') {
            var idEtat = this.state.selectedEtat.value
        }
        else {
            var idEtat = ''
        }
        if (this.state.selectedIdModPay != '') {
            var idModPay = this.state.selectedIdModPay.value
        }
        else {
            var idModPay = ''
        }
        if (this.state.selectedIdGroup != '') {
            var idGroup = this.state.selectedIdGroup.value
        }
        else {
            var idGroup = ''
        }
        var convertDateSearchDebut = ""
        if (this.state.dateSearchDebut != '') {
            // convert dateSearchDebut
            let debutDate = this.state.dateSearchDebut
            let month = '' + (debutDate.getMonth() + 1);
            let day = '' + debutDate.getDate();
            let year = debutDate.getFullYear();
            if (month.length < 2)
                month = '0' + month;
            if (day.length < 2)
                day = '0' + day;
            var convertDateSearchDebut = [year, month, day].join('-');
        }
        var convertDateSearchFin = ""
        if (this.state.dateSearchFin != '') {
            // convert dateSearchFin
            let FinDate = this.state.dateSearchFin
            let month = '' + (FinDate.getMonth() + 1);
            let day = '' + FinDate.getDate();
            let year = FinDate.getFullYear();
            if (month.length < 2)
                month = '0' + month;
            if (day.length < 2)
                day = '0' + day;
            var convertDateSearchFin = [year, month, day].join('-')
        }
        if ((idEleve != '') && (idModPay != '') && (idGroup != '') && (convertDateSearchDebut != '') && (convertDateSearchFin != '')) {
            this.setState({
                pending: true
            },
                () => {
                    jQuery.ajax({
                        url: process.env.REACT_APP_API_URL + 'Back_mediatraining/ReglementsApp.php',
                        type: 'POST',
                        data: {
                            query: this.state.filterElement,
                            limit: this.state.nbrEltperPage,
                            page: page
                        },
                        dataType: 'json',
                        success: (code_html, statut) => {
                            console.log(code_html)
                            this.setState({
                                nombreTotalElement: code_html.total,
                                data: code_html.data,
                                pending: false
                            });
                        }
                    });
                }
            )
        }
        else {
            this.setState({
                pending: true
            },
                () => {
                    jQuery.ajax({
                        url: process.env.REACT_APP_API_URL + 'Back_mediatraining/ReglementsAppSearch.php',
                        type: 'POST',
                        data: {
                            idEleve: idEleve,
                            idEtat: idEtat,
                            idModPay: idModPay,
                            idGroup: idGroup,
                            dateSearchDebut: convertDateSearchDebut,
                            dateSearchFin: convertDateSearchFin,
                            limit: this.state.nbrEltperPage,
                            page: page
                        },
                        dataType: 'json',
                        success: (code_html, statut) => {
                            console.log(code_html)
                            this.setState({
                                nombreTotalElement: code_html.total,
                                data: code_html.data,
                                pending: false
                            });
                        }
                    });
                }
            )
        }
    }

    perPage(currentRowsPerPage, currentPage) {
        if (this.state.selectedIdEleve != '') {
            var idEleve = this.state.selectedIdEleve.value
        }
        else {
            var idEleve = ''
        }
        if (this.state.selectedEtat != '') {
            var idEtat = this.state.selectedEtat.value
        }
        else {
            var idEtat = ''
        }
        if (this.state.selectedIdModPay != '') {
            var idModPay = this.state.selectedIdModPay.value
        }
        else {
            var idModPay = ''
        }
        if (this.state.selectedIdGroup != '') {
            var idGroup = this.state.selectedIdGroup.value
        }
        else {
            var idGroup = ''
        }
        var convertDateSearchDebut = ""
        if (this.state.dateSearchDebut != '') {
            // convert dateSearchDebut
            let debutDate = this.state.dateSearchDebut
            let month = '' + (debutDate.getMonth() + 1);
            let day = '' + debutDate.getDate();
            let year = debutDate.getFullYear();
            if (month.length < 2)
                month = '0' + month;
            if (day.length < 2)
                day = '0' + day;
            var convertDateSearchDebut = [year, month, day].join('-');
        }
        var convertDateSearchFin = ""
        if (this.state.dateSearchFin != '') {
            // convert dateSearchFin
            let FinDate = this.state.dateSearchFin
            let month = '' + (FinDate.getMonth() + 1);
            let day = '' + FinDate.getDate();
            let year = FinDate.getFullYear();
            if (month.length < 2)
                month = '0' + month;
            if (day.length < 2)
                day = '0' + day;
            var convertDateSearchFin = [year, month, day].join('-')
        }
        if ((idEleve !== '') && (idModPay !== '') && (idGroup !== '') && (convertDateSearchDebut !== '') && (convertDateSearchFin !== '')) {
            this.setState(
                {
                    nbrEltperPage: currentRowsPerPage,
                    pending: true
                }
                , () => {
                    jQuery.ajax({
                        url: process.env.REACT_APP_API_URL + 'Back_mediatraining/ReglementsApp.php',
                        type: 'POST',
                        data: {
                            query: this.state.filterElement,
                            limit: currentRowsPerPage,
                            page: currentPage
                        },
                        dataType: 'json',
                        success: (code_html, statut) => {
                            console.log(code_html)
                            this.setState({
                                nombreTotalElement: code_html.total,
                                data: code_html.data,
                                pending: false
                            });
                        }
                    });
                }
            );
        }
        else {
            this.setState({
                pending: true
            },
                () => {
                    jQuery.ajax({
                        url: process.env.REACT_APP_API_URL + 'Back_mediatraining/ReglementsAppSearch.php',
                        type: 'POST',
                        data: {
                            idEleve: idEleve,
                            idEtat: idEtat,
                            idModPay: idModPay,
                            idGroup: idGroup,
                            dateSearchDebut: convertDateSearchDebut,
                            dateSearchFin: convertDateSearchFin,
                            limit: currentRowsPerPage,
                            page: currentPage
                        },
                        dataType: 'json',
                        success: (code_html, statut) => {
                            console.log(code_html)
                            this.setState({
                                nombreTotalElement: code_html.total,
                                data: code_html.data,
                                pending: false
                            });
                        }
                    });
                }
            )
        }
    }

    filterSearch(event) {
        this.setState({
            filterElement: event.target.value,
            resetFirstPage: !this.state.resetFirstPage,
            pending: true
        },
            () => {
                jQuery.ajax({
                    url: process.env.REACT_APP_API_URL + 'Back_mediatraining/ReglementsApp.php',
                    type: 'POST',
                    data: {
                        query: this.state.filterElement,
                        limit: this.state.nbrEltperPage,
                        page: 1
                    },
                    dataType: 'json',
                    success: (code_html, statut) => {
                        console.log(code_html)
                        this.setState({
                            nombreTotalElement: code_html.total,
                            data: code_html.data,
                            pending: false
                        });
                    }
                })
            }
        );
    }

    searchEleve = selectedIdEleve => {
        if (selectedIdEleve !== null) {
            this.setState({ selectedIdEleve });
        }
        else {
            this.setState({
                selectedIdEleve: ''
            });
        }
    };

    searchEtat = selectedEtat => {
        if (selectedEtat !== null) {
            this.setState({ selectedEtat });
        }
        else {
            this.setState({
                selectedEtat: ''
            });
        }
    };

    searchModPay = selectedIdModPay => {
        if (selectedIdModPay !== null) {
            this.setState({ selectedIdModPay });
        }
        else {
            this.setState({
                selectedIdModPay: ''
            });
        }
    };

    searchGroup = selectedIdGroup => {
        if (selectedIdGroup !== null) {
            this.setState({ selectedIdGroup });
        }
        else {
            this.setState({
                selectedIdGroup: ''
            });
        }
    };

    searchDateDebut = d => {
        this.setState({
            dateSearchDebut: d
        });
    };

    searchDateFin = d => {
        this.setState({
            dateSearchFin: d
        });
    };

    recherche = () => {
        var userPermissions = JSON.parse((localStorage.getItem('userPermissions')));
        var anneeScolaire = userPermissions[0].anneeScolaire
        if (this.state.selectedIdEleve != '') {
            var idEleve = this.state.selectedIdEleve.value
        }
        else {
            var idEleve = ''
        }
        if (this.state.selectedEtat != '') {
            var idEtat = this.state.selectedEtat.value
        }
        else {
            var idEtat = ''
        }
        if (this.state.selectedIdModPay != '') {
            var idModPay = this.state.selectedIdModPay.value
        }
        else {
            var idModPay = ''
        }
        if (this.state.selectedIdGroup != '') {
            var idGroup = this.state.selectedIdGroup.value
        }
        else {
            var idGroup = ''
        }
        var convertDateSearchDebut = ""
        if (this.state.dateSearchDebut != '') {
            // convert dateSearchDebut
            let debutDate = this.state.dateSearchDebut
            let month = '' + (debutDate.getMonth() + 1);
            let day = '' + debutDate.getDate();
            let year = debutDate.getFullYear();
            if (month.length < 2)
                month = '0' + month;
            if (day.length < 2)
                day = '0' + day;
            var convertDateSearchDebut = [year, month, day].join('-');
        }
        var convertDateSearchFin = ""
        if (this.state.dateSearchFin != '') {
            // convert dateSearchFin
            let FinDate = this.state.dateSearchFin
            let month = '' + (FinDate.getMonth() + 1);
            let day = '' + FinDate.getDate();
            let year = FinDate.getFullYear();
            if (month.length < 2)
                month = '0' + month;
            if (day.length < 2)
                day = '0' + day;
            var convertDateSearchFin = [year, month, day].join('-')
        }
        this.setState({
            resetFirstPage: !this.state.resetFirstPage,
            pending: true
        },
            () => {
                jQuery.ajax({
                    url: process.env.REACT_APP_API_URL + 'Back_mediatraining/ReglementsAppSearch.php',
                    type: 'POST',
                    data: {
                        eleve: idEleve,
                        etat: idEtat,
                        modPay: idModPay,
                        group: idGroup,
                        dateDebut: convertDateSearchDebut,
                        dateFin: convertDateSearchFin,
                        anneeScolaire: anneeScolaire,
                        limit: this.state.nbrEltperPage,
                        page: 1
                    },
                    dataType: 'json',
                    success: (code_html, statut) => {
                        console.log(code_html)
                        this.setState({
                            nombreTotalElement: code_html.total,
                            data: code_html.data,
                            pending: false
                        });
                    }
                })
            }
        );
    }

    afficherTout = () => {
        var userPermissions = JSON.parse((localStorage.getItem('userPermissions')));
        var anneeScolaire = userPermissions[0].anneeScolaire
        jQuery.ajax({
            url: process.env.REACT_APP_API_URL + 'Back_mediatraining/ReglementsApp.php',
            type: 'POST',
            data: {
                query: '',
                limit: this.state.nbrEltperPage,
                page: 1,
                anneeScolaire: anneeScolaire
            },
            dataType: 'json',
            success: (code_html, statut) => {
                console.log(code_html)
                this.setState({
                    nombreTotalElement: code_html.total,
                    data: code_html.data,
                    pending: false
                });
            }
        });
    }

    render() {
        return (
            <div id="page-wrapper" style={{ minHeight: '263px', marginLeft: '0px' }}>
                <div className="container-fluid">
                    <div className="row bg-title">
                        <div className="col-lg-12">
                            <h4 className="page-title">Reglement</h4>
                        </div>
                        {/* /.col-lg-12 */}
                    </div>
                    {/* /.row */}
                    <div class="row">
                        <div class="col-sm-12">
                            <div class="white-box">
                                <div class="row">
                                    <h4 style={{ paddingLeft: '10px' }}>Critères de recherche</h4>
                                    <Fragment>
                                        <div class="col-md-6">
                                            <div class="form-group">
                                                <label>Apprenant</label>
                                                <Select
                                                    isClearable={true}
                                                    isSearchable={true}
                                                    onChange={this.searchEleve}
                                                    options={this.state.listEleve}
                                                />
                                            </div>
                                        </div>
                                        <div class="col-md-6">
                                            <div class="form-group">
                                                <label>Etat</label>
                                                <Select
                                                    isClearable={true}
                                                    isSearchable={true}
                                                    onChange={this.searchEtat}
                                                    options={this.state.listEtat}
                                                />
                                            </div>
                                        </div>
                                        <div class="col-md-6">
                                            <div class="form-group">
                                                <label>Mode paiement</label>
                                                <Select
                                                    isClearable={true}
                                                    isSearchable={true}
                                                    onChange={this.searchModPay}
                                                    options={this.state.listModPay}
                                                />
                                            </div>
                                        </div>
                                        <div class="col-md-6">
                                            <div class="form-group">
                                                <label>Groupe</label>
                                                <Select
                                                    isClearable={true}
                                                    isSearchable={true}
                                                    onChange={this.searchGroup}
                                                    options={this.state.listGroup}
                                                />
                                            </div>
                                        </div>
                                        <span>
                                            <div class="col-md-6">
                                                <DatePicker class="form-control ddate" style={{ border: 'none' }}
                                                    selected={this.state.dateSearchDebut}
                                                    onChange={this.searchDateDebut}
                                                    customInput={<div><label>Date début</label><div class="form-group"><input style={{ height: '38px', borderRadius: '5px', border: 'solid 1px', borderColor: '#B3B3B3' }} /></div></div>}
                                                />
                                            </div>
                                            <div class="col-md-6">
                                                <DatePicker class="form-control ddate" style={{ border: 'none' }}
                                                    selected={this.state.dateSearchFin}
                                                    onChange={this.searchDateFin}
                                                    customInput={<div><label>Date fin</label><div class="form-group"><input style={{ height: '38px', borderRadius: '5px', border: 'solid 1px', borderColor: '#B3B3B3' }} /></div></div>}
                                                />
                                            </div>
                                        </span>
                                    </Fragment>
                                </div>
                                <div style={{ display: 'flex', justifyContent: 'space-around' }}>
                                    <div>
                                        <button style={{ borderRadius: '5px', backgroundColor: '#761C19', borderColor: '#761C19' }} type="button" className="btn btn-success" onClick={this.recherche}>Rechercher</button>
                                    </div>
                                    <div>
                                        <button style={{ borderRadius: '5px', backgroundColor: '#761C19', borderColor: '#761C19' }} type="button" className="btn btn-success" onClick={this.afficherTout}>Afficher tout</button>
                                    </div>
                                </div>
                                <div className="mb-0 form-group">
                                    <div className="input-group-alternative input-group">
                                        <div className="input-group-prepend">
                                            <span className="input-group-text">
                                                <i className="fas fa-search"></i>
                                            </span>
                                            <input onChange={(currentRowsPerPage, currentPage) => this.filterSearch(currentRowsPerPage, currentPage)} placeholder="Search" type="text" className="search" />
                                        </div>
                                    </div>
                                </div>
                                <DataTable
                                    columns={this.state.columns}
                                    data={this.state.data}
                                    highlightOnHover={true}
                                    pagination={true}
                                    paginationServer={true}
                                    onChangePage={(page, totalRows) => this.newElement(page, totalRows)}
                                    paginationTotalRows={this.state.nombreTotalElement}
                                    paginationPerPage={this.state.nbrEltperPage}
                                    paginationRowsPerPageOptions={[5, 10, 15, 20, 25]}
                                    onChangeRowsPerPage={(currentRowsPerPage, currentPage) => this.perPage(currentRowsPerPage, currentPage)}
                                    paginationResetDefaultPage={this.state.resetFirstPage}
                                    paginationComponentOptions={{ rowsPerPageText: 'Elements par page:', rangeSeparatorText: 'de', noRowsPerPage: false }}
                                    progressPending={this.state.pending}
                                />
                            </div>
                        </div>
                    </div>
                </div>
                <Modal isOpen={this.state.modal} toggle={this.toggle} className={this.props.className} fade={false}>
                    <ModalHeader toggle={this.toggle}><div style={{ display: 'flex', justifyContent: 'space-between' }}><div><i style={{ color: '#87B87F' }} class="fas fa-money-check-alt"></i></div><div style={{ marginLeft: '5px', fontWeight: 'bold' }}><p>Reglement eleve</p></div></div></ModalHeader>
                    <ModalBody>
                        <div>
                            <table class="table">
                                <thead >
                                    <tr>
                                        <th style={{ textAlign: 'center' }} scope="col">Date</th>
                                        <th style={{ textAlign: 'center' }} scope="col">Tranche</th>
                                        <th style={{ textAlign: 'center' }} scope="col">Etat</th>
                                    </tr>
                                </thead>
                                {this.state.reglInscrip.map((el, index) =>
                                    <tbody>
                                        <tr>
                                            <td style={{ textAlign: 'center' }}><p>{el.date}</p></td>
                                            <td style={{ textAlign: 'center' }}><p>{index + 1 + ' tranche'}</p></td>
                                            {el.etat !== '' ?
                                                (<td style={{ textAlign: 'center' }}><p>{el.etat}</p><p>{el.mntNonRegle}</p>
                                                    <Collapsible trigger={<button style={{ backgroundColor: '#D9534F', borderRadius: '5px' }}><i style={{ color: 'white' }} class="fas fa-info"></i></button>}>
                                                        <Fragment>
                                                            <table class="table table-bordered">
                                                                <thead >
                                                                    <tr>
                                                                        <th style={{ textAlign: 'center', fontSize: '11px' }} scope="col">Date</th>
                                                                        <th style={{ textAlign: 'center', fontSize: '11px' }} scope="col">Montant</th>
                                                                        <th style={{ textAlign: 'center', fontSize: '11px' }} scope="col">Reglement</th>
                                                                        <th style={{ textAlign: 'center', fontSize: '11px' }} scope="col">Piéce</th>
                                                                    </tr>
                                                                </thead>
                                                                {el.detailRegl.map(ele =>
                                                                    <tbody>
                                                                        <tr>
                                                                            <td style={{ textAlign: 'center', fontSize: '10px' }}><p>{el.date}</p></td>
                                                                            <td style={{ textAlign: 'center', fontSize: '10px' }}><p>{ele.totalPay}</p></td>
                                                                            {ele.typePay == "Chéque" ?
                                                                                (<td style={{ textAlign: 'center', fontSize: '10px' }}><p>{ele.typePay}</p><p style={{ fontSize: '9px' }}>{'Echéance : ' + ele.echeance}</p></td>) : (<td style={{ textAlign: 'center' }}><p>{ele.typePay}</p></td>)}
                                                                            <td><i onClick={() => this.recuRegl(ele)} class="fas fa-print"></i></td>
                                                                        </tr>
                                                                    </tbody>
                                                                )}
                                                            </table>
                                                        </Fragment>
                                                    </Collapsible>
                                                </td>) : (<td></td>)}
                                        </tr>
                                    </tbody>
                                )}
                            </table>
                            <div className='hvr-push' style={{ display: 'flex', justifyContent: 'space-around', marginBottom: '15px' }}>
                                <div><button onClick={() => this.toggle()} style={{ backgroundColor: '#D9534F', borderRadius: '5px', color: 'white', width: '40px ' }}><i style={{ color: 'white' }} class="fas fa-sign-out-alt"></i></button></div>
                            </div>
                        </div>
                    </ModalBody>
                </Modal>
                <ToastContainer transition={Flip} enableMultiContainer containerId={'A'} position={toast.POSITION.TOP_RIGHT} autoClose={2500} />
            </div>
        );
    }
}

export default ReglApprenant;
