            var dateEcheChec = this.state.tabModPay[i].echeanceChec;
            let monthOne = '' + (dateEcheChec.getMonth() + 1);
            let dayOne = '' + dateEcheChec.getDate();
            let yearOne = dateEcheChec.getFullYear();
            if (monthOne.length < 2) monthOne = '0' + monthOne;
            if (dayOne.length < 2) dayOne = '0' + dayOne;
            convertDateEcheChec = [yearOne, monthOne, dayOne].join('-');
          }
        }

        jQuery.ajax({
          url: process.env.REACT_APP_API_URL + 'Back_mediatraining/UpdReglementEnseignant.php',
          type: 'POST',
          data: {
            idReg: this.state.idReg,
            dateReg: convertDatePaiement,
            sommeTot: sommePaiement,
            regEnsAnc: this.state.regEnsAnc,
            regEnsNv: this.state.regEnsNv,
            modPay: this.state.tabModPay,
            echeanceChec: convertDateEcheChec,
