import React, { Component, Fragment } from 'react'
import axios from 'axios'
import jQuery from 'jquery'
import { Link, BrowserRouter as Router, Route, Switch, withRouter, HashRouter, Redirect } from 'react-router-dom'
import Select from 'react-select';
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";
import { Button, Modal, ModalHeader, ModalBody, ModalFooter } from 'reactstrap';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import { Slide, Zoom, Flip, Bounce } from 'react-toastify';
import Checkbox from 'rc-checkbox';
import 'rc-checkbox/assets/index.css';

class PaiementEtudGroup extends Component {
    constructor(props) {
        super(props);
        this.state = {
            formation: [],
            selectedFormation: '',
            groupe: [],
            selectedGroupe: ''
        }
    }

    componentDidMount() {
        if (JSON.parse((localStorage.getItem('userPermissions'))) != null) {
            var userPermissions = JSON.parse((localStorage.getItem('userPermissions')));
            var anneeScolaire = userPermissions[0].anneeScolaire
            jQuery.ajax({
                url: process.env.REACT_APP_API_URL + 'Back_mediatraining/SelectFilieres.php',
                type: 'POST',
                data: {
                    anneeScolaire: anneeScolaire
                },
                dataType: 'json',
                success: (code_html, statut) => {
                    this.setState({
                        formation: code_html
                    });
                }
            });
        }
        else {
            document.location = '/'
        }
    }

    addFormation = selectedFormation => {
        if (selectedFormation != null) {
            this.setState({ selectedFormation: selectedFormation }, () => {
                jQuery.ajax({
                    url: process.env.REACT_APP_API_URL + 'Back_mediatraining/SelectGroupesSelonForm.php',
                    type: 'POST',
                    data: {
                        anneeScolaire: anneeScolaire
                    },
                    dataType: 'json',
                    success: (code_html, statut) => {
                        this.setState({
                            formation: code_html
                        });
                    }
                });
            });
        }
        else {
            this.setState({ selectedFormation: '' })
        }
    };

    addGroupe = selectedGroupe => {
        this.setState({ selectedGroupe: selectedGroupe });
    };

    imprToday = () => {
        if (this.state.selectedGroupe != '') {
            var userPermissions = JSON.parse((localStorage.getItem('userPermissions')));
            var anneeScolaire = userPermissions[0].anneeScolaire
            // Date aujourdhui
            let dateToday = new Date()
            let month = '' + (dateToday.getMonth() + 1);
            let day = '' + dateToday.getDate();
            let year = dateToday.getFullYear();
            if (month.length < 2)
                month = '0' + month;
            if (day.length < 2)
                day = '0' + day;
            var today = [year, month, day].join('-');
            //
            jQuery.ajax({
                url: process.env.REACT_APP_API_URL + 'Back_mediatraining/ImprPayGroupeGlobal.php',
                type: 'POST',
                data: {
                    today: today,
                    groupe: this.state.selectedGroupe.value,
                    anneeScolaire: anneeScolaire
                },
                dataType: 'json',
                success: function (code_html, statut) {
                    var server_path = code_html.server_path;
                    var file_path = code_html.file_path
                    var path = server_path + file_path;
                    window.open(path, "_blank")
                }
            });
        }
        else {
            toast.error('⛔ Veuillez selectionner le groupe correspondant !!', { containerId: 'A' });
        }
    }

    impr = () => {
        if (this.state.selectedGroupe != '') {
            var userPermissions = JSON.parse((localStorage.getItem('userPermissions')));
            var anneeScolaire = userPermissions[0].anneeScolaire
            jQuery.ajax({
                url: process.env.REACT_APP_API_URL + 'Back_mediatraining/ImprPayGroupeGlobal.php',
                type: 'POST',
                data: {
                    today: '',
                    groupe: this.state.selectedGroupe.value,
                    anneeScolaire: anneeScolaire
                },
                dataType: 'json',
                success: function (code_html, statut) {
                    var server_path = code_html.server_path;
                    var file_path = code_html.file_path
                    var path = server_path + file_path;
                    window.open(path, "_blank")
                }
            });
        }
        else {
            toast.error('⛔ Veuillez selectionner le groupe correspondant !!', { containerId: 'A' });
        }
    }

    render() {
        return (
            <div id="page-wrapper" style={{ minHeight: '263px', marginLeft: '0px' }}>
                <div className="container-fluid">
                    <div className="row bg-title">
                        <div className="col-lg-12">
                            <h4 className="page-title">Paiments groupes</h4>
                        </div>
                        {/* /.col-lg-12 */}
                    </div>
                    {/* /.row */}
                    <div className="row">
                        <div className="col-sm-12">
                            <div className="white-box">
                                <div className="row">
                                    <div className="col-md-6">
                                        <div className="form-group">
                                            <label>Formations</label>
                                            <Select
                                                isClearable={true}
                                                isSearchable={true}
                                                onChange={(e) => this.addFormation(e)}
                                                options={this.state.formation}
                                            />
                                        </div>
                                    </div>
                                    <div className="col-md-6">
                                        <div className="form-group">
                                            <label></label>
                                            <Select
                                                isClearable={true}
                                                isSearchable={true}
                                                onChange={(e) => this.addGroupe(e)}
                                                options={this.state.groupe}
                                            />
                                        </div>
                                    </div>
                                </div>
                                <div style={{ display: 'flex', justifyContent: 'spaceAround' }}>
                                    <div>
                                        <button style={{ borderRadius: '5px', backgroundColor: '#761C19', borderColor: '#761C19' }} type="button" className="btn btn-success" onClick={this.imprToday}><i style={{ color: 'white', paddingRight: '5px' }} class="fas fa-print"></i>Impression aujourd'hui</button>
                                        <button style={{ borderRadius: '5px', backgroundColor: '#761C19', borderColor: '#761C19' }} type="button" className="btn btn-success" onClick={this.impr}><i style={{ color: 'white', paddingRight: '5px' }} class="fas fa-print"></i>Impression global</button>
                                    </div>
                                </div>
                            </div>
                            <ToastContainer transition={Flip} enableMultiContainer containerId={'A'} position={toast.POSITION.TOP_RIGHT} autoClose={2500} />
                        </div>
                    </div>
                </div>
            </div>
        );
    }
}
export default withRouter(PaiementEtudGroup);
