    addMntFraisPayer = (event, indexMntFraisPayer) => {
        let mntFraisPayer = event.target.value
        this.setState({
            listFrais: this.state.listFrais.map((el, id) => (id === indexMntFraisPayer ? Object.assign(el, { mntFraisPayer: Number.parseFloat(mntFraisPayer).toFixed(3) }) : el))
        }, () => {
            let mntFrais = parseInt(this.state.listFrais[indexMntFraisPayer].mntFrais)
            if (this.state.listFrais[indexMntFraisPayer].mntFraisPayer != "") {
                var mntFraisPay = parseInt(this.state.listFrais[indexMntFraisPayer].mntFraisPayer)
            }
            else {
                var mntFraisPay = 0
            }
            var mntFraisRest = parseInt(this.state.listFrais[indexMntFraisPayer].mntFraisRestant)
            if (mntFrais >= mntFraisPay) {
                if (mntFraisRest > 0) {
                    let mntFraisRestant = mntFrais - mntFraisPay
                    this.setState({
                        listFrais: this.state.listFrais.map((el, id) => (id === indexMntFraisPayer ? Object.assign(el, { mntFraisRestant: Number.parseFloat(mntFraisRestant).toFixed(3) }) : el))
                    });
                }
                let sommeMntFraisPayer = 0;
                for (var i = 0; i < this.state.listFrais.length; i++) {
                    if (this.state.listFrais[i].mntFraisPayer != "") {
                        sommeMntFraisPayer += parseInt(this.state.listFrais[i].mntFraisPayer)
                    }
                    else {
                        sommeMntFraisPayer += 0
                    }
                }
                this.setState({
                    mntFraisTotalPayer: Number.parseFloat(sommeMntFraisPayer).toFixed(3)
                });
            }
        });
    }

    addMntInscripPayer = (event, indexMntInscripPayer) => {
        let mntInscripPayer = event.target.value
        this.setState({
            payFormation: this.state.payFormation.map((el, id) => (id === indexMntInscripPayer ? Object.assign(el, { mntInscripPayer: Number.parseFloat(mntInscripPayer).toFixed(3) }) : el))
        }, () => {
            let mntInscrip = this.state.payFormation[indexMntInscripPayer].mntInscrip
            let mntInscripRestant = this.state.payFormation[indexMntInscripPayer].mntInscripRestant
            if (parseInt(mntInscrip) >= parseInt(mntInscripPayer)) {
                if (parseInt(mntInscripRestant) > 0) {
                    let mntInscripRest = mntInscrip - mntInscripPayer
                    this.setState({
                        payFormation: this.state.payFormation.map((el, id) => (id === indexMntInscripPayer ? Object.assign(el, { mntInscripRestant: Number.parseFloat(mntInscripRest).toFixed(3) }) : el))
                    });
                }
                let mntIncripTotalPayer = 0
                for (var i = 0; i < this.state.payFormation.length; i++) {
                    mntIncripTotalPayer += parseInt(this.state.payFormation[i].mntInscripPayer)
                }
                this.setState({
                    mntIncripTotalPayer: Number.parseFloat(mntIncripTotalPayer).toFixed(3)
                });
            }
        });
    }

