                                                    <label>Specialité</label>
                                                    <Select
                                                        isClearable={true}
                                                        isSearchable={true}
                                                        onChange={this.addSpecialite}
                                                        options={this.state.listSpecialites}
                                                    />
                                                </div>
                                            </div>
                                            <div class="col-md-6">
                                                <div class="form-group">
                                                    <label>Formation</label>
                                                    <Select
                                                        isClearable={true}
                                                        isSearchable={true}
                                                        onChange={this.addFormation}
                                                        options={this.state.listFormations}
                                                    />
                                                </div>
                                            </div>
                                            <div class="col-md-6">
                                                <div class="form-group">
                                                    <label>Niveau</label>
                                                    <Select
                                                        isClearable={true}
                                                        isSearchable={true}
                                                        onChange={this.addNiveau}
                                                        options={this.state.listNiveaux}
                                                    />
                                                </div>
                                            </div>
                                            <div class="col-md-6">
                                                <div class="form-group">
                                                    <label>Groupe</label>
                                                    <Select
                                                        isClearable={true}
                                                        isSearchable={true}
                                                        onChange={this.addGroup}
                                                        options={this.state.listGroupes}
                                                    />
                                                </div>
                                            </div>
                                        </div>
                                        <div style={{ marginBottom: '20px' }} class="row">
                                            <div class="col-md-6">
                                                <label>Papiers Apportées</label>
                                                <span style={{ display: 'flex', flexWrap: 'wrap', justifyContent: 'space-between', border: '1px solid', padding: '10px', borderRadius: '5px' }}>
                                                    {this.state.listPapiers.map((el, index) =>
                                                        <label>
                                                            <Checkbox
                                                                onChange={(e) => this.addPapiers(e, el, index)}
                                                                disabled={this.state.disabled}
                                                            />
                                                            {el.papier}
                                                        </label>
                                                    )}
                                                </span>
                                            </div>
                                            <div class="col-md-6">
                                                <label>Paiement</label>
                                                <RadioGroup
                                                    name="typePay"
                                                    selectedValue={this.state.selectedTypePay}
                                                    onChange={this.addTypePay}>
                                                    <div style={{ border: '1px solid', borderRadius: '5px', height: '72px', display: 'flex', justifyContent: 'space-between', alignItems: 'center', padding: '10px' }}>
                                                        {this.state.listTypePay.map(el =>
                                                            <div>
                                                                <label>
                                                                    <Radio value={el.id} />{el.type}
                                                                </label>
                                                            </div>
                                                        )}
                                                    </div>
                                                </RadioGroup>
                                            </div>
                                        </div>
                                    </Fragment>) : (null)}
                                {this.state.blockReglement == true ?
                                    (<div class="row">
                                        <table style={{ marginTop: '10px' }} class="table table-bordered">
                                            <thead>
                                                <tr>
                                                    <th style={{ textAlign: 'center' }} scope="col">Nom</th>
                                                    <th style={{ textAlign: 'center' }} scope="col">Montant total</th>
                                                    <th style={{ textAlign: 'center' }} scope="col">Montant payé</th>
                                                    <th style={{ textAlign: 'center' }} scope="col">Montant restant</th>
                                                </tr>
                                            </thead>
                                            {this.state.listFrais.map((el, index) =>
                                                <tbody>
                                                    <tr>
                                                        <td style={{ textAlign: 'center' }}><p style={{ textAlign: 'center', borderRadius: '5px' }}>{el.frais}</p></td>
                                                        <td style={{ textAlign: 'center' }}><input type="number" className='mntFrais' disabled value={el.mntFrais} style={{ textAlign: 'center', borderRadius: '5px' }} /></td>
                                                        <td style={{ textAlign: 'center' }}><input type="number" className='mntFraisPayer' value={el.mntFraisPayer} onChange={(e) => this.addMntFraisPayer(e, index)} style={{ textAlign: 'center', borderRadius: '5px' }} /></td>
                                                        <td style={{ textAlign: 'center' }}><input type="number" disabled className='mntFraisRestant' value={el.mntFraisRestant} style={{ textAlign: 'center', borderRadius: '5px' }} /></td>
                                                    </tr>
                                                </tbody>
                                            )}
                                        </table>
                                        <div style={{ display: 'flex', justifyContent: 'center', marginBottom: '10px', marginTop: '15px' }}>
                                            <div style={{ marginRight: '10px' }}>
                                                <p>Montant total frais payer</p>
                                            </div>
                                            <div style={{ marginRight: '-40px' }}>
                                                <input disabled value={this.state.mntFraisTotalPayer} style={{ border: 'solid', borderColor: '#DCDCDC', textAlign: 'center', width: '70%', borderRadius: '5px' }} type="text" />
                                            </div>
                                        </div>
                                        {paiementInscrip}
                                        <div style={{ display: 'flex', justifyContent: 'center', marginBottom: '10px', marginTop: '15px' }}>
                                            <div style={{ marginRight: '10px' }}>
                                                <p>Montant total inscription payer</p>
                                            </div>
                                            <div style={{ marginRight: '-40px' }}>
                                                <input disabled value={this.state.mntIncripTotalPayer} style={{ border: 'solid', borderColor: '#DCDCDC', textAlign: 'center', width: '70%', borderRadius: '5px' }} type="text" />
                                            </div>
                                        </div>
                                        <table style={{ marginTop: '10px' }} class="table table-bordered">
                                            <thead>
                                                <tr>
                                                    <th style={{ textAlign: 'center' }} scope="col">Mode paiement</th>
                                                    <th style={{ textAlign: 'center' }} scope="col">Montant Paiement</th>
                                                    <th style={{ textAlign: 'center' }} scope="col">Numéro cheque</th>
                                                    <th style={{ textAlign: 'center' }} scope="col">Echéance cheque</th>
                                                    <th style={{ textAlign: 'center' }} scope="col">Titulaire cheque</th>
                                                    <th style={{ textAlign: 'center' }} scope="col">Banque</th>
                                                    <th style={{ textAlign: 'center', width: '15%' }} scope="col">Suppression</th>
                                                </tr>
                                            </thead>
                                            {this.state.tabModPay.map((el, index) =>
                                                <tbody>
                                                    <tr>
                                                        <td style={{ width: '75%' }}><Select
                                                            value={el.modPay}
                                                            isClearable={true}
                                                            isSearchable={true}
                                                            onChange={(e) => this.addModPay(e, index)}
                                                            options={this.state.listModPay}
                                                        />  </td>
                                                        <td style={{ textAlign: 'center' }}><input type="number" value={el.mntModPay} onChange={(e) => this.addMntModPay(e, index)} style={{ textAlign: 'center', borderRadius: '5px', width: '110px' }} /></td>
                                                        {this.state.tabModPay[index].modPay.value == 2 ?
                                                            (<Fragment>
                                                                <td style={{ textAlign: 'center' }}><input type="number" value={el.numCheque} onChange={(e) => this.addNumChec(e, index)} style={{ textAlign: 'center', borderRadius: '5px', width: '110px' }} /></td>
                                                                <td style={{ textAlign: 'center' }}><DatePicker class="form-control ddate" style={{ border: 'none' }} selected={el.echeanceChec} onChange={(e) => this.addEcheanceChec(e, index)} /></td>
                                                                <td style={{ textAlign: 'center' }}><input type="text" value={el.titulaireChec} onChange={(e) => this.addTitulaireChec(e, index)} style={{ textAlign: 'center', borderRadius: '5px', width: '110px' }} /></td>
                                                                <td style={{ textAlign: 'center' }}><input type="text" value={el.banque} onChange={(e) => this.addBanque(e, index)} style={{ textAlign: 'center', borderRadius: '5px', width: '110px' }} /></td>
                                                                <td style={{ textAlign: 'center' }}><button style={{ width: '15%' }} className='buttonSupprimer' onClick={() => this.toggleLign(index)}><i style={{ color: 'white' }} class="fas fa-trash-alt"></i></button></td>
                                                            </Fragment>) :
                                                            (<Fragment>
                                                                <td style={{ textAlign: 'center' }}><input type="number" disabled style={{ textAlign: 'center', borderRadius: '5px', width: '110px' }} /></td>
                                                                <td style={{ textAlign: 'center' }}><input type="text" disabled style={{ textAlign: 'center', borderRadius: '5px' }} /></td>
                                                                <td style={{ textAlign: 'center' }}><input type="text" disabled style={{ textAlign: 'center', borderRadius: '5px', width: '110px' }} /></td>
                                                                <td style={{ textAlign: 'center' }}><input type="text" disabled style={{ textAlign: 'center', borderRadius: '5px' }} /></td>
                                                                <td style={{ textAlign: 'center' }}><button style={{ width: '15%' }} className='buttonSupprimer' onClick={() => this.toggleLign(index)}><i style={{ color: 'white' }} class="fas fa-trash-alt"></i></button></td>
                                                            </Fragment>)}
                                                    </tr>
                                                </tbody>
                                            )}
                                        </table>
                                        <div style={{ display: 'flex', justifyContent: 'flex-start', marginLeft: '15px' }}>
                                            <Button style={{ borderRadius: '5px' }} onClick={this.additionLign} color="secondary"><i class="fas fa-plus-circle"></i>Ajouter ligne</Button>
                                        </div>
                                    </div>) : (null)}
                                {this.state.ajoutInscrip === false ?
                                    (<div style={{ display: 'flex', justifyContent: 'center', marginBottom: '10px' }}>
                                        <button style={{ width: '15%', borderRadius: '5px' }} type="button" className="btn btn-success" onClick={this.ajoutInscrip}>Confirmer</button>
                                    </div>) : (<div style={{ display: 'flex', justifyContent: 'center', marginBottom: '10px' }}>
                                        <button style={{ width: '15%', borderRadius: '15px' }} type="button" className="btn btn-success" onClick={this.ajoutRegInscrip}>Confirmer</button>
                                    </div>)}
                                <Modal isOpen={this.state.modal} toggle={this.nonDeleteLign} className={this.props.className} fade={false}>
                                    <ModalHeader toggle={this.nonDeleteLign}> <div style={{ display: 'flex', justifyContent: 'center', width: '465px' }}><img width='30%' src="./images/deleteModal.png" alt=".." /></div></ModalHeader>
                                    <ModalBody>
                                        <div style={{ fontFamily: 'Montserrat, sans-serif', FontSize: '14px', FontWeight: '700', LineHeight: '18.375px' }}>
                                            <div style={{ display: 'flex', justifyContent: 'center' }}>
                                                <p>Êtes-Vous sûr de vouloir supprimer ce mode de paiement ?</p>
                                            </div>
                                            <div className='hvr-push' style={{ display: 'flex', justifyContent: 'space-around', marginBottom: '15px' }}>
                                                <div><button onClick={() => this.deleteLign()} style={{ backgroundColor: '#00FF7F', borderRadius: '5px', color: 'white' }}><i style={{ color: 'white' }} class="far fa-thumbs-up"></i>Oui</button></div>
                                                <div><button onClick={() => this.nonDeleteLign()} style={{ backgroundColor: '#D9534F', borderRadius: '5px', color: 'white' }}><i style={{ color: 'white' }} class="far fa-thumbs-down"></i>Non</button></div>
                                            </div>
                                        </div>
                                    </ModalBody>
                                </Modal>
                                <ToastContainer transition={Flip} enableMultiContainer containerId={'A'} position={toast.POSITION.TOP_RIGHT} autoClose={2500} />
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        );
    }
}

export default withRouter(Inscription);
