import React, { Component } from 'react'
import jQuery from 'jquery'
import { Link, BrowserRouter as Router, Route, Switch, withRouter, HashRouter, Redirect } from 'react-router-dom'
import Collapsible from 'react-collapsible';
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";

class Planning extends Component {
    constructor(props) {
        super(props);
        this.state = {
            dateDebut: '',
            dateFin: ''
        }
    }

    componentDidMount() {
        if (JSON.parse((localStorage.getItem('userPermissions'))) != null) {
        }
        else {
            document.location = '/'
        }
    }

    addDateDebut = d => {
        this.setState({
            dateDebut: d
        }, () => {
            if (this.state.dateDebut != '' && this.state.dateFin != '') {
                var arrayDate = []
                for (var i = this.state.dateDebut; i < this.state.dateFin; i++) {
                    var x = this.state.dateDebut.setDate(this.state.dateDebut.getDate() + 1)
                    var z = new Date(timestamp).toISOString(x)
                    arrayDate.push(z)
                }
            }
        });
    };

    addDateFin = d => {
        this.setState({
            dateFin: d
        }, () => {
        });
    };

    render() {
        return (
            <div id="page-wrapper" style={{ minHeight: '263px', marginLeft: '0px' }}>
                <div className="container-fluid">
                    <div className="row bg-title">
                        <div className="col-lg-12">
                            <h4 className="page-title">Planning</h4>
                        </div>
                        {/* /.col-lg-12 */}
                    </div>
                    {/* /.row */}
                    <div className="row">
                        <div className="col-sm-12">
                            <div className="white-box">
                                <div className="row">
                                    <div className="col-md-6">
                                        <div className="form-group">
                                            <label>Date debut</label>
                                            <DatePicker class="form-control ddate" style={{ border: 'none' }}
                                                selected={this.state.dateDebut}
                                                onChange={this.addDateDebut}
                                                dateFormat="dd/MM/yyy"
                                            />
                                        </div>
                                    </div>
                                    <div className="col-md-6">
                                        <div className="form-group">
                                            <label>Date fin</label>
                                            <DatePicker class="form-control ddate" style={{ border: 'none' }}
                                                selected={this.state.dateFin}
                                                onChange={this.addDateFin}
                                                dateFormat="dd/MM/yyy"
                                            />
                                        </div>
                                    </div>
                                </div>
                                {this.state.dateDebut != '' && this.state.dateDebut != ''}
                                <div className="row">
                                    <table className="table table-bordered">
                                        <thead >
                                            <tr>
                                                <th style={{ textAlign: 'center' }} scope="col">Jour</th>
                                            </tr>
                                        </thead>
                                        <tbody>
                                            <td>

                                            </td>
                                        </tbody>
                                    </table>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        );
    }
}

export default withRouter(Planning);
