import React, { Component } from 'react'
import axios from 'axios'
import jQuery from 'jquery'
import { Link, BrowserRouter as Router, Route, Switch, withRouter, HashRouter, Redirect } from 'react-router-dom'
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";
import Select from 'react-select';
import { RadioGroup, Radio } from 'react-radio-group'

class ModifApprenant extends Component {
    constructor(props) {
        super(props);
        this.state = {
            apprenantId: '',
            numIns: '',
            prenom: '',
            nom: '',
            selectedType: 'CIN',
            numSelonType: '',
            nationalite: '',
            dateDelivSelonType: '',
            villeDelivreSelonType: '',
            dateNaissance: '',
            lieuNaissance: '',
            email: '',
            selectedGender: 'Masculin',
            mobile: '',
            tel: '',
            adresseOfficiel: '',
            adresseTun: '',
            imgDiplScol: '',
            diplScol: null,
            imgCin: '',
            pathCin: null,
            imgVerso: '',
            imageVerso: null,
            imgExtNaiss: '',
            extNaiss: null,
            imgReglInt: '',
            reglInt: null,
            nomNiveau:''
        }
    }

    componentDidMount() {
        if (JSON.parse((localStorage.getItem('userPermissions'))) != null) {
            let idApprenant = this.props.location.state.idApprenant;
            axios.get(process.env.REACT_APP_API_URL + 'Back_mediatraining/GetApprenantById.php?id=' + idApprenant).then(res => {
                console.log(res)
                if (res.data.dateNum != "0000-00-00") {
                    var dateType = new Date(res.data.dateNum)
                }
                else {
                    var dateType = ''
                }
                this.setState({
                    apprenantId: res.data.idEleve,
                    numIns: res.data.numIns,
                    prenom: res.data.prenom,
                    nom: res.data.nom,
                    selectedType: res.data.type,
                    numSelonType: res.data.numero,
                    nationalite: res.data.nationalite,
                    dateDelivSelonType: dateType,
                    villeDelivreSelonType: res.data.villeNum,
                    dateNaissance: new Date(res.data.naissance),
                    lieuNaissance: res.data.villeNaissance,
                    adresseOfficiel: res.data.adrOfficiel,
                    adresseTun: res.data.adresseTun,
                    email: res.data.email,
                    selectedGender: res.data.sex,
                    mobile: res.data.mobile,
                    tel: res.data.tel,
                    imgDiplScol: res.data.diplScol,
                    imgCin: res.data.cinPath,
                    imgVerso: res.data.photo,
                    imgExtNaiss: res.data.extNaiss,
                    imgReglInt: res.data.reglInt,
                    nomNiveau:res.data.nomNiveau
                })
            })
        }
    }

    updNumIns = (event) => {
        this.setState({
            numIns: event.target.value
        });
    }

    updPrenom = (event) => {
        this.setState({
            prenom: event.target.value
        });
    }

    updNom = (event) => {
        this.setState({
            nom: event.target.value
        });
    }

    updType = (selectedType) => {
        this.setState({ selectedType: selectedType });
    }

    updNumSelonType = (event) => {
        this.setState({
            numSelonType: event.target.value
        }, () => {
            this.setState({
                numIns: this.state.numSelonType
            });
        });
    }

    updNationalite = (event) => {
        this.setState({
            nationalite: event.target.value
        });
    }

    updDateLivraison = d => {
        this.setState({
            dateDelivSelonType: d
        });
    };

    updVilleDelivreSelonType = (event) => {
        this.setState({
            villeDelivreSelonType: event.target.value
        });
    }

    updDateNaissance = d => {
        this.setState({
            dateNaissance: d
        });
    };

    updLieuNaissance = (event) => {
        this.setState({
            lieuNaissance: event.target.value
        });
    }

    updAdresseOfficiel = (event) => {
        this.setState({
            adresseOfficiel: event.target.value
        });
    }

    updAdresseTun = (event) => {
        this.setState({
            adresseTun: event.target.value
        });
    }

    updEmail = (event) => {
        this.setState({
            email: event.target.value
        });
    }

    updGender = (selectedGender) => {
        this.setState({ selectedGender: selectedGender });
    }

    updMobile = (event) => {
        this.setState({
            mobile: event.target.value
        });
    }

    updTel = (event) => {
        this.setState({
            tel: event.target.value
        });
    }

    updDiplScol(e) {
        this.setState({ diplScol: e.target.files })
    }

    updCinPath(e) {
        this.setState({ pathCin: e.target.files })
    }

    updImageVerso(e) {
        this.setState({ imageVerso: e.target.files })
    }

    updExtraitNaiss(e) {
        this.setState({ extNaiss: e.target.files })
    }

    updReglInt(e) {
        this.setState({ reglInt: e.target.files })
    }

    modif = () => {
        let dateDelivSelonType = this.state.dateDelivSelonType
        let month_one = '' + (dateDelivSelonType.getMonth() + 1);
        let day_one = '' + dateDelivSelonType.getDate();
        let year_one = dateDelivSelonType.getFullYear();
        if (month_one.length < 2)
            month_one = '0' + month_one;
        if (day_one.length < 2)
            day_one = '0' + day_one;
        let convertDateDelivSelonType = [year_one, month_one, day_one].join('-');
        let dateNaissance = this.state.dateNaissance
        let month_two = '' + (dateNaissance.getMonth() + 1);
        let day_two = '' + dateNaissance.getDate();
        let year_two = dateNaissance.getFullYear();
        if (month_two.length < 2)
            month_two = '0' + month_two;
        if (day_two.length < 2)
            day_two = '0' + day_two;
        let convertDateNaissance = [year_two, month_two, day_two].join('-');
        // Test si diplome scolaire,cin,photos et extrait de naissance vide ! 
        var pathDiplScol = '';
        if (this.state.diplScol != null) {
            pathDiplScol = process.env.REACT_APP_API_URL + "images/Dossier_Apprenants/" + this.state.numIns + '_' + this.state.nomNiveau + '_' + this.state.diplScol[0].name
        }
        var pathCin = '';
        if (this.state.pathCin != null) {
            pathCin = process.env.REACT_APP_API_URL + "images/Dossier_Apprenants/" + this.state.numIns + '_' + this.state.nomNiveau + '_' + this.state.pathCin[0].name
        }
        var pathImageVerso = '';
        if (this.state.imageVerso != null) {
