import React, { Component } from 'react'
import axios from 'axios'
import jQuery from 'jquery'
import { withRouter } from 'react-router-dom'
import Select from 'react-select';
import { Button } from 'reactstrap';
import Switch from "react-switch";

class AjoutArticle extends Component {
    constructor(props) {
        super(props);
        this.state = {
            compose: '',
            nomArticle: '',
            selectedUnite: '',
            poids: '',
            prixUnitaire: '',
            prixKg: '',
            unites: [],
            familleArticles: [],
            selectedFamilleArticles: '',
            tousSousArticles: [{ sousArticle: '' }],
            sousArticleAjouter: '',
            tousCombine: [{ article: '', quantite: '', uniteCombine: '', prix: '' }],
            articleAjouter: '',
            quantiteAjouter: '',
            uniteCombineAjouter: '',
            prixAjouter: '',
            checked: false,
            articles: []
        }
        this.addNom = this.addNom.bind(this);
        this.addPoids = this.addPoids.bind(this);
        this.addPrixUnitaire = this.addPrixUnitaire.bind(this);
        this.addPrixKg = this.addPrixKg.bind(this);
        this.additionSousArticle = this.additionSousArticle.bind(this);
        this.additionCombine = this.additionCombine.bind(this);
        this.handleChange = this.handleChange.bind(this);
        this.ajout = this.ajout.bind(this)
    }

    componentDidMount() {
        axios.get(process.env.REACT_APP_API_URL + 'Back_agora/FamilleArticlesSelect.php').then(res => {
            console.log(res)
            this.setState({
                familleArticles: res.data
            })
        })
        axios.get(process.env.REACT_APP_API_URL + 'Back_agora/UnitesSelect.php').then(res => {
            console.log(res)
            this.setState({
                unites: res.data
            })
        })
        axios.get(process.env.REACT_APP_API_URL + 'Back_agora/ArticlesSelect.php').then(res => {
            console.log(res)
            this.setState({
                articles: res.data
            })
        })
    }

    handleChange(checked) {
        this.setState({ checked });
        console.log(this.state.checked)
    }

    addNom(event) {
        this.setState({
            nomArticle: event.target.value
        });
    }

    addUnite = selectedUnite => {
        this.setState({ selectedUnite });
        console.log(`Unite selected:`, selectedUnite);
    };

    addPoids(event) {
        this.setState({
            poids: event.target.value
        });
    }

    addPrixUnitaire(event) {
        this.setState({
            prixUnitaire: event.target.value
        });
    }

    addPrixKg(event) {
        this.setState({
            prixKg: event.target.value
        });
    }

    addSousArticle = (event, idSousArticle) => {
        let nomSousArticle = event.target.value;
        this.setState({
            tousSousArticles: this.state.tousSousArticles.map((el, id) => (id === idSousArticle ? Object.assign({}, { sousArticle: nomSousArticle }) : el))
        });
    }

    addFamilleArticles = selectedFamilleArticles => {
        this.setState({ selectedFamilleArticles });
        console.log(`Famille article selected:`, selectedFamilleArticles);
    };

    additionSousArticle() {
        let element = { sousArticle: this.state.sousArticleAjouter }
        this.setState({
            tousSousArticles: [...this.state.tousSousArticles, element]
        })
    }

    additionCombine() {
        let element = { article: this.state.articleAjouter, quantite: this.state.quantiteAjouter, uniteCombine: this.state.uniteCombineAjouter, prix: this.state.prixAjouter }
        this.setState({
            tousCombine: [...this.state.tousCombine, element]
        })
    }

    addArticle = (selectedArticle, idArticle) => {
        let article = selectedArticle;
        this.setState({
            tousCombine: this.state.tousCombine.map((el, id) => (id === idArticle ? Object.assign(el, { article: article }) : el))
        });
    }

    addQuantite = (event, idQuantite) => {
        let quantite = event.target.value;
        this.setState({
            tousCombine: this.state.tousCombine.map((el, id) => (id === idQuantite ? Object.assign(el, { quantite: quantite }) : el))
        });
    }

    addUniteCombine = (selectedUniteCombine, idUniteCombine) => {
        let uniteCombine = selectedUniteCombine;
        this.setState({
            tousCombine: this.state.tousCombine.map((el, id) => (id === idUniteCombine ? Object.assign(el, { uniteCombine: uniteCombine }) : el))
        });
    };

    addPrix = (event, idPrix) => {
        let prix = event.target.value;
        this.setState({
            tousCombine: this.state.tousCombine.map((el, id) => (id === idPrix ? Object.assign(el, { prix: prix }) : el))
        });
    }

    ajout() {
        if (this.state.checked === true) {
            jQuery.ajax({
                url: process.env.REACT_APP_API_URL + 'Back_agora/AddArticle.php',
                type: 'POST',
                data: {
                    nomArticle: this.state.nomArticle,
                    poids: this.state.poids,
                    prixUnitaire: this.state.prixUnitaire,
                    prixKg: this.state.prixKg,
                    arraySousArticles: this.state.tousSousArticles,
                    idFamilleArticles: this.state.selectedFamilleArticles.value,
                    idUnite: this.state.selectedUnite.value,
                    arrayComposes: this.state.tousCombine,
                    compose: 1
                },
                dataType: 'json',
                success: (code_html, statut) => {
                    if (code_html.Result == "OK") {
                        this.props.history.push('/Articles');
                    }
                    else if (code_html.Result == "KO") {
                        alert("erreur,l'un de vos champs est vide!!");
                    }
                }
            });
        }
        else if (this.state.checked === false) {
            jQuery.ajax({
                url: process.env.REACT_APP_API_URL + 'Back_agora/AddArticle.php',
                type: 'POST',
                data: {
                    nomArticle: this.state.nomArticle,
                    poids: this.state.poids,
                    prixUnitaire: this.state.prixUnitaire,
                    prixKg: this.state.prixKg,
                    arraySousArticles: this.state.tousSousArticles,
                    idFamilleArticles: this.state.selectedFamilleArticles.value,
                    idUnite: this.state.selectedUnite.value,
                    arrayComposes: '',
                    compose: 0
                },
                dataType: 'json',
                success: (code_html, statut) => {
                    if (code_html.Result == "OK") {
                        this.props.history.push('/Articles');
                    }
                    else if (code_html.Result == "KO") {
                        alert("erreur,l'un de vos champs est vide!!");
                    }
                }
            });
        }
    }

    render() {
        return (
            <div id="page-wrapper" style={{ minHeight: '263px' }}>
                <div className="container-fluid">
                    <div className="row bg-title">
                        <div className="col-lg-12">
                            <h4 className="page-title">Ajout article</h4>
                        </div>
                        {/* /.col-lg-12 */}
                    </div>
                    {/* /.row */}
                    <div class="row">
                        <div class="col-sm-12">
                            <div class="white-box">
                                <div class="row">
                                    <label>
                                        <span style={{ marginLeft: '15px',marginRight: '10px' }}>Combine</span>
                                        <Switch offColor='#FF0000' onChange={this.handleChange} checked={this.state.checked} />
                                    </label>
                                </div>
                                <div class="row">
                                    <table style={{ width: '85%', marginTop: '10px' }} class="table table-bordered">
                                        <thead>
                                            <tr>
                                                <th style={{ textAlign: 'center' }} scope="col">Nom</th>
                                                <th style={{ textAlign: 'center' }} scope="col">Unit√©</th>
                                                <th style={{ textAlign: 'center' }} scope="col">Poids</th>
                                                <th style={{ textAlign: 'center' }} scope="col">Prix unitaire piece</th>
                                                <th style={{ textAlign: 'center' }} scope="col">Prix unitaire/kg</th>
                                                <th style={{ textAlign: 'center' }} scope="col">Famille</th>
                                            </tr>
                                        </thead>
                                        <tbody>
                                            <tr>
                                                <td><input type="text" placeholder="Nom" onChange={this.addNom.bind(this)} style={{ textAlign: 'center', height: '38px', borderRadius: '5px' }} /></td>
                                                <td style={{ width: '20%' }}><Select
                                                    isClearable={true}
                                                    isSearchable={true}
                                                    onChange={this.addUnite}
                                                    options={this.state.unites}
                                                />  </td>
                                                <td><input type="number" placeholder="Poids" onChange={this.addPoids.bind(this)} style={{ textAlign: 'center', height: '38px', borderRadius: '5px' }} /></td>
                                                <td><input type="number" placeholder="Prix unitaire" onChange={this.addPrixUnitaire.bind(this)} style={{ textAlign: 'center', height: '38px', borderRadius: '5px' }} /></td>
                                                <td><input type="number" placeholder="Prix par kg" onChange={this.addPrixKg.bind(this)} style={{ textAlign: 'center', height: '38px', borderRadius: '5px' }} /></td>
                                                <td style={{ width: '20%' }}><Select
                                                    isClearable={true}
                                                    isSearchable={true}
                                                    onChange={this.addFamilleArticles}
                                                    options={this.state.familleArticles}
                                                />  </td>
                                            </tr>
                                        </tbody>
                                    </table>
                                </div>
                                <div class="row">
                                    <div style={{ display: 'flex', justifyContent: 'flex-start', marginLeft: '15px' }}>
                                        <Button style={{ borderRadius: '5px' }} onClick={this.additionSousArticle.bind(this)} color="secondary">Ajouter ligne</Button>
                                    </div>
                                    <table style={{ width: '10%', marginTop: '10px' }} class="table table-bordered">
                                        <thead>
                                            <tr>
                                                <th style={{ textAlign: 'center' }} scope="col">Sous article</th>
                                            </tr>
                                        </thead>
                                        {this.state.tousSousArticles.map((el, id) =>
                                            <tbody>
                                                <tr>
                                                    <td><input type="text" placeholder="Sous article" onChange={(e) => this.addSousArticle(e, id)} style={{ textAlign: 'center', height: '38px', borderRadius: '5px' }} /></td>
                                                </tr>
                                            </tbody>
                                        )}
                                    </table>
                                </div>
                                {this.state.checked === true ?
                                    (<div class="row">
                                        <div style={{ display: 'flex', justifyContent: 'flex-start', marginLeft: '15px' }}>
                                            <Button style={{ borderRadius: '5px' }} onClick={this.additionCombine.bind(this)} color="secondary">Ajouter ligne</Button>
                                        </div>
                                        <table style={{ width: '60%', marginTop: '10px' }} class="table table-bordered">
                                            <thead>
                                                <tr>
                                                    <th style={{ textAlign: 'center' }} scope="col">Article</th>
                                                    <th style={{ textAlign: 'center' }} scope="col">Quantit√©</th>
                                                    <th style={{ textAlign: 'center' }} scope="col">Unit√©</th>
                                                    <th style={{ textAlign: 'center' }} scope="col">Prix</th>
                                                </tr>
                                            </thead>
                                            {this.state.tousCombine.map((el, id) =>
                                                <tbody>
                                                    <tr>
                                                        <td><Select
                                                            isClearable={true}
                                                            isSearchable={true}
                                                            onChange={(e) => this.addArticle(e, id)}
                                                            options={this.state.articles}
                                                        />  </td>
                                                        <td style={{ width: '10%' }}><input type="number" placeholder='Quantit√©' onChange={(e) => this.addQuantite(e, id)} style={{ textAlign: 'center', height: '38px', borderRadius: '5px' }} /></td>
                                                        <td><Select
                                                            isClearable={true}
                                                            isSearchable={true}
                                                            onChange={(e) => this.addUniteCombine(e, id)}
                                                            options={this.state.unites}
                                                        />  </td>
                                                        <td style={{ width: '10%' }}><input type="text" placeholder='Prix' onChange={(e) => this.addPrix(e, id)} style={{ textAlign: 'center', height: '38px', borderRadius: '5px' }} /></td>
                                                    </tr>
                                                </tbody>
                                            )}
                                        </table>
                                    </div>) : (null)}
                                <div style={{ display: 'flex', justifyContent: 'center', marginBottom: '10px' }}>
                                    <button style={{ width: '15%', borderRadius: '5px' }} type="button" className="btn btn-success" onClick={this.ajout.bind(this)}>Confirmer</button>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        );
    }
}

export default withRouter(AjoutArticle);
