import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import { Fragment } from 'react';

class MenuDonneeBase extends Component {
    constructor(props) {
        super(props);
        this.state = {
            barreNavigation: [{ titre: 'Données de base', element: [{nomElement: "Visiteurs", iconElement: 'fas fa-walking', linkElement: '/Visiteurs', sousPermission: 0, arraySousElement: [] },{ nomElement: "Apprenants", iconElement: 'fas fa-child', linkElement: '/Apprenants', sousPermission: 0, arraySousElement: [] }, { nomElement: "Enseignants", iconElement: 'fas fa-chalkboard-teacher', linkElement: '/Enseignants', sousPermission: 0, arraySousElement: [] }, { nomElement: "Scolarite", iconElement: '', linkElement: '', sousPermission: 0, arraySousElement: [{ nomSousElement: 'Filiéres', linkSousElement: '/Filieres', sousSousPermission: 0, iconSousElement: 'fas fa-bookmark', arraySousSousElement: [] }, { nomSousElement: 'Specialités', linkSousElement: '/Specialites', sousSousPermission: 0, iconSousElement: 'fas fa-chalkboard', arraySousSousElement: [] }, { nomSousElement: 'Formations', linkSousElement: '/Formations', sousSousPermission: 0, iconSousElement: 'fas fa-scroll', arraySousSousElement: [] }, { nomSousElement: 'Niveaux', linkSousElement: '/Niveaux', sousSousPermission: 0, iconSousElement: 'fas fa-list-ol', arraySousSousElement: [] }, { nomSousElement: 'Groupes', linkSousElement: '/Groupes', sousSousPermission: 0, iconSousElement: 'fas fa-user-friends', arraySousSousElement: [] }] }, { nomElement: "Formation accéléré", iconElement: 'fas fa-book-reader', linkElement: '/Cycles', sousPermission: 0, arraySousElement: [] },], iconTitre: 'fas fa-address-card', permission: 0 }]
        }
    }

    componentDidMount() {
        var userPermissions = JSON.parse((localStorage.getItem('userPermissions')));
        if (userPermissions != null) {
        }
        else {
            document.location = '/'
        }
    }

    render() {
        return (
            <div id="page-wrapper" style={{ minHeight: '263px', marginLeft: '0px' }}>
                <div className="container-fluid">
                    <div className="row bg-title">
                        <div className="col-lg-12">
                            <h4 className="page-title">Menu</h4>
                        </div>
                        {/* /.col-lg-12 */}
                    </div>
                    {/* /.row */}
                    <div className="row">
                        <div className="col-sm-12">
                            <div className="white-box" >
                                <div className="row">
                                    {this.state.barreNavigation.map((el, index) =>
                                        <div className="col-md-4">
                                            <div className="white-box" style={{ border: 'solid 1px', borderRadius: '5px' }}>
                                                <h3 style={{ border: 'solid 1px', backgroundColor: 'black', color: 'white' }}><i style={{ paddingRight: '5px', paddingLeft: '5px' }} className={el.iconTitre}></i>{el.titre}</h3>
                                                <div className="myadmin-dd dd" id="nestable">
                                                    <ol className="dd-list">
                                                        {el.element.map((ele, indice) =>
                                                            <div>
                                                                {
                                                                    (ele.arraySousElement.length > 0) ?
                                                                        // Noeud Parent Ayant des Fils Feuilles
                                                                        (<Fragment>
                                                                            <li className="dd-item" data-id={index}>
                                                                                <div style={{ border: '1px solid rgba(120,130,140,.13)', padding: '8px 16px', height: 'auto', fontWeight: '600', color: '#2b2b2b' }}><i style={{ paddingRight: '5px' }} className={ele.iconElement}></i>{ele.nomElement}</div>
                                                                                <ol className="dd-list">
                                                                                    {ele.arraySousElement.map(elm =>
                                                                                        <li className="dd-item" data-id={index + 1}>
                                                                                            <Link style={{ color: '#2b2b2b' }} to={elm.linkSousElement}><div style={{ border: '1px solid rgba(120,130,140,.13)', padding: '8px 16px', height: 'auto', fontWeight: '600' }}><i style={{ paddingRight: '5px' }} className={elm.iconSousElement}></i>{elm.nomSousElement}</div></Link>
                                                                                        </li>)}
                                                                                </ol>
                                                                            </li> </Fragment>) :
                                                                        // Noeud Feuille (Terminaison)
                                                                        (<Link style={{ color: '#2b2b2b' }} to={ele.linkElement}><div style={{ border: '1px solid rgba(120,130,140,.13)', padding: '8px 16px', height: 'auto', fontWeight: '600' }}><i style={{ paddingRight: '5px' }} className={ele.iconElement}></i>{ele.nomElement}</div></Link>)
                                                                }
                                                            </div>
                                                        )}
                                                    </ol>
                                                </div>
                                            </div>
                                        </div>)}
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        );
    }
}

export default MenuDonneeBase;
