import React, { Component, Fragment } from 'react'
import { Link, BrowserRouter as Router, Route, Switch, withRouter, HashRouter, Redirect } from 'react-router-dom'
import DataTable from 'react-data-table-component';
import axios from 'axios'
import jQuery from 'jquery'
import './paginate.css'
import { Button, Modal, ModalHeader, ModalBody, ModalFooter } from 'reactstrap';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import { Slide, Zoom, Flip, Bounce } from 'react-toastify';
import Select from 'react-select';

class Apprenants extends Component {
    constructor(props) {
        super(props);
        this.state = {
            data: [],
            columns: [
                {
                    name: 'CIN',
                    selector: 'cin',
                    sortable: false,
                    center: true
                },
                {
                    name: 'PrÃ©nom-Nom',
                    selector: 'prenomNom',
                    sortable: false,
                    center: true
                },
                {
                    name: 'Mobile',
                    selector: 'mobile',
                    sortable: false,
                    center: true
                },
                {
                    name: 'Modifier',
                    cell: row => <Link to={{
                        pathname: '/ModifApprenant',
                        state: { idApprenant: row.idApprenant }
                    }}><button className='buttonModifier'><i style={{ color: 'white' }} class="fas fa-pencil-alt"></i></button></Link>,
                    ignoreRowClick: true,
                    allowOverflow: true,
                    button: true,
                },
                {
                    name: 'Desactiver',
                    cell: row => <button className='buttonSupprimer' onClick={() => this.toggletwo(row.idApprenant)}><i style={{ color: 'white' }} class="fas fa-trash-alt"></i></button>,
                    ignoreRowClick: true,
                    allowOverflow: true,
                    button: true,
                },
            ],
            nombreTotalElement: 0,
            filterElement: '',
            mycurrentPage: 1,
            resetFirstPage: false,
            nbrEltperPage: 5,
            modal: false,
            idApprenant: '',
            pending: true,
            search: [{ nom: '', prenom: '', cin: '', villeResid: '', dateNaiss: '', nivBase: '', ageMin: '', ageMax: '' }],
            listNivBase: []
        }
        this.newElement = this.newElement.bind(this);
        this.filterSearch = this.filterSearch.bind(this);
    }

    componentDidMount() {
        if (JSON.parse((localStorage.getItem('userPermissions'))) != null) {
            axios.get(process.env.REACT_APP_API_URL + 'Back_mediatraining/SelectNivBase.php').then(res => {
                console.log(res)
                this.setState({
                    listNivBase: res.data
                })
            })
            jQuery.ajax({
                url: process.env.REACT_APP_API_URL + 'Back_mediatraining/Apprenants.php',
                type: 'POST',
                data: {
                    query: '',
                    limit: this.state.nbrEltperPage,
                    page: 1
                },
                dataType: 'json',
                success: (code_html, statut) => {
                    console.log(code_html)
                    this.setState({
                        nombreTotalElement: code_html.total,
                        data: code_html.data,
                        pending: false
                    });
                }
            });
        }
    }

    newElement(page, totalRows) {
        this.setState({
            pending: true
        },
            () => {
                jQuery.ajax({
                    url: process.env.REACT_APP_API_URL + 'Back_mediatraining/Apprenants.php',
                    type: 'POST',
                    data: {
                        query: this.state.filterElement,
                        limit: this.state.nbrEltperPage,
                        page: page
                    },
                    dataType: 'json',
                    success: (code_html, statut) => {
                        console.log(code_html)
                        this.setState({
                            nombreTotalElement: code_html.total,
                            data: code_html.data,
                            pending: false
                        });
                    }
                });
            }
        )
    }

    perPage(currentRowsPerPage, currentPage) {
        this.setState(
            {
                nbrEltperPage: currentRowsPerPage,
                pending: true
            }
            , () => {
                jQuery.ajax({
                    url: process.env.REACT_APP_API_URL + 'Back_mediatraining/Apprenants.php',
                    type: 'POST',
                    data: {
                        query: this.state.filterElement,
                        limit: currentRowsPerPage,
                        page: currentPage
                    },
                    dataType: 'json',
                    success: (code_html, statut) => {
                        console.log(code_html)
                        this.setState({
                            nombreTotalElement: code_html.total,
                            data: code_html.data,
                            pending: false
                        });
                    }
                });
            }
        );
    }

    filterSearch(event) {
        this.setState({
            filterElement: event.target.value,
            resetFirstPage: !this.state.resetFirstPage,
            pending: true
        },
            () => {
                jQuery.ajax({
                    url: process.env.REACT_APP_API_URL + 'Back_mediatraining/Apprenants.php',
                    type: 'POST',
                    data: {
                        query: this.state.filterElement,
                        limit: this.state.nbrEltperPage,
                        page: 1
                    },
                    dataType: 'json',
                    success: (code_html, statut) => {
                        console.log(code_html)
                        this.setState({
                            nombreTotalElement: code_html.total,
                            data: code_html.data,
                            pending: false
                        });
                    }
                })
            }
        );
    }

    toggletwo = (idApprenant) => {
        this.setState(prevState => ({
            modal: !prevState.modal,
            idApprenant: idApprenant
        }));
    }

    toggle = () => {
        this.setState(prevState => ({
            modal: !prevState.modal
        }));
    }

    addNom = (event, indexNom) => {
        let nom = event.target.value;
        this.setState({
            search: this.state.search.map((el, id) => (id === indexNom ? Object.assign(el, { nom: nom }) : el))
        });
    }

    addPrenom = (event, indexPrenom) => {
        let prenom = event.target.value;
        this.setState({
            search: this.state.search.map((el, id) => (id === indexPrenom ? Object.assign(el, { prenom: prenom }) : el))
        });
    }

    addCin = (event, indexCin) => {
        let cin = event.target.value;
        this.setState({
            search: this.state.search.map((el, id) => (id === indexCin ? Object.assign(el, { cin: cin }) : el))
        });
    }

    addVilleResid = (event, indexVilleResid) => {
        let villeResid = event.target.value;
        this.setState({
            search: this.state.search.map((el, id) => (id === indexVilleResid ? Object.assign(el, { villeResid: villeResid }) : el))
        });
    }

    addDateNaiss = (event, indexDateNaiss) => {
        let dateNaiss = event.target.value;
        this.setState({
            search: this.state.search.map((el, id) => (id === indexDateNaiss ? Object.assign(el, { dateNaiss: dateNaiss }) : el))
        });
    }

    addNivBase = (selectedNivBase, indexNivBase) => {
        let nivBase = selectedNivBase;
        this.setState({
            search: this.state.search.map((el, id) => (id === indexNivBase ? Object.assign(el, { nivBase: nivBase }) : el))
        });
    }

    addAgeMin = (event, indexAgeMin) => {
        let ageMin = event.target.value;
        this.setState({
            search: this.state.search.map((el, id) => (id === indexAgeMin ? Object.assign(el, { ageMin: ageMin }) : el))
        });
    }

    addAgeMax = (event, indexAgeMax) => {
        let ageMax = event.target.value;
        this.setState({
            search: this.state.search.map((el, id) => (id === indexAgeMax ? Object.assign(el, { ageMax: ageMax }) : el))
        });
    }

    deleteSearch = () => {
        this.state.search.splice(0, 1)
    }

    recherche = () => {
        console.log(this.state.search)
        this.setState({
            resetFirstPage: !this.state.resetFirstPage,
            pending: true
        },
            () => {
                jQuery.ajax({
                    url: process.env.REACT_APP_API_URL + 'Back_mediatraining/ApprenantSearch.php',
                    type: 'POST',
                    data: {
                        query: this.state.search,
                        limit: this.state.nbrEltperPage,
                        page: 1
                    },
                    dataType: 'json',
                    success: (code_html, statut) => {
                        console.log(code_html)
                        this.setState({
                            nombreTotalElement: code_html.total,
                            data: code_html.data,
                            pending: false
                        });
                    }
                })
            }
        );
    }

    afficherTout = () => {
        this.setState({
            search: this.state.search.map((el, id) => (id === 0 ? Object.assign(el, { prenom: "", nom: "", cin: "", villeResid: "", dateNaiss: "", nivBase: "", ageMin: "", ageMax: "" }) : el)),
        });
        jQuery.ajax({
            url: process.env.REACT_APP_API_URL + 'Back_mediatraining/Apprenants.php',
            type: 'POST',
            data: {
                query: '',
                limit: this.state.nbrEltperPage,
                page: 1
            },
            dataType: 'json',
            success: (code_html, statut) => {
                console.log(code_html)
                this.setState({
                    nombreTotalElement: code_html.total,
                    data: code_html.data,
                    pending: false
                });
            }
        });
    }

    deleteApprenant() {
        // var userPermissions = JSON.parse((localStorage.getItem('userPermissions')));
        // var userId = userPermissions[0].idUser
        jQuery.ajax({
            url: process.env.REACT_APP_API_URL + "Back_mediatraining/DeleteApprenant.php?id=" + this.state.idApprenant,
            type: 'POST',
            dataType: 'json',
            success: (code_html, statut) => {
                this.setState({
                    modal: false
                }, () => {
                    jQuery.ajax({
                        url: process.env.REACT_APP_API_URL + 'Back_mediatraining/Apprenants.php',
                        type: 'POST',
                        data: {
                            query: '',
                            limit: this.state.nbrEltperPage,
                            page: 1
                        },
                        dataType: 'json',
                        success: (code_html, statut) => {
                            console.log(code_html)
                            this.setState({
                                nombreTotalElement: code_html.total,
                                data: code_html.data
                            });
                        }
                    });
                })
            }
        });
    }

    render() {
        return (
            <div id="page-wrapper" style={{ minHeight: '263px' }}>
                <div className="container-fluid">
                    <div className="row bg-title">
                        <div className="col-lg-12">
                            <h4 className="page-title">Apprenants</h4>
                        </div>
                        {/* /.col-lg-12 */}
                    </div>
                    {/* /.row */}
                    <div class="row">
                        <div class="col-sm-12">
                            <div class="white-box">
                                <div style={{ display: 'flex', justifyContent: 'flex-end', alignItems: 'center', marginBottom: '10px', marginRight: '20px' }}>
                                    <Link to="/AjoutApprenant" > <button style={{ background: '#3A3F51', border: '1px solid #3A3F51', borderRadius: '5px' }} type="button" className="btn btn-info"> <i class="fas fa-plus"></i>Apprenant</button></Link>
                                </div>
                                <div class="row">
                                    <h4 style={{ padding: '10px' }}>CritÃ¨res de recherche</h4>
                                    {this.state.search.map((el, index) =>
                                        <Fragment>
                                            <div class="col-md-6">
                                                <div class="form-group">
                                                    <label>PrÃ©nom</label>
                                                    <input type="text" value={el.prenom} onChange={(e) => this.addPrenom(e, index)} style={{ textAlign: 'center', height: '38px', borderRadius: '5px', marginLeft: '50px', width: '350px' }} />
                                                </div>
                                            </div>
                                            <div class="col-md-6">
                                                <div class="form-group">
                                                    <label>Nom</label>
                                                    <input type="text" value={el.nom} onChange={(e) => this.addNom(e, index)} style={{ textAlign: 'center', height: '38px', borderRadius: '5px', marginLeft: '75px', width: '350px' }} />
                                                </div>
                                            </div>
                                            <div class="col-md-6">
                                                <div class="form-group">
                                                    <label>CIN</label>
                                                    <input type="text" value={el.cin} onChange={(e) => this.addCin(e, index)} style={{ textAlign: 'center', height: '38px', borderRadius: '5px', marginLeft: '75px', width: '350px' }} />
                                                </div>
                                            </div>
                                            <div class="col-md-6">
                                                <div class="form-group">
                                                    <label>RÃ©sidence</label>
                                                    <input type="text" value={el.villeResid} onChange={(e) => this.addVilleResid(e, index)} style={{ textAlign: 'center', height: '38px', borderRadius: '5px', marginLeft: '43px', width: '350px' }} />
                                                </div>
                                            </div>
                                            <div class="col-md-6">
                                                <div class="form-group">
                                                    <label>Naissance</label>
                                                    <input type="text" value={el.dateNaiss} onChange={(e) => this.addDateNaiss(e, index)} style={{ textAlign: 'center', height: '38px', borderRadius: '5px', marginLeft: '36px', width: '350px' }} />
                                                </div>
                                            </div>
                                            <div class="col-md-6">
                                                <div style={{ width: '30%' }} class="form-group">
                                                    <label>Niveau</label>
                                                    <Select
                                                        value={el.nivBase}
                                                        isClearable={true}
                                                        isSearchable={true}
                                                        onChange={(e) => this.addNivBase(e, index)}
                                                        options={this.state.listNivBase}
                                                    />
                                                </div>
                                            </div>
                                            <div class="col-md-6">
                                                <div class="form-group">
                                                    <label>Age min</label>
                                                    <input type="text" value={el.ageMin} onChange={(e) => this.addAgeMin(e, index)} style={{ textAlign: 'center', height: '38px', borderRadius: '5px', marginLeft: '46px', width: '350px' }} />
                                                </div>
                                            </div>
                                            <div class="col-md-6">
                                                <div class="form-group">
                                                    <label>Age max</label>
                                                    <input type="text" value={el.ageMax} onChange={(e) => this.addAgeMax(e, index)} style={{ textAlign: 'center', height: '38px', borderRadius: '5px', marginLeft: '53px', width: '350px' }} />
                                                </div>
                                            </div>
                                        </Fragment>
                                    )}
                                </div>
                                <div style={{ display: 'flex', justifyContent: 'space-around' }}>
                                    <div>
                                        <button style={{ borderRadius: '5px', backgroundColor: '#761C19', borderColor: '#761C19' }} type="button" className="btn btn-success" onClick={this.recherche}>Rechercher</button>
                                    </div>
                                    <div>
                                        <button style={{ borderRadius: '5px', backgroundColor: '#761C19', borderColor: '#761C19' }} type="button" className="btn btn-success" onClick={this.afficherTout}>Afficher tout</button>
                                    </div>
                                </div>
                                <div className="mb-0 form-group">
                                    <div className="input-group-alternative input-group">
                                        <div className="input-group-prepend">
                                            <span className="input-group-text">
                                                <i className="fas fa-search"></i>
                                            </span>
                                            <input onChange={(currentRowsPerPage, currentPage) => this.filterSearch(currentRowsPerPage, currentPage)} placeholder="Search" type="text" className="search" />
                                        </div>
                                    </div>
                                </div>
                                <DataTable
                                    columns={this.state.columns}
                                    data={this.state.data}
                                    highlightOnHover={true}
                                    pagination={true}
                                    paginationServer={true}
                                    onChangePage={(page, totalRows) => this.newElement(page, totalRows)}
                                    paginationTotalRows={this.state.nombreTotalElement}
                                    paginationPerPage={this.state.nbrEltperPage}
                                    paginationRowsPerPageOptions={[5, 10, 15, 20, 25]}
                                    onChangeRowsPerPage={(currentRowsPerPage, currentPage) => this.perPage(currentRowsPerPage, currentPage)}
                                    paginationResetDefaultPage={this.state.resetFirstPage}
                                    paginationComponentOptions={{ rowsPerPageText: 'Elements par page:', rangeSeparatorText: 'de', noRowsPerPage: false }}
                                    progressPending={this.state.pending}
                                />
                            </div>
                        </div>
                    </div>
                </div>
                <Modal isOpen={this.state.modal} toggle={this.toggle} className={this.props.className} fade={false}>
                    <ModalHeader toggle={this.toggle}> <div style={{ display: 'flex', justifyContent: 'center', width: '465px' }}><img width='30%' src="./images/deleteModal.png" alt=".." /></div></ModalHeader>
                    <ModalBody>
                        <div style={{ fontFamily: 'Montserrat, sans-serif', FontSize: '14px', FontWeight: '700', LineHeight: '18.375px' }}>
                            <div style={{ display: 'flex', justifyContent: 'center' }}>
                                <p>Ãtes-Vous sÃ»r de vouloir supprimer cet enseignant ?</p>
                            </div>
                            <div className='hvr-push' style={{ display: 'flex', justifyContent: 'space-around', marginBottom: '15px' }}>
                                <div><button onClick={() => this.deleteApprenant()} style={{ backgroundColor: '#00FF7F', borderRadius: '5px', color: 'white' }}><i style={{ color: 'white' }} class="far fa-thumbs-up"></i>Oui</button></div>
                                <div><button onClick={() => this.toggle()} style={{ backgroundColor: '#D9534F', borderRadius: '5px', color: 'white' }}><i style={{ color: 'white' }} class="far fa-thumbs-down"></i>Non</button></div>
                            </div>
                        </div>
                    </ModalBody>
                </Modal>
                <ToastContainer transition={Flip} enableMultiContainer containerId={'A'} position={toast.POSITION.TOP_RIGHT} autoClose={2500} />
            </div>
        );
    }
}

export default Apprenants;
