import React, { Component } from 'react'
import axios from 'axios'
import jQuery from 'jquery'
import { withRouter } from 'react-router-dom'
import Select from 'react-select';
import { Button, Modal, ModalHeader, ModalBody, ModalFooter } from 'reactstrap';

class AjoutFormationNiv extends Component {
    constructor(props) {
        super(props);
        this.state = {
            Formations: [{ nomFormation: '', nbrAnnee: '', nivBase: '' }],
            indexLignFormationASupprimer: '',
            modal: false,
            ListFilieres: [],
            ListSpecialites: [],
            ListNivBase: [],
            selectedFiliere: '',
            selectedSpecialite: ''
        }
    }

    componentDidMount() {
        axios.get(process.env.REACT_APP_API_URL + 'Back_mediatraining/SelectFilieres.php').then(res => {
            console.log(res)
            this.setState({
                ListFilieres: res.data
            })
        })
        axios.get(process.env.REACT_APP_API_URL + 'Back_mediatraining/SelectSpecialites.php').then(res => {
            console.log(res)
            this.setState({
                ListSpecialites: res.data
            })
        })
        axios.get(process.env.REACT_APP_API_URL + 'Back_mediatraining/SelectNivBase.php').then(res => {
            console.log(res)
            this.setState({
                ListNivBase: res.data
            })
        })
    }

    additionLignFormation = () => {
        let element = { nomFormation: '', nbrAnnee: '', nivBase: '' }
        this.setState({
            formations: [...this.state.formations, element]
        })
    }

    addFiliere = selectedFiliere => {
        this.setState({ selectedFiliere });
        console.log(`Filiere selected:`, selectedFiliere);
    };

    addSpecialite = selectedSpecialite => {
        this.setState({ selectedSpecialite });
        console.log(`Specialite selected:`, selectedSpecialite);
    };

    addNomFormation = (event, indexNomFormation) => {
        let nomFormation = event.target.value;
        this.setState({
            formations: this.state.formations.map((el, id) => (id === indexNomFormation ? Object.assign(el, { nomFormation: nomFormation }) : el))
        });
    }

    addNbrAnnee = (event, indexNbrAnnee) => {
        let nbrAnnee = event.target.value;
        this.setState({
            formations: this.state.formations.map((el, id) => (id === indexNbrAnnee ? Object.assign(el, { nbrAnnee: nbrAnnee }) : el))
        });
    }

    addNivBase = (selectedNivBase, indexNivBase) => {
        let nivBase = selectedNivBase;
        this.setState({
            formations: this.state.formations.map((el, id) => (id === indexNivBase ? Object.assign(el, { nivBase: nivBase }) : el))
        });
    }

    toggleLignFormation = (indexLignFormation) => {
        this.setState(prevState => ({
            modal: !prevState.modal,
            indexLignFormationASupprimer: indexLignFormation
        }));
    }

    deleteLignFormation = () => {
        this.state.formations.splice(this.state.indexLignFormationASupprimer, 1)
        this.setState(prevState => ({
            modal: !prevState.modal
        }));
    }

    nonDeleteLignFormation = () => {
        this.setState(prevState => ({
            modal: !prevState.modal
        }));
    }

    ajout = () => {
        let year = new Date().getFullYear();
        console.log(this.state.Formations)
        jQuery.ajax({
            url: process.env.REACT_APP_API_URL + 'Back_mediatraining/AddFormationNiv.php',
            type: 'POST',
            data: {
                formations: this.state.formations,
                year: year
            },
            dataType: 'json',
            success: (code_html, statut) => {
                if (code_html.Result == "OK") {
                    this.props.history.push('/FormationNiv');
                }
                else if (code_html.Result == "KO") {
                    alert("erreur,l'un de vos champs est vide!!");
                }
            }
        });
    }

    render() {
        return (
            <div id="page-wrapper" style={{ minHeight: '263px' }}>
                <div className="container-fluid">
                    <div className="row bg-title">
                        <div className="col-lg-12">
                            <h4 className="page-title">Ajout formation</h4>
                        </div>
                        {/* /.col-lg-12 */}
                    </div>
                    {/* /.row */}
                    <div class="row">
                        <div class="col-sm-12">
                            <div class="white-box">
                                <div class="row">
                                    <div class="col-md-6">
                                        <div class="form-group">
                                            <label>Filiére</label>
                                            <Select
                                                isClearable={true}
                                                isSearchable={true}
                                                onChange={this.addFiliere}
                                                options={this.state.ListFilieres}
                                            />
                                        </div>
                                    </div>
                                    <div class="col-md-6">
                                        <div class="form-group">
                                            <label>Spécialité</label>
                                            <Select
                                                isClearable={true}
                                                isSearchable={true}
                                                onChange={this.addSpecialite}
                                                options={this.state.ListSpecialites}
                                            />
                                        </div>
                                    </div>
                                </div>
                                <div class="row">
                                    <table style={{ width: '100%', marginTop: '10px' }} class="table table-bordered">
                                        <thead>
                                            <tr>
                                                <th style={{ textAlign: 'center' ,width:'25%'}} scope="col">Nom</th>
                                                <th style={{ textAlign: 'center',width:'25%' }} scope="col">Nbr année</th>
                                                <th style={{ textAlign: 'center' }} scope="col">Niveau</th>
                                                <th style={{ textAlign: 'center',width:'25%' }} scope="col">Suppression</th>
                                            </tr>
                                        </thead>
                                        {this.state.Formations.map((el, index) =>
                                            <tbody>
                                                <tr>
                                                    <td style={{ textAlign: 'center' }}><input type="text" placeholder='Nom' value={el.nomFormation} onChange={(e) => this.addNomFormation(e, index)} style={{ textAlign: 'center', height: '38px', borderRadius: '5px' }} /></td>
                                                    <td style={{ textAlign: 'center' }}><input type="text" placeholder='Nbr année' value={el.nbrAnnee} onChange={(e) => this.addNbrAnnee(e, index)} style={{ textAlign: 'center', height: '38px', borderRadius: '5px' }} /></td>
                                                    <td><Select
                                                        value={el.nivBase}
                                                        isClearable={true}
                                                        isSearchable={true}
                                                        onChange={(e) => this.addNivBase(e, index)}
                                                        options={this.state.ListNivBase}
                                                    />  </td>
                                                    <td style={{ textAlign: 'center' }}><button style={{ width: '15%' }} className='buttonSupprimer' onClick={() => this.toggleLignFormation(index)}><i style={{ color: 'white' }} class="fas fa-trash-alt"></i></button></td>
                                                </tr>
                                            </tbody>
                                        )}
                                    </table>
                                    <div style={{ display: 'flex', justifyContent: 'flex-start', marginLeft: '15px' }}>
                                        <Button style={{ borderRadius: '5px' }} onClick={this.additionLignFormation} color="secondary"><i class="fas fa-plus-circle"></i>Ajouter ligne</Button>
                                    </div>
                                </div>
                                <Modal isOpen={this.state.modal} toggle={this.nonDeleteLignFormation} className={this.props.className} fade={false}>
                                    <ModalHeader toggle={this.nonDeleteLignFormation}> <div style={{ display: 'flex', justifyContent: 'center', width: '465px' }}><img width='30%' src="./images/deleteModal.png" alt=".." /></div></ModalHeader>
                                    <ModalBody>
                                        <div style={{ fontFamily: 'Montserrat, sans-serif', FontSize: '14px', FontWeight: '700', LineHeight: '18.375px' }}>
                                            <div style={{ display: 'flex', justifyContent: 'center' }}>
                                                <p>Êtes-Vous sûr de vouloir supprimer cette formation ?</p>
                                            </div>
                                            <div className='hvr-push' style={{ display: 'flex', justifyContent: 'space-around', marginBottom: '15px' }}>
                                                <div><button onClick={() => this.deleteLignFormation()} style={{ backgroundColor: '#00FF7F', borderRadius: '5px', color: 'white' }}><i style={{ color: 'white' }} class="far fa-thumbs-up"></i>Oui</button></div>
                                                <div><button onClick={() => this.nonDeleteLignFormation()} style={{ backgroundColor: '#D9534F', borderRadius: '5px', color: 'white' }}><i style={{ color: 'white' }} class="far fa-thumbs-down"></i>Non</button></div>
                                            </div>
                                        </div>
                                    </ModalBody>
                                </Modal>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        );
    }
}

export default withRouter(AjoutFormationNiv);
