import React, { Component } from 'react'
import axios from 'axios'
import jQuery from 'jquery'
import { withRouter } from 'react-router-dom'
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";
import Select from 'react-select';
import Checkbox from 'rc-checkbox';
import 'rc-checkbox/assets/index.css';
import { Fragment } from 'react';
import { Button, Modal, ModalHeader, ModalBody, ModalFooter } from 'reactstrap';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import { Slide, Zoom, Flip, Bounce } from 'react-toastify';
import { RadioGroup, Radio } from 'react-radio-group'

class ConfirmationCycle extends Component {
    constructor(props) {
        super(props);
        this.state = {
            nomCycle: '',
            nbrHeure: '',
            tarifCycle: '',
            tarifAvecCarte: '',
            type: ["Individuelle"],
            selectedType: '',
            anneScol: '',
            mois: [{ label: 'Janvier', value: '01' }, { label: 'Février', value: '02' }, { label: 'Mars', value: '03' }, { label: 'Avril', value: '04' }, { label: 'Mai', value: '05' }, { label: 'Juin', value: '06' }, { label: 'Juillet', value: '07' }, { label: 'Aout', value: '08' }, { label: 'Septembre', value: '09' }, { label: 'Octobre', value: '10' }, { label: 'November', value: '11' }, { label: 'Decembre', value: '12' }],
            enseignants: [],
            arrayCycle: [],
            indexLignCycleASupprimer: '',
            modal: false,
            indexLignEnseignASupprimer: '',
            indexSousLignEnseignASupprimer: '',
            modalEnseign: false,
            listChoix: ['Oui', 'Non']
        }
    }

    componentDidMount() {
        if (JSON.parse((localStorage.getItem('userPermissions'))) != null) {
            var userPermissions = JSON.parse((localStorage.getItem('userPermissions')));
            var anneeScolaire = userPermissions[0].anneeScolaire
            let idCycle = this.props.location.state.idCycle;
            axios.get(process.env.REACT_APP_API_URL + 'Back_mediatraining/GetCycleById.php?id=' + idCycle).then(res => {
                console.log(res)
                this.setState({
                    nomCycle: '',
                    nbrHeure: '',
                    tarifCycle: '',
                    tarifAvecCarte: '',
                    arrayCycle: []
                })
            })
            jQuery.ajax({
                url: process.env.REACT_APP_API_URL + 'Back_mediatraining/GetAnneeScolaire.php',
                type: 'POST',
                data: {
                    anneeScolaire: anneeScolaire
                },
                dataType: 'json',
                success: (code_html, statut) => {
                    console.log(code_html)
                    this.setState({
                        anneScol: code_html.scolaire
                    });
                }
            });
            axios.get(process.env.REACT_APP_API_URL + 'Back_mediatraining/SelectCours.php').then(res => {
                console.log(res)
                this.setState({
                    cours: res.data
                })
            })
            axios.get(process.env.REACT_APP_API_URL + 'Back_mediatraining/SelectEnseignants.php').then(res => {
                console.log(res)
                this.setState({
                    enseignants: res.data
                })
            })
        }
    }

    addChoix = (e, index) => {
        let selectedChoix = e
        this.setState({
            arrayCycle: this.state.arrayCycle.map((el, id) => (id === index ? Object.assign(el, { selectedChoix: selectedChoix }) : el))
        });
    }

    addEnseignent = (selectedEnseignent, index) => {

    }

    addNbrHeureParEnseign = (e, index) => {

    }

    addHonoraireParEnseign = (e, index) => {

    }

    additionLign = () => {
        let element = { selectedMois: '', dateD: '', dateF: '', selectedChoix: '', selectedCour: '', infoEnseignant: [{ selectedEnseignent: '' }, { nbrHeureParEnseign: '' }, { honoraireParEnseign: '' }] }
        this.setState({
            arrayCycle: [...this.state.arrayCycle, element]
        })
    }

    toggleLign = (index) => {
        this.setState(prevState => ({
            modal: !prevState.modal,
            indexLignCycleASupprimer: index
        }));
    }

    deleteLign = () => {
        this.state.arrayCycle.splice(this.state.indexLignCycleASupprimer, 1)
        this.setState(prevState => ({
            modal: !prevState.modal
        }));
    }

    nonDeleteLign = () => {
        this.setState(prevState => ({
            modal: !prevState.modal
        }));
    }

    additionEnseignant = (index) => {
        var x = this.state.arrayCycle[index].enseignant
        let element = { selectedEnseignant: '' }
        x.push(element)
        this.setState({
            arrayCycle: this.state.arrayCycle.map((el, id) => (id === index ? Object.assign(el, { enseignant: x }) : el))
        });
    }

