import React, { Component } from 'react'
import axios from 'axios'
import jQuery from 'jquery'
import { Link, BrowserRouter as Router, Route, Switch, withRouter, HashRouter, Redirect } from 'react-router-dom'
import Select from 'react-select';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import { Slide, Zoom, Flip, Bounce } from 'react-toastify';

class AjoutSousFamille extends Component {
    constructor(props) {
        super(props);
        this.state = {
            familles: [],
            selectedFamille: '',
            arraySousArt: [{ sousFamille: '', code: '' }],
            ligneSousFamilleSupp: '',
            modal: false
        }
    }

    componentDidMount() {
        if (JSON.parse((localStorage.getItem('userPermissions'))) != null) {
            axios.get(process.env.REACT_APP_API_URL + 'Back_magasin/SelectFamilles.php').then(res => {
                console.log(res)
                this.setState({
                    familles: res.data
                })
            })
        }
        else {
            document.location = '/'
        }
    }

    addFamille = selectedFamille => {
        this.setState({ selectedFamille: selectedFamille });
    };

    addSousFamille = (event) => {
        this.setState({
            sousFamille: event.target.value
        });
    }

    addCode = (event) => {
        this.setState({
            code: event.target.value
        });
    }

    toggleSousFamille = (index) => {
        this.setState(prevState => ({
            modal: !prevState.modal,
            ligneSousFamilleSupp: index
        }));
    }

    deleteSousFamille = () => {
        this.state.arraySousArt.splice(this.state.ligneSousFamilleSupp, 1)
        this.setState(prevState => ({
            modal: !prevState.modal
        }));
    }

    nonDeleteSousFamille = () => {
        this.setState(prevState => ({
            modal: !prevState.modal
        }));
    }

    ajout = () => {
        if (this.state.selectedFamille != '') {
            jQuery.ajax({
                url: process.env.REACT_APP_API_URL + 'Back_magasin/AddSousFamille.php',
                type: 'POST',
                data: {
                    familleId: this.state.selectedFamille.value,
                    arraySousArt: this.state.arraySousArt
                },
                dataType: 'json',
                success: (code_html, statut) => {
                    if (code_html.Result == "OK") {
                        this.props.history.push('/SousFamilleArt');
                    }
                    else if (code_html.Result == "KO") {
                        alert("erreur,l'un de vos champs est vide!!");
                    }
                }
            });
        }
        else {
            toast.error('⛔ Veuillez verifier le champs famille !!', { containerId: 'A' });
        }
    }

    render() {
        return (
            <div id="page-wrapper" style={{ minHeight: '263px', marginLeft: '0px' }}>
                <div className="container-fluid">
                    <div className="row bg-title">
                        <div className="col-lg-12">
                            <h4 className="page-title">Ajout sous-famille</h4>
                        </div>
                        {/* /.col-lg-12 */}
                    </div>
                    {/* /.row */}
                    <div className="row">
                        <div className="col-sm-12">
                            <div className="white-box">
                                <div style={{ display: 'flex', justifyContent: 'flex-start', alignItems: 'center', marginBottom: '10px', marginRight: '20px' }}>
                                    <Link to="/SousFamilleArt" > <button style={{ background: '#DC143C', border: '1px solid #DC143C', borderRadius: '5px' }} type="button" className="btn btn-info"> <i style={{ paddingRight: '5px' }} className="fas fa-arrow-circle-left"></i>Retour</button></Link>
                                </div>
                                <div className="row">
                                    <div className="col-md-6">
                                        <div className="form-group">
                                            <label>Famille</label>
                                            <Select
                                                isClearable={true}
                                                isSearchable={true}
                                                onChange={this.addFamille}
                                                options={this.state.familles}
                                            />
                                        </div>
                                    </div>
                                </div>
                                <div className="row">
                                    <div className="col-md-6">
                                        <div className="form-group">
                                            <label>Sous-famille</label>
                                        </div>
                                    </div>
                                    <table style={{ width: '100%', marginTop: '10px' }} className="table table-bordered">
                                        <thead>
                                            <tr>
                                                <th style={{ textAlign: 'center' }} scope="col">Sous-famille</th>
                                                <th style={{ textAlign: 'center' }} scope="col">Code</th>
                                                <th style={{ textAlign: 'center' }} scope="col">Supp</th>
                                            </tr>
                                        </thead>
                                        {this.state.arraySousArt.map((el, index) =>
                                            <tbody>
                                                <tr>
                                                    <td style={{ textAlign: 'center' }} className="col-md-5"><input style={{ width: '100%' }} type="text" onChange={(e) => this.addSousFamille(e, index)} /></td>
                                                    <td style={{ textAlign: 'center' }} className="col-md-5"><input style={{ width: '100%' }} type="text" onChange={(e) => this.addCode(e, index)} /></td>
                                                    <td className="col-md-2" style={{ textAlign: 'center' }}><button className='buttonSupprimer' onClick={() => this.toggleSousFamille(index)}><i style={{ color: 'white' }} className="fas fa-trash-alt"></i></button></td>
                                                </tr>
                                            </tbody>
                                        )}
                                    </table>
                                </div>
                                <div style={{ display: 'flex', justifyContent: 'center', marginBottom: '10px' }}>
                                    <button style={{ borderRadius: '5px' }} type="button" className="btn btn-success" onClick={this.ajout}>Confirmer</button>
                                </div>
                            </div>
                        </div>
                    </div>
                    <ToastContainer transition={Flip} enableMultiContainer containerId={'A'} position={toast.POSITION.TOP_RIGHT} autoClose={2500} />
                </div>
            </div>
        );
    }
}

export default withRouter(AjoutSousFamille);
