import React, { Component } from 'react'
import axios from 'axios'
import { Link, BrowserRouter as Router, Route, Switch, withRouter, HashRouter, Redirect } from 'react-router-dom'
import ReactApexChart from 'react-apexcharts'

class StatServicesChiffresAffaires extends Component {
    constructor(props) {
        super(props);
        this.state = {
            series: [{
                name: "Chiffre d'affaire",
                data: []
            }
            ],
            options: {
                chart: {
                    type: 'area',
                    height: 350,
                    stacked: true,
                    toolbar: {
                        show: true
                    },
                    zoom: {
                        enabled: true
                    }
                },
                responsive: [{
                    breakpoint: 480,
                    options: {
                        legend: {
                            position: 'bottom',
                            offsetX: -10,
                            offsetY: 0
                        }
                    }
                }],
                plotOptions: {
                    bar: {
                        horizontal: false,
                    },
                },
                xaxis: {
                    type: 'text',
                    categories: ['Janvier', 'Fevrier', 'Mars', 'Avril', 'MAI', 'Juin', 'Juillet', 'Aout', 'Septembre', 'Octobre', 'November', 'Decembre']
                },
                legend: {
                    position: 'right',
                    offsetY: 40
                },
                fill: {
                    opacity: 1
                }
            },
        };
    }

    componentDidMount() {
        let chiffreAffaire = []
        axios.get(process.env.REACT_APP_API_URL + 'Back_snaii/StatChiffreAffaireServicesParMois.php').then(res => {
            console.log(res)
            for (var i = 0; i < res.data.length[i]; i + 11) {
                chiffreAffaire.push({ data: res.data.splice(0 + i, 11 + i), name: "Chiffre d'affaire" });
            }
            this.setState({
                series: chiffreAffaire
            },
                function () {
                    console.log(this.state.series)
                }.bind(this)
            )
        })
    }

    render() {
        return (
            <div id="page-wrapper" style={{ minHeight: '263px' }}>
                <div className="container-fluid">
                    <div className="row bg-title">
                        <div className="col-lg-12">
                            <h4 className="page-title">Chiffre d'affaire services par mois</h4>
                        </div>
                        {/* /.col-lg-12 */}
                    </div>
                    {/* /.row */}
                    <div class="row">
                        <div class="col-sm-12">
                            <div class="white-box">
                                {/* <div class="row">
                                    <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center' }}>
                                        <div id="chart">
                                            <ReactApexChart options={this.state.options} series={this.state.series} type="area" width={700} height={450} />
                                        </div>
                                    </div>
                                </div> */}
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        );
    }
}

export default StatServicesChiffresAffaires;
