import React, { Component } from 'react'
import axios from 'axios'
import jQuery from 'jquery'
import { withRouter } from 'react-router-dom'

class ModifFamilleArticles extends Component {
    constructor(props) {
        super(props);
        this.state = {
            idFamilleArticles: '',
            nomFamilleArticles: ''
        }
        this.modifNomFamilleArticles = this.modifNomFamilleArticles.bind(this);
        this.modif = this.modif.bind(this)
    }

    componentDidMount() {
        let id_famille_article = this.props.location.state.id_famille_article;
        axios.get(process.env.REACT_APP_API_URL + 'Back_snaii/GetFamilleArticlesById.php?id=' + id_famille_article).then(res => {
            console.log(res)
            this.setState({
                idFamilleArticles: res.data.id_famille_article,
                nomFamilleArticles: res.data.nomFamille
            })
        })
    }

    modifNomFamilleArticles(event) {
        this.setState({
            nomFamilleArticles: event.target.value
        });
    }

    modif() {
        jQuery.ajax({
            url: process.env.REACT_APP_API_URL + 'Back_agora/UpdFamilleArticles.php?id=' + this.state.idFamilleArticles,
            type: 'POST',
            data: {
                nomFamilleArticles: this.state.nomFamilleArticles
            },
            dataType: 'json',
            success: (code_html, statut) => {
                if (code_html.Result == "OK") {
                    this.props.history.push('/FamilleArticles');
                }
                else if (code_html.Result == "KO") {
                    alert("erreur,l'un de vos champs est vide!!");
                }
            }
        });
    }

    render() {
        return (
            <div id="page-wrapper" style={{ minHeight: '263px' }}>
                <div className="container-fluid">
                    <div className="row bg-title">
                        <div className="col-lg-12">
                            <h4 className="page-title">Modification famille articles</h4>
                        </div>
                        {/* /.col-lg-12 */}
                    </div>
                    {/* /.row */}
                    <div class="row">
                        <div class="col-sm-12">
                            <div class="white-box">
                                <div class="row">
                                    <div class="col-md-6">
                                        <div class="form-group">
                                            <label>Nom famille</label>
                                            <input type="text" class="form-control" value={this.state.nomFamilleArticles} onChange={this.modifNomFamilleArticles.bind(this)} style={{ borderRadius: '5px' }} />
                                        </div>
                                    </div>
                                </div>
                                <div style={{ display: 'flex', justifyContent: 'center', marginBottom: '10px' }}>
                                    <button style={{ width: '15%', borderRadius: '5px' }} type="button" className="btn btn-success" onClick={this.modif.bind(this)}>Confirmer</button>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        );
    }
}

export default withRouter(ModifFamilleArticles);
