import React, { Component } from 'react'
import axios from 'axios'
import jQuery from 'jquery'
import { Link, BrowserRouter as Router, Route, Switch, withRouter, HashRouter, Redirect } from 'react-router-dom'
import Select from 'react-select';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import { Slide, Zoom, Flip, Bounce } from 'react-toastify';
import { Button, Modal, ModalHeader, ModalBody, ModalFooter } from 'reactstrap';

class AjoutRayon extends Component {
    constructor(props) {
        super(props);
        this.state = {
            familles: [],
            selectedFamille: '',
            arrayRayon: [{ rayon: '' }],
            ligneRayonSupp: '',
            modal: false
        }
    }

    componentDidMount() {
        if (JSON.parse((localStorage.getItem('userPermissions'))) != null) {
            axios.get(process.env.REACT_APP_API_URL + 'Back_magasin/SelectFamilles.php').then(res => {
                console.log(res)
                this.setState({
                    familles: res.data
                })
            })
        }
        else {
            document.location = '/'
        }
    }

    addFamille = selectedFamille => {
        this.setState({ selectedFamille: selectedFamille });
    };

    addRayon = (event, index) => {
        var rayon = event.target.value;
        this.setState({
            arrayRayon: this.state.arrayRayon.map((el, id) => (id === index ? Object.assign(el, { rayon: rayon }) : el))
        });
    }

    addLigneRayon = () => {
        var element = { rayon: '' }
        this.setState({
            arrayRayon: [...this.state.arrayRayon, element]
        })
    }

    toggleRayon = (index) => {
        this.setState(prevState => ({
            modal: !prevState.modal,
            ligneRayonSupp: index
        }));
    }

    deleteRayon = () => {
        this.state.arrayRayon.splice(this.state.ligneRayonSupp, 1)
        this.setState(prevState => ({
            modal: !prevState.modal
        }));
    }

    nonDeleteRayon = () => {
        this.setState(prevState => ({
            modal: !prevState.modal
        }));
    }

    ajout = () => {
        if (this.state.selectedFamille != '' && this.state.arrayRayon.length > 0) {
            jQuery.ajax({
                url: process.env.REACT_APP_API_URL + 'Back_magasin/AddSousFamille.php',
                type: 'POST',
                data: {
                    familleId: this.state.selectedFamille.value,
                    arrayRayon: this.state.arrayRayon
                },
                dataType: 'json',
                success: (code_html, statut) => {
                    if (code_html.Result == "OK") {
                        this.props.history.push('/Rayons');
                    }
                    else if (code_html.Result == "KO") {
                        alert("erreur,l'un de vos champs est vide!!");
                    }
                }
            });
        }
        else {
            toast.error('â›” Veuillez verifier les champs famille et rayon  !!', { containerId: 'A' });
        }
    }

    render() {
        return (
            <div id="page-wrapper" style={{ minHeight: '263px', marginLeft: '0px' }}>
                <div className="container-fluid">
                    <div className="row bg-title">
                        <div className="col-lg-12">
                            <h4 className="page-title">Ajout rayon</h4>
                        </div>
                        {/* /.col-lg-12 */}
                    </div>
                    {/* /.row */}
                    <div className="row">
                        <div className="col-sm-12">
                            <div className="white-box">
                                <div style={{ display: 'flex', justifyContent: 'flex-start', alignItems: 'center', marginBottom: '10px', marginRight: '20px' }}>
                                    <Link to="/Rayons" > <button style={{ background: '#DC143C', border: '1px solid #DC143C', borderRadius: '5px' }} type="button" className="btn btn-info"> <i style={{ paddingRight: '5px' }} className="fas fa-arrow-circle-left"></i>Retour</button></Link>
                                </div>
                                <div className="row">
                                    <div className="col-md-6">
                                        <div className="form-group">
                                            <label>Famille</label>
                                            <Select
                                                isClearable={true}
                                                isSearchable={true}
                                                onChange={this.addFamille}
                                                options={this.state.familles}
                                            />
                                        </div>
                                    </div>
                                </div>
                                <div className="row">
                                    <div className="col-md-6">
                                        <div className="form-group">
                                            <label>Rayon:</label>
                                        </div>
                                    </div>
                                    <div>
                                        <table style={{ width: '50%', marginTop: '10px' }} className="table table-bordered">
                                            <thead>
                                                <tr>
                                                    <th style={{ textAlign: 'center' }} scope="col">Rayon</th>
                                                    <th style={{ textAlign: 'center' }} scope="col">Supp</th>
                                                </tr>
                                            </thead>
                                            {this.state.arrayRayon.map((el, index) =>
                                                <tbody>
                                                    <tr>
                                                        <td style={{ textAlign: 'center' }} ><input style={{ width: '100%' }} type="text" onChange={(e) => this.addRayon(e, index)} /></td>
                                                        <td  style={{ textAlign: 'center' }}><button className='buttonSupprimer' onClick={() => this.toggleRayon(index)}><i style={{ color: 'white' }} className="fas fa-trash-alt"></i></button></td>
                                                    </tr>
                                                </tbody>
                                            )}
                                        </table>
                                    </div>
                                    <div style={{ display: 'flex', justifyContent: 'flex-start', marginLeft: '15px' }}>
                                        <Button style={{ borderRadius: '5px' }} onClick={this.addLigneSousFamille} color="secondary"><i className="fas fa-plus-circle"></i>Ajouter ligne</Button>
                                    </div>
                                </div>
                                <div style={{ display: 'flex', justifyContent: 'center', marginBottom: '10px' }}>
                                    <button style={{ borderRadius: '5px' }} type="button" className="btn btn-success" onClick={this.ajout}>Confirmer</button>
                                </div>
                            </div>
                        </div>
                        <Modal isOpen={this.state.modal} toggle={this.nonDeleteRayon} className={this.props.className} fade={false}>
                            <ModalHeader toggle={this.nonDeleteRayon}> <div style={{ display: 'flex', justifyContent: 'center', width: '465px' }}><img width='30%' src="./images/deleteModal.png" alt=".." /></div></ModalHeader>
                            <ModalBody>
                                <div style={{ fontFamily: 'Montserrat, sans-serif', FontSize: '14px', FontWeight: '700', LineHeight: '18.375px' }}>
                                    <div style={{ display: 'flex', justifyContent: 'center' }}>
                                        <p>ÃŠtes-Vous sÃ»r de vouloir supprimer cette ligne ?</p>
                                    </div>
                                    <div className='hvr-push' style={{ display: 'flex', justifyContent: 'space-around', marginBottom: '15px' }}>
                                        <div><button onClick={() => this.deleteRayon()} style={{ backgroundColor: '#00FF7F', borderRadius: '5px', color: 'white' }}><i style={{ color: 'white' }} className="far fa-thumbs-up"></i>Oui</button></div>
                                        <div><button onClick={() => this.nonDeleteRayon()} style={{ backgroundColor: '#D9534F', borderRadius: '5px', color: 'white' }}><i style={{ color: 'white' }} className="far fa-thumbs-down"></i>Non</button></div>
                                    </div>
                                </div>
                            </ModalBody>
                        </Modal>
                    </div>
                    <ToastContainer transition={Flip} enableMultiContainer containerId={'A'} position={toast.POSITION.TOP_RIGHT} autoClose={2500} />
                </div>
            </div>
        );
    }
}

export default withRouter(AjoutRayon);
