import React, { Component } from 'react'
import axios from 'axios'
import jQuery from 'jquery'
import { Link, BrowserRouter as Router, Route, withRouter, HashRouter, Redirect } from 'react-router-dom'
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";
import Select from 'react-select';
import { RadioGroup, Radio } from 'react-radio-group'
import Switch from "react-switch";
import Checkbox from 'rc-checkbox';
import 'rc-checkbox/assets/index.css';
import { Fragment } from 'react';
import { Button, Modal, ModalHeader, ModalBody, ModalFooter } from 'reactstrap';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import { Slide, Zoom, Flip, Bounce } from 'react-toastify';

class Inscription extends Component {
    constructor(props) {
        super(props);
        this.state = {
            prenom: '',
            nom: '',
            selectedType: 'CIN',
            numSelonType: '',
            nationalite: '',
            dateDelivSelonType: new Date(),
            villeDelivreSelonType: '',
            dateNaissance: new Date(),
            lieuNaissance: '',
            email: '',
            selectedGender: 'Masculin',
            mobile: '',
            tel: '',
            adresseOfficiel: '',
            adresseTun: '',
            listNivBase: [],
            selectedNivBase: '',
            imageVerso: null,
            numInscrip: '',
            listApprenants: [],
            selectedApprenant: '',
            disabled: false,
            listPapiers: [],
            listTypePay: [],
            selectedTypePay: '',
            listFilieres: [],
            selectedFiliere: '',
            listSpecialites: [],
            selectedSpecialite: '',
            listFormations: [],
            selectedFormation: '',
            listNiveaux: [],
            selectedNiveau: '',
            listGroupes: [],
            selectedGroup: '',
            papiersChoisi: [],
            nomPere: '',
            adresseParents: '',
            profession: '',
            telBureau: '',
            fb: '',
            dateIns: new Date(),
            //
            blockReglement: false,
            listFrais: [],
            listTypePaiement: '',
            selectedTypePayFrais: '',
            mntFraisPayer: 0,
            mntFraisRestant: '',
            mntFraisTotalPayer: 0,
            payFormation: [],
            listModPay: [],
            tabModPay: [{ modPay: '', mntModPay: '', numCheque: '', echeanceChec: new Date(), dateEncaiss: new Date(), titulaireChec: '', banque: '' }],
            mntInscripPayer: 0,
            mntInscripRestant: '',
            mntIncripTotalPayer: 0,
            ajoutInscrip: false,
            modal: false,
            indexLignASupprimer: '',
            eleveId: '',
            indexTranchePay: [],
            disabledFrais: false,
            disabledReg: false
        }
    }

    componentDidMount() {
        if (JSON.parse((localStorage.getItem('userPermissions'))) != null) {
            axios.get(process.env.REACT_APP_API_URL + 'Back_mediatraining/SelectNivBase.php').then(res => {
                console.log(res)
                this.setState({
                    listNivBase: res.data
                })
            })
            var userPermissions = JSON.parse((localStorage.getItem('userPermissions')));
            var anneeScolaire = userPermissions[0].anneeScolaire
            jQuery.ajax({
                url: process.env.REACT_APP_API_URL + 'Back_mediatraining/SelectFilieres.php',
                type: 'POST',
                data: {
                    anneeScolaire: anneeScolaire
                },
                dataType: 'json',
                success: (code_html, statut) => {
                    this.setState({
                        listFilieres: code_html
                    });
                }
            });
            axios.get(process.env.REACT_APP_API_URL + 'Back_mediatraining/PapiersDemandee.php').then(res => {
                console.log(res)
                this.setState({
                    listPapiers: res.data
                })
            })
