    ajoutRegInscrip = () => {
        var sommePaiement = 0
        sommePaiement = parseInt(this.state.mntFraisTotalPayer) + parseInt(this.state.mntIncripTotalPayer)
        var sommeReg = 0
        for (var i = 0; i < this.state.tabModPay.length; i++) {
            sommeReg += parseInt(this.state.tabModPay[i].mntModPay)
        }
        if (sommePaiement !== sommeReg) {
            toast.error('⛔ Veuillez vérifier les sommes saisie !', { containerId: 'A' });
        }
        else {
            var userPermissions = JSON.parse((localStorage.getItem('userPermissions')));
            var anneeScolaire = userPermissions[0].anneeScolaire
            var userId = userPermissions[0].idUser
            var arrayModPay = []
            for (var i = 0; i < this.state.tabModPay.length; i++) {
                arrayModPay.push(this.state.tabModPay[i].modPay.value)
            }
            // convertir date echeance et encaissement cheque si le mode de paiement => cheque
            var convertDateEcheChec = ''
            var convertDateEncaissChec = ''
            for (var i = 0; i < this.state.tabModPay.length; i++) {
                if (this.state.tabModPay[i].modPay.value == 2) {
                    // convertir date echeance cheque
                    var dateEcheChec = this.state.tabModPay[i].echeanceChec
                    let monthOne = '' + (dateEcheChec.getMonth() + 1);
                    let dayOne = '' + dateEcheChec.getDate();
                    let yearOne = dateEcheChec.getFullYear();
                    if (monthOne.length < 2)
                        monthOne = '0' + monthOne;
                    if (dayOne.length < 2)
                        dayOne = '0' + dayOne;
                    convertDateEcheChec = [yearOne, monthOne, dayOne].join('-');
                    // convertir date encaissement cheque
                    var dateEncaissChec = this.state.tabModPay[i].dateEncaiss
                    let monthTwo = '' + (dateEncaissChec.getMonth() + 1);
                    let dayTwo = '' + dateEncaissChec.getDate();
                    let yearTwo = dateEncaissChec.getFullYear();
                    if (monthTwo.length < 2)
                        monthTwo = '0' + monthTwo;
                    if (dayTwo.length < 2)
                        dayTwo = '0' + dayTwo;
                    convertDateEncaissChec = [yearTwo, monthTwo, dayTwo].join('-');
                }
            }
            // convert dateInscription
            let dateInscrip = this.state.dateIns
            let month = '' + (dateInscrip.getMonth() + 1);
            let day = '' + dateInscrip.getDate();
            let year = dateInscrip.getFullYear();
            if (month.length < 2)
                month = '0' + month;
            if (day.length < 2)
                day = '0' + day;
            var convertInscrip = [year, month, day].join('-');
            //
            jQuery.ajax({
                url: process.env.REACT_APP_API_URL + 'Back_mediatraining/AddRegInscrip.php',
                type: 'POST',
                data: {
                    idInscrip: this.state.numInscrip,
                    eleveId: this.state.eleveId,
                    regFrais: this.state.listFrais,
                    regInscrip: this.state.payFormation,
                    modPay: this.state.tabModPay,
                    arrayIdModPay: arrayModPay,
                    groupId: this.state.selectedGroup.value,
                    typePaiement: this.state.selectedTypePay,
                    anneeScolaire: anneeScolaire,
                    userId: userId,
                    dateReg: convertInscrip,
                    dateEcheChec: convertDateEcheChec,
                    dateEncaissChec: convertDateEncaissChec,
                    montantTotal: sommeReg
                },
                dataType: 'json',
                success: (code_html, statut) => {
                    if (code_html.Result == "OK") {
                        var idRegFor = code_html.idRegFor
                        // Recu reglement inscription
                        jQuery.ajax({
                            url: process.env.REACT_APP_API_URL + 'Back_mediatraining/RecuRegFor.php',
                            type: 'POST',
                            data: {
                                idRegFor: idRegFor
                            },
                            dataType: 'json',
                            success: (code_html, statut) => {
                                var server_path = code_html.server_path;
                                var file_path = code_html.file_path
                                var path = server_path + file_path;
                                window.open(path, "_blank")
                                //
                                if (this.state.imageVerso != null) {
                                    let files = this.state.imageVerso;
                                    console.warn('data files', files)
                                    let reader = new FileReader();
                                    reader.readAsDataURL(files[0])
                                    reader.onload = (e) => {
                                        console.log(e)
                                        jQuery.ajax({
                                            url: process.env.REACT_APP_API_URL + 'Back_mediatraining/UploadphotoAppr.php',
                                            type: 'POST',
                                            data: {
                                                file: e.target.result,
                                                nomfile: this.state.imageVerso[0].name
                                            },
                                            dataType: 'json',
                                            success: (code_html, statut) => {
                                            }
                                        }
                                        )
                                    }
                                    this.props.history.push('/ReglApprenant');
                                }
                                else {
                                    this.props.history.push('/ReglApprenant');
                                }
                            }
                        });
                    }
                    else {
                        alert("prbl lors de l'insertion")
                    }
                }
            });
        }
    }

