import React, { Component } from 'react'
import axios from 'axios'
import jQuery from 'jquery'
import { withRouter } from 'react-router-dom'
import Select from 'react-select';
import { Button } from 'reactstrap';

class ModifMatiere extends Component {
    constructor(props) {
        super(props);
        this.state = {
            idMatiere: '',
            matiere: {},
            ListTypeEns: [],
            selectedTypeEns: ''
        }
    }

    componentDidMount() {
        let idMatiere = this.props.location.state.idMatiere;
        axios.get(process.env.REACT_APP_API_URL + 'Back_mediatraining/GetMatiereById.php?id=' + idMatiere).then(res => {
            console.log(res)
            this.setState({
                idMatiere: res.data.idMatiere,
                matiere: res.data
            })
        })
        axios.get(process.env.REACT_APP_API_URL + 'Back_mediatraining/SelectTypeEns.php').then(res => {
            console.log(res)
            this.setState({
                ListTypeEns: res.data
            })
        })
    }

    updCode = (event) => {
        let code = event.target.value;
        this.setState({
            matiere: Object.assign(this.state.matiere, { code: code })
        });
    }

    updNomMatiere = (event) => {
        let nomMatiere = event.target.value;
        this.setState({
            matiere: Object.assign(this.state.matiere, { nomMatiere: nomMatiere })
        });
    }

    updAbrev = (event) => {
        let abrev = event.target.value;
        this.setState({
            matiere: Object.assign(this.state.matiere, { abrev: abrev })
        });
    }

    updTypeEns = (selectedTypeEns) => {
        let typeEns = selectedTypeEns;
        this.setState({
            matiere: Object.assign(this.state.matiere, { typeEns: typeEns })
        });
    }

    modif = () => {
        let year = new Date().getFullYear();
        console.log(this.state.matiere)
        // jQuery.ajax({
        //     url: process.env.REACT_APP_API_URL + 'Back_mediatraining/UpdMatiere.php?id=' + this.state.idMatiere,
        //     type: 'POST',
        //     data: {
        //         matiere: this.state.matiere,
        //         year: year
        //     },
        //     dataType: 'json',
        //     success: (code_html, statut) => {
        //         if (code_html.Result == "OK") {
        //             this.props.history.push('/Matieres');
        //         }
        //         else if (code_html.Result == "KO") {
        //             alert("erreur,l'un de vos champs est vide!!");
        //         }
        //     }
        // });
    }

    render() {
        return (
            <div id="page-wrapper" style={{ minHeight: '263px' }}>
                <div className="container-fluid">
                    <div className="row bg-title">
                        <div className="col-lg-12">
                            <h4 className="page-title">Modification mati√©re</h4>
                        </div>
                        {/* /.col-lg-12 */}
                    </div>
                    {/* /.row */}
                    <div class="row">
                        <div class="col-sm-12">
                            <div class="white-box">
                                <div class="row">
                                    <table style={{ width: '100%', marginTop: '10px' }} class="table table-bordered">
                                        <thead>
                                            <tr>
                                                <th style={{ textAlign: 'center' }} scope="col">Code</th>
                                                <th style={{ textAlign: 'center' }} scope="col">Nom</th>
                                                <th style={{ textAlign: 'center' }} scope="col">Abrev</th>
                                                <th style={{ textAlign: 'center' }} scope="col">TypeEns</th>
                                            </tr>
                                        </thead>
                                        <tbody>
                                            <tr>
                                                <td><input type="text" value={this.state.matiere.code} onChange={(e) => this.updCode(e)} style={{ textAlign: 'center', height: '38px', borderRadius: '5px' }} /></td>
                                                <td><input type="text" value={this.state.matiere.nomMatiere} onChange={(e) => this.updNomMatiere(e)} style={{ textAlign: 'center', height: '38px', borderRadius: '5px' }} /></td>
                                                <td><input type="text" value={this.state.matiere.abrev} onChange={(e) => this.updAbrev(e)} style={{ textAlign: 'center', height: '38px', borderRadius: '5px' }} /></td>
                                                <td style={{ width: '20%' }}><Select
                                                    value={this.state.matiere.typeEns}
                                                    isClearable={true}
                                                    isSearchable={true}
                                                    onChange={(e) => this.updTypeEns(e)}
                                                    options={this.state.ListTypeEns}
                                                />  </td>
                                            </tr>
                                        </tbody>
                                    </table>
                                    <div style={{ display: 'flex', justifyContent: 'flex-start', marginLeft: '15px' }}>
                                        <Button style={{ borderRadius: '5px' }} onClick={this.additionLignMatiere} color="secondary"><i class="fas fa-plus-circle"></i>Ajouter ligne</Button>
                                    </div>
                                </div>
                                <div style={{ display: 'flex', justifyContent: 'center', marginBottom: '10px' }}>
                                    <button style={{ width: '15%', borderRadius: '5px' }} type="button" className="btn btn-success" onClick={this.ajout}>Confirmer</button>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        );
    }
}

export default withRouter(ModifMatiere);
