import React, { Component, Fragment } from 'react'
import { Link, BrowserRouter as Router, Route, Switch, withRouter, HashRouter, Redirect } from 'react-router-dom'
import DataTable, { createTheme } from 'react-data-table-component';
import axios from 'axios'
import jQuery from 'jquery'
import './paginate.css'
import { Button, Modal, ModalHeader, ModalBody, ModalFooter } from 'reactstrap';
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";

class HistoriqueConnexion extends Component {
    constructor(props) {
        super(props);
        this.state = {
            data: [],
            columns: [
                {
                    name: 'Users',
                    selector: 'user',
                    sortable: false,
                    center: true,
                    style: { width: '35%' }
                },
                {
                    name: 'Date connexion',
                    selector: 'dateConnex',
                    sortable: false,
                    center: true,
                    style: { width: '35%' }
                },
                {
                    name: 'Historique',
                    cell: row => <div onClick={() => this.histUser(row)} style={{ display: 'flex', justifyContent: 'space-between' }}><div><i style={{ color: '#87CEEB' }} className="fas fa-chalkboard-teacher"></i></div><div style={{ marginLeft: '5px' }}><p>Historique</p></div></div>
                    ,
                    ignoreRowClick: true,
                    allowOverflow: true,
                    button: true,
                    width: '30%'
                },
            ],
            nombreTotalElement: 0,
            filterElement: '',
            mycurrentPage: 1,
            resetFirstPage: false,
            nbrEltperPage: 5,
            modal: false,
            pending: true,
            realisation: []
        }
        this.newElement = this.newElement.bind(this);
        this.filterSearch = this.filterSearch.bind(this);
    }

    componentDidMount() {
        jQuery.ajax({
            url: process.env.REACT_APP_API_URL + 'Back_mediatraining/HistConnexion.php',
            type: 'POST',
            data: {
                query: '',
                limit: this.state.nbrEltperPage,
                page: 1
            },
            dataType: 'json',
            success: (code_html, statut) => {
                console.log(code_html)
                this.setState({
                    nombreTotalElement: code_html.total,
                    data: code_html,
                    pending: false
                });
            }
        });
    }

    histUser = (row) => {
        this.setState({
            realisation: row.realisation
        }, () => {
            this.setState(prevState => ({
                modal: !prevState.modal,
            }));
        });
    }

    toggle = () => {
        this.setState(prevState => ({
            modal: !prevState.modal,
        }));
    }

    newElement(page, totalRows) {
        this.setState({
            pending: true
        },
            () => {
                jQuery.ajax({
                    url: process.env.REACT_APP_API_URL + 'Back_mediatraining/HistConnexion.php',
                    type: 'POST',
                    data: {
                        query: this.state.filterElement,
                        limit: this.state.nbrEltperPage,
                        page: page
                    },
                    dataType: 'json',
                    success: (code_html, statut) => {
                        console.log(code_html)
                        this.setState({
                            nombreTotalElement: code_html.total,
                            data: code_html.data,
                            pending: false
                        });
                    }
                });
            }
        )
    }

    perPage(currentRowsPerPage, currentPage) {
        this.setState(
            {
                nbrEltperPage: currentRowsPerPage,
                pending: true
            }
            , () => {
                jQuery.ajax({
                    url: process.env.REACT_APP_API_URL + 'Back_mediatraining/HistConnexion.php',
                    type: 'POST',
                    data: {
                        query: this.state.filterElement,
                        limit: currentRowsPerPage,
                        page: currentPage
                    },
                    dataType: 'json',
                    success: (code_html, statut) => {
                        console.log(code_html)
                        this.setState({
                            nombreTotalElement: code_html.total,
                            data: code_html.data,
                            pending: false
                        });
                    }
                });
            }
        );
    }

    filterSearch(event) {
        this.setState({
            filterElement: event.target.value,
            resetFirstPage: !this.state.resetFirstPage,
            pending: true
        },
            () => {
                jQuery.ajax({
                    url: process.env.REACT_APP_API_URL + 'Back_mediatraining/HistConnexion.php',
                    type: 'POST',
                    data: {
                        query: this.state.filterElement,
                        limit: this.state.nbrEltperPage,
                        page: 1
                    },
                    dataType: 'json',
                    success: (code_html, statut) => {
                        console.log(code_html)
                        this.setState({
                            nombreTotalElement: 1,
                            data: code_html,
                            pending: false
                        });
                    }
                })
            }
        );
    }

    render() {
        return (
            <div id="page-wrapper" style={{ minHeight: '263px', marginLeft: '0px' }}>
                <div className="container-fluid">
                    <div className="row bg-title">
                        <div className="col-lg-12">
                            <h4 className="page-title">Historique</h4>
                        </div>
                        {/* /.col-lg-12 */}
                    </div>
                    {/* /.row */}
                    <div className="row">
                        <div className="col-sm-12">
                            <div className="white-box">
                                <div className="mb-0 form-group">
                                    <div className="input-group-alternative input-group">
                                        <div className="input-group-prepend">
                                            <span className="input-group-text">
                                                <i className="fas fa-search"></i>
                                            </span>
                                            <input onChange={(currentRowsPerPage, currentPage) => this.filterSearch(currentRowsPerPage, currentPage)} placeholder="Search" type="text" className="search" />
                                            <div style={{ display: 'flex', alignItems: 'center' }}>
                                                <div style={{ display: 'flex' }}>
                                                    <div>
                                                        <DatePicker class="form-control ddate" style={{ border: 'none' }}
                                                            selected={this.state.dateSearchDebut}
                                                            onChange={this.searchDateDebut}
                                                            customInput={<div><label>Date début</label><div class="form-group"><input style={{ borderRadius: '5px', border: 'solid 1px', borderColor: '#B3B3B3' }} /></div></div>}
                                                        />
                                                    </div>
                                                    <div>
                                                        <DatePicker class="form-control ddate" style={{ border: 'none' }}
                                                            selected={this.state.dateSearchFin}
                                                            onChange={this.searchDateFin}
                                                            customInput={<div><label>Date fin</label><div class="form-group"><input style={{ borderRadius: '5px', border: 'solid 1px', borderColor: '#B3B3B3' }} /></div></div>}
                                                        />
                                                    </div>
                                                </div>
                                                <div>
                                                    <button style={{ borderRadius: '5px' }} type="button" className="btn btn-success">Chercher</button>
                                                </div>
                                            </div>
                                        </div>
                                    </div>
                                </div>
                                <DataTable
                                    columns={this.state.columns}
                                    data={this.state.data}
                                    highlightOnHover={true}
                                    pagination={true}
                                    paginationServer={true}
                                    onChangePage={(page, totalRows) => this.newElement(page, totalRows)}
                                    paginationTotalRows={this.state.nombreTotalElement}
                                    paginationPerPage={this.state.nbrEltperPage}
                                    paginationRowsPerPageOptions={[5, 10, 15, 20, 25]}
                                    onChangeRowsPerPage={(currentRowsPerPage, currentPage) => this.perPage(currentRowsPerPage, currentPage)}
                                    paginationResetDefaultPage={this.state.resetFirstPage}
                                    paginationComponentOptions={{ rowsPerPageText: 'Elements par page:', rangeSeparatorText: 'de', noRowsPerPage: false }}
                                    progressPending={this.state.pending}
                                />
                            </div>
                        </div>
                    </div>
                </div>
                <Modal isOpen={this.state.modal} toggle={this.toggle} className={this.props.className} fade={false}>
                    <ModalHeader toggle={this.toggle}><div style={{ display: 'flex', justifyContent: 'space-between' }}><div><i style={{ color: '#87CEEB' }} className="fas fa-chalkboard-teacher"></i></div><div style={{ marginLeft: '5px', fontWeight: 'bold' }}><p>Historique action utilisateur</p></div></div></ModalHeader>
                    <ModalBody>
                        <div>
                            {this.state.realisation.map((el, index) =>
                                <p>{'Action ' + (index + 1) + " : " + el.action + ' element a' + ' ' + el.dateAction + ' dans la table ' + el.nomTable}</p>
                            )}
                        </div>
                    </ModalBody>
                </Modal>
            </div>
        );
    }
}

export default HistoriqueConnexion;
