import React, { Component, Fragment } from 'react'
import { Link, BrowserRouter as Router, Route, Switch, withRouter, HashRouter, Redirect } from 'react-router-dom'
import DataTable from 'react-data-table-component';
import axios from 'axios'
import jQuery from 'jquery'
import './paginate.css'
import { Button, Modal, ModalHeader, ModalBody, ModalFooter } from 'reactstrap';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import { Slide, Zoom, Flip, Bounce } from 'react-toastify';

class Enseignants extends Component {
    constructor(props) {
        super(props);
        this.state = {
            data: [],
            columns: [
                {
                    name: 'Pr√©nom-Nom',
                    selector: 'prenomNom',
                    sortable: false,
                    center: true,
                    style: { width: '30%' }
                },
                {
                    name: 'Tel',
                    selector: 'tel',
                    sortable: false,
                    center: true,
                    style: { width: '10%' }
                },
                {
                    name: 'Email',
                    selector: 'email',
                    sortable: false,
                    center: true,
                    style: { width: '10%' }
                },
                {
                    name: 'Fiche',
                    cell: row => <button style={{ backgroundColor: '#87B87F', borderColor: '#87B87F', borderRadius: '5px' }} onClick={() => this.ficheEnseignant(row)}><i style={{ color: 'white' }} className="fas fa-address-card"></i></button>,
                    ignoreRowClick: true,
                    allowOverflow: true,
                    button: true,
                    width: '10%'
                },
                {
                    name: 'Modifier',
                    cell: row => this.state.permissionEnseignants.modifier == 1 ? (<Link to={{
                        pathname: '/ModifEnseignant',
                        state: { idEnseignant: row.idEnseignant }
                    }}><button className='buttonModifier'><i style={{ color: 'white' }} className="fas fa-pencil-alt"></i></button></Link>) : (null),
                    ignoreRowClick: true,
                    allowOverflow: true,
                    button: true,
                    width: '10%'
                },
                {
                    name: 'Groupes',
                    cell: row => ((this.state.permissionEnseignGroup.ajouter == 1) && (this.state.permissionEnseignGroup.explorer == 1) && (this.state.permissionEnseignGroup.modifier == 1) && (this.state.permissionEnseignGroup.supprimer == 1)) ? (<Link to={{
                        pathname: '/EnseignantsGroupes',
                        state: { idEnseignant: row.idEnseignant }
                    }}><button style={{ backgroundColor: '#8A2BE2', borderRadius: '5px' }}><i style={{ color: 'white' }} className="fas fa-users-cog"></i></button>
                    </Link>) : (null),
                    ignoreRowClick: true,
                    allowOverflow: true,
                    button: true,
                    width: '10%'
                },
                {
                    name: 'Paiements',
                    cell: row => ((this.state.permissionPayEnseign.ajouter == 1) && (this.state.permissionPayEnseign.explorer == 1) && (this.state.permissionPayEnseign.modifier == 1) && (this.state.permissionPayEnseign.supprimer == 1)) ? (<Link to={{
                        pathname: '/PaiementsEnseignant',
                        state: { idEnseignant: row.idEnseignant }
                    }}><button style={{ backgroundColor: '#EFAD0C', borderRadius: '5px' }}><i style={{ color: 'white' }} className="fas fa-money-bill-wave"></i></button>
                    </Link>) : (null),
                    ignoreRowClick: true,
                    allowOverflow: true,
                    button: true,
                    width: '10%'
                },
                {
                    name: 'Desactiver',
                    cell: row => this.state.permissionEnseignants.supprimer == 1 ? (<button className='buttonSupprimer' onClick={() => this.toggletwo(row.idEnseignant)}><i style={{ color: 'white' }} className="fas fa-trash-alt"></i></button>) : (null),
                    ignoreRowClick: true,
                    allowOverflow: true,
                    button: true,
                    width: '10%'
                },
            ],
            nombreTotalElement: 0,
            filterElement: '',
            mycurrentPage: 1,
            resetFirstPage: false,
            nbrEltperPage: 5,
            modal: false,
            idEnseignant: '',
            pending: true,
            permissionEnseignants: '',
            permissionPayEnseign: '',
            permissionEnseignGroup: '',
            // search
            nom: '',
            prenom: '',
            cin: ''
        }
        this.newElement = this.newElement.bind(this);
        this.filterSearch = this.filterSearch.bind(this);
    }

    componentDidMount() {
        if (JSON.parse((localStorage.getItem('userPermissions'))) != null) {
            var userPermissions = JSON.parse((localStorage.getItem('userPermissions')));
            var permissionEnseignants = userPermissions[9]
            var permissionPayEnseign = userPermissions[11]
            var permissionEnseignGroup = userPermissions[13]
            this.setState({
                permissionEnseignants: permissionEnseignants,
                permissionPayEnseign: permissionPayEnseign,
                permissionEnseignGroup: permissionEnseignGroup
            })
            jQuery.ajax({
                url: process.env.REACT_APP_API_URL + 'Back_mediatraining/Enseignants.php',
                type: 'POST',
                data: {
                    query: '',
                    limit: this.state.nbrEltperPage,
                    page: 1
                },
                dataType: 'json',
                success: (code_html, statut) => {
                    console.log(code_html)
                    this.setState({
                        nombreTotalElement: code_html.total,
                        data: code_html.data,
                        pending: false
                    });
                }
            });
        }
        else {
            document.location = '/'
        }
    }

    newElement(page, totalRows) {
        if (this.state.prenom == "" && this.state.nom == "" && this.state.cin == "") {
            this.setState({
                pending: true
            },
                () => {
                    jQuery.ajax({
                        url: process.env.REACT_APP_API_URL + 'Back_mediatraining/Enseignants.php',
                        type: 'POST',
                        data: {
                            query: this.state.filterElement,
                            limit: this.state.nbrEltperPage,
                            page: page
                        },
                        dataType: 'json',
                        success: (code_html, statut) => {
                            console.log(code_html)
                            this.setState({
                                nombreTotalElement: code_html.total,
                                data: code_html.data,
                                pending: false
                            });
                        }
                    });
                }
            )
        }
        else {
            this.setState({
                pending: true
            },
                () => {
                    jQuery.ajax({
                        url: process.env.REACT_APP_API_URL + 'Back_mediatraining/EnseignantsSearch.php',
                        type: 'POST',
                        data: {
                            prenom: this.state.prenom,
                            nom: this.state.nom,
                            cin: this.state.cin,
                            limit: this.state.nbrEltperPage,
                            page: page
                        },
                        dataType: 'json',
                        success: (code_html, statut) => {
                            console.log(code_html)
                            this.setState({
                                nombreTotalElement: code_html.total,
                                data: code_html.data,
                                pending: false
                            });
                        }
                    });
                }
            )
        }
    }

    perPage(currentRowsPerPage, currentPage) {
        if (this.state.prenom == "" && this.state.nom == "" && this.state.cin == "") {
            this.setState(
                {
                    nbrEltperPage: currentRowsPerPage,
                    pending: true
                }
                , () => {
                    jQuery.ajax({
                        url: process.env.REACT_APP_API_URL + 'Back_mediatraining/Enseignants.php',
                        type: 'POST',
                        data: {
                            query: this.state.filterElement,
                            limit: currentRowsPerPage,
                            page: currentPage
                        },
                        dataType: 'json',
                        success: (code_html, statut) => {
                            console.log(code_html)
                            this.setState({
                                nombreTotalElement: code_html.total,
                                data: code_html.data,
                                pending: false
                            });
                        }
                    });
                }
            );
        }
        else {
            this.setState(
                {
                    nbrEltperPage: currentRowsPerPage,
                    pending: true
                }
                , () => {
                    jQuery.ajax({
                        url: process.env.REACT_APP_API_URL + 'Back_mediatraining/EnseignantsSearch.php',
                        type: 'POST',
                        data: {
                            prenom: this.state.prenom,
                            nom: this.state.nom,
                            cin: this.state.cin,
                            limit: currentRowsPerPage,
                            page: currentPage
                        },
                        dataType: 'json',
                        success: (code_html, statut) => {
                            console.log(code_html)
                            this.setState({
                                nombreTotalElement: code_html.total,
                                data: code_html.data,
                                pending: false
                            });
                        }
                    });
                }
            );
        }
    }

    filterSearch(event) {
        this.setState({
            filterElement: event.target.value,
            resetFirstPage: !this.state.resetFirstPage,
            pending: true
        },
            () => {
                jQuery.ajax({
                    url: process.env.REACT_APP_API_URL + 'Back_mediatraining/Enseignants.php',
                    type: 'POST',
                    data: {
                        query: this.state.filterElement,
                        limit: this.state.nbrEltperPage,
                        page: 1
                    },
                    dataType: 'json',
                    success: (code_html, statut) => {
                        console.log(code_html)
                        this.setState({
                            nombreTotalElement: code_html.total,
                            data: code_html.data,
                            pending: false
                        });
                    }
                })
            }
        );
    }

    ficheEnseignant = (el) => {
        var userPermissions = JSON.parse((localStorage.getItem('userPermissions')));
        var anneeScolaire = userPermissions[0].anneeScolaire
        var idEns = el.idEnseignant
        var type = el.type
        if (type == 'CIN') {
            jQuery.ajax({
                url: process.env.REACT_APP_API_URL + 'Back_mediatraining/FicheEnseignantCin.php',
                type: 'POST',
                data: {
                    idEns: idEns,
                    anneeScolaire: anneeScolaire
                },
                dataType: 'json',
                success: function (code_html, statut) {
                    var server_path = code_html.server_path;
                    var file_path = code_html.file_path
                    var path = server_path + file_path;
                    window.open(path, "_blank")
                }
            });
        }
        else if (type == 'Passeport') {
            jQuery.ajax({
                url: process.env.REACT_APP_API_URL + 'Back_mediatraining/FicheEnseignantPasse.php',
                type: 'POST',
                data: {
                    idEns: idEns,
                    anneeScolaire: anneeScolaire
                },
                dataType: 'json',
                success: function (code_html, statut) {
                    var server_path = code_html.server_path;
                    var file_path = code_html.file_path
                    var path = server_path + file_path;
                    window.open(path, "_blank")
                }
            });
        }
    }

    toggletwo = (idEnseignant) => {
        this.setState(prevState => ({
            modal: !prevState.modal,
            idEnseignant: idEnseignant
        }));
    }

    toggle = () => {
        this.setState(prevState => ({
            modal: !prevState.modal
        }));
    }

    addNom = (event) => {
        this.setState({
            nom: event.target.value
        });
    }

    addPrenom = (event) => {
        this.setState({
            prenom: event.target.value
        });
    }

    addCin = (event) => {
        this.setState({
            cin: event.target.value
        });
    }

    deleteSearch = () => {
        this.state.search.splice(0, 1)
    }

    recherche = () => {
        console.log(this.state.search)
        this.setState({
            resetFirstPage: !this.state.resetFirstPage,
            pending: true
        },
            () => {
                jQuery.ajax({
                    url: process.env.REACT_APP_API_URL + 'Back_mediatraining/EnseignantsSearch.php',
                    type: 'POST',
                    data: {
                        prenom: this.state.prenom,
                        nom: this.state.nom,
                        cin: this.state.cin,
                        limit: this.state.nbrEltperPage,
                        page: 1
                    },
                    dataType: 'json',
                    success: (code_html, statut) => {
                        console.log(code_html)
                        this.setState({
                            nombreTotalElement: code_html.total,
                            data: code_html.data,
                            pending: false
                        });
                    }
                })
            }
        );
    }

    afficherTout = () => {
        this.setState({
            nom: '',
            prenom: '',
            cin: ''
        });
        jQuery.ajax({
            url: process.env.REACT_APP_API_URL + 'Back_mediatraining/Enseignants.php',
            type: 'POST',
            data: {
                query: '',
                limit: this.state.nbrEltperPage,
                page: 1
            },
            dataType: 'json',
            success: (code_html, statut) => {
                console.log(code_html)
                this.setState({
                    nombreTotalElement: code_html.total,
                    data: code_html.data,
                    pending: false
                });
            }
        });
    }

    deleteEnseignant() {
        var userPermissions = JSON.parse((localStorage.getItem('userPermissions')));
        var userId = userPermissions[0].idUser
        jQuery.ajax({
            url: process.env.REACT_APP_API_URL + "Back_mediatraining/DeleteEnseignant.php?id=" + this.state.idEnseignant + "&userId=" + userId,
            type: 'POST',
            dataType: 'json',
            success: (code_html, statut) => {
                this.setState({
                    modal: false
                }, () => {
                    jQuery.ajax({
                        url: process.env.REACT_APP_API_URL + 'Back_mediatraining/Enseignants.php',
                        type: 'POST',
                        data: {
                            query: '',
                            limit: this.state.nbrEltperPage,
                            page: 1
                        },
                        dataType: 'json',
                        success: (code_html, statut) => {
                            console.log(code_html)
                            this.setState({
                                nombreTotalElement: code_html.total,
                                data: code_html.data
                            });
                        }
                    });
                })
            }
        });
    }

    render() {
        return (
            <div id="page-wrapper" style={{ minHeight: '263px', marginLeft: '0px' }}>
                <div className="container-fluid">
                    <div className="row bg-title">
                        <div className="col-lg-12">
                            <h4 className="page-title">Enseignants</h4>
                        </div>
                        {/* /.col-lg-12 */}
                    </div>
                    {/* /.row */}
                    <div className="row">
                        <div className="col-sm-12">
                            <div className="white-box">
                                {this.state.permissionEnseignants.ajouter == 1 ?
                                    (<div style={{ display: 'flex', justifyContent: 'flex-end', alignItems: 'center', marginBottom: '10px', marginRight: '20px' }}>
                                        <Link to="/AjoutEnseignant" > <button style={{ background: '#3A3F51', border: '1px solid #3A3F51', borderRadius: '5px' }} type="button" className="btn btn-info"> <i className="fas fa-plus"></i>Enseignant</button></Link>
                                    </div>) : (null)}
                                <div className="row">
                                    <h4 style={{ paddingLeft: '10px' }}>Crit√®res de recherche</h4>
                                    <Fragment>
                                        <div className="col-md-6">
                                            <label>Pr√©nom</label>
                                            <div className="form-group">
                                                <input type="text" onChange={(e) => this.addPrenom(e)} style={{ textAlign: 'center', height: '38px', borderRadius: '5px', width: '100%', border: 'solid 1px #B3B3B3' }} />
                                            </div>
                                        </div>
                                        <div className="col-md-6">
                                            <label>Nom</label>
                                            <div className="form-group">
                                                <input type="text" onChange={(e) => this.addNom(e)} style={{ textAlign: 'center', height: '38px', borderRadius: '5px', width: '100%', border: 'solid 1px #B3B3B3' }} />
                                            </div>
                                        </div>
                                        <div className="col-md-6">
                                            <label>CIN</label>
                                            <div className="form-group">
                                                <input type="text" onChange={(e) => this.addCin(e)} style={{ textAlign: 'center', height: '38px', borderRadius: '5px', width: '100%', border: 'solid 1px #B3B3B3' }} />
                                            </div>
                                        </div>
                                    </Fragment>
                                </div>
                                <div style={{ display: 'flex', justifyContent: 'space-around' }}>
                                    <div>
                                        <button style={{ borderRadius: '5px', backgroundColor: '#761C19', borderColor: '#761C19' }} type="button" className="btn btn-success" onClick={this.recherche}>Rechercher</button>
                                    </div>
                                    <div>
                                        <button style={{ borderRadius: '5px', backgroundColor: '#761C19', borderColor: '#761C19' }} type="button" className="btn btn-success" onClick={this.afficherTout}>Afficher tout</button>
                                    </div>
                                </div>
                                <div className="mb-0 form-group">
                                    <div className="input-group-alternative input-group">
                                        <div className="input-group-prepend">
                                            <span className="input-group-text">
                                                <i className="fas fa-search"></i>
                                            </span>
                                            <input onChange={(currentRowsPerPage, currentPage) => this.filterSearch(currentRowsPerPage, currentPage)} placeholder="Search" type="text" className="search" />
                                        </div>
                                    </div>
                                </div>
                                <DataTable
                                    columns={this.state.columns}
                                    data={this.state.data}
                                    highlightOnHover={true}
                                    pagination={true}
                                    paginationServer={true}
                                    onChangePage={(page, totalRows) => this.newElement(page, totalRows)}
                                    paginationTotalRows={this.state.nombreTotalElement}
                                    paginationPerPage={this.state.nbrEltperPage}
                                    paginationRowsPerPageOptions={[5, 10, 15, 20, 25]}
                                    onChangeRowsPerPage={(currentRowsPerPage, currentPage) => this.perPage(currentRowsPerPage, currentPage)}
                                    paginationResetDefaultPage={this.state.resetFirstPage}
                                    paginationComponentOptions={{ rowsPerPageText: 'Elements par page:', rangeSeparatorText: 'de', noRowsPerPage: false }}
                                    progressPending={this.state.pending}
                                />
                            </div>
                        </div>
                    </div>
                </div>
                <Modal isOpen={this.state.modal} toggle={this.toggle} className={this.props.className} fade={false}>
                    <ModalHeader toggle={this.toggle}> <div style={{ display: 'flex', justifyContent: 'center', width: '465px' }}><img width='30%' src="./images/deleteModal.png" alt=".." /></div></ModalHeader>
                    <ModalBody>
                        <div style={{ fontFamily: 'Montserrat, sans-serif', FontSize: '14px', FontWeight: '700', LineHeight: '18.375px' }}>
                            <div style={{ display: 'flex', justifyContent: 'center' }}>
                                <p>√ätes-Vous s√ªr de vouloir supprimer cet enseignant ?</p>
                            </div>
                            <div className='hvr-push' style={{ display: 'flex', justifyContent: 'space-around', marginBottom: '15px' }}>
                                <div><button onClick={() => this.deleteEnseignant()} style={{ backgroundColor: '#00FF7F', borderRadius: '5px', color: 'white' }}><i style={{ color: 'white' }} className="far fa-thumbs-up"></i>Oui</button></div>
                                <div><button onClick={() => this.toggle()} style={{ backgroundColor: '#D9534F', borderRadius: '5px', color: 'white' }}><i style={{ color: 'white' }} className="far fa-thumbs-down"></i>Non</button></div>
                            </div>
                        </div>
                    </ModalBody>
                </Modal>
                <ToastContainer transition={Flip} enableMultiContainer containerId={'A'} position={toast.POSITION.TOP_RIGHT} autoClose={2500} />
            </div>
        );
    }
}

export default Enseignants;
