import React, { Component } from 'react'
import jQuery from 'jquery'
import { Link, BrowserRouter as Router, Route, Switch, withRouter, HashRouter, Redirect } from 'react-router-dom'

class ExplMagasin extends Component {
    constructor(props) {
        super(props);
        this.state = {
            selectedEnseigne: '',
            magasin: '',
            code: '',
            tel: '',
            adresse: '',
            selectedArticle: ''
        }
    }

    componentDidMount() {
        if (JSON.parse((localStorage.getItem('userPermissions'))) != null) {
            var idMagasin = this.props.location.state.idNiveau;
            axios.get(process.env.REACT_APP_API_URL + 'Back_magasin/GetMagasinById.php?id=' + idMagasin).then(res => {
                this.setState({
                    selectedEnseigne: res.data.enseigne,
                    magasin: res.data.magasin,
                    code: res.data.code,
                    tel: res.data.tel,
                    selectedArticle: res.data.articles
                })
            })
        }
        else {
            document.location = '/'
        }
    }

    render() {
        return (
            <div id="page-wrapper" style={{ minHeight: '263px', marginLeft: '0px' }}>
                <div className="container-fluid">
                    <div className="row bg-title">
                        <div className="col-lg-12">
                            <h4 className="page-title">Consultation magasin</h4>
                        </div>
                        {/* /.col-lg-12 */}
                    </div>
                    {/* /.row */}
                    <div className="row">
                        <div className="col-sm-12">
                            <div className="white-box">
                                <div style={{ display: 'flex', justifyContent: 'flex-start', alignItems: 'center', marginBottom: '10px', marginRight: '20px' }}>
                                    <Link to="/Magasins" > <button style={{ background: '#DC143C', border: '1px solid #DC143C', borderRadius: '5px' }} type="button" className="btn btn-info"> <i style={{ paddingRight: '5px' }} className="fas fa-arrow-circle-left"></i>Retour</button></Link>
                                </div>
                                <div className="row">
                                    <div className="col-md-6">
                                        <div className="form-group">
                                            <label>Enseigne</label>
                                            <Select
                                                disabled
                                                value={this.state.selectedEnseigne}
                                                isClearable={true}
                                                isSearchable={true}
                                            />
                                        </div>
                                    </div>
                                    <div className="col-md-6">
                                        <div className="form-group">
                                            <label>Magasin</label>
                                            <input type="text" className="form-control" disabled value={this.state.magasin} style={{ borderRadius: '5px', border: 'solid 1px #B3B3B3' }} />
                                        </div>
                                    </div>
                                    <div className="col-md-6">
                                        <div className="form-group">
                                            <label>Code</label>
                                            <input type="text" className="form-control" disabled value={this.state.code} style={{ borderRadius: '5px', border: 'solid 1px #B3B3B3' }} />
                                        </div>
                                    </div>
                                    <div className="col-md-6">
                                        <div className="form-group">
                                            <label>Tel</label>
                                            <input type="text" className="form-control" disabled value={this.state.tel} style={{ borderRadius: '5px', border: 'solid 1px #B3B3B3' }} />
                                        </div>
                                    </div>
                                    <div className="col-md-6">
                                        <div className="form-group">
                                            <label>Articles</label>
                                            <Select
                                                disabled
                                                value={this.state.selectedArticle}
                                                isMulti={true}
                                                isClearable={true}
                                                isSearchable={true}
                                            />
                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        );
    }
}
export default withRouter(ExplMagasin);
