import React, { Component } from 'react'
import { Link, BrowserRouter as Router, Route, Switch, withRouter, HashRouter, Redirect } from 'react-router-dom'
import DataTable from 'react-data-table-component';
import axios from 'axios'
import jQuery from 'jquery'
import './paginate.css'
import { Button, Modal, ModalHeader, ModalBody, ModalFooter } from 'reactstrap';

class Cycles extends Component {
    constructor(props) {
        super(props);
        this.state = {
            data: [],
            columns: [
                // {
                //     name: 'Cycle',
                //     selector: 'cycle',
                //     sortable: false,
                //     center: true
                // },
                // {
                //     name: 'Heure',
                //     selector: 'heure',
                //     sortable: false,
                //     center: true
                // },
                // {
                //     name: 'Tarif',
                //     selector: 'tarif',
                //     sortable: false,
                //     center: true
                // },
                {
                    name: 'Tarif avec carte',
                    selector: 'carte',
                    sortable: false,
                    center: true
                },
                {
                    name: 'Date cycle',
                    cell: row => <table style={{ marginTop: '10px' }} className="table table-bordered">
                        <thead>
                            <tr>
                                <th className="col-md-2" style={{ textAlign: 'center' }} scope="col">Date debut</th>
                                <th className="col-md-1" style={{ textAlign: 'center' }} scope="col">Date fin</th>
                            </tr>
                        </thead>
                        <tbody>
                            <tr>
                                {row.dateD.map(el =>
                                    <td style={{ textAlign: 'center' }}>{el}</td>
                                )}
                                {row.dateF.map(el =>
                                    <td style={{ textAlign: 'center' }}>{el}</td>
                                )}
                            </tr>
                        </tbody>
                    </table>,
                    ignoreRowClick: true,
                    allowOverflow: true,
                    button: true
                },
                {
                    name: 'Enseignant',
                    cell: row => <table style={{ marginTop: '10px' }} className="table table-bordered">
                        <thead>
                            <tr>
                                <th className="col-md-2" style={{ textAlign: 'center' }} scope="col">Horraire</th>
                                <th className="col-md-1" style={{ textAlign: 'center' }} scope="col">Enseignant</th>
                                <th className="col-md-1" style={{ textAlign: 'center' }} scope="col">Nombre d'heure</th>
                                <th className="col-md-2" style={{ textAlign: 'center' }} scope="col">Honoraire</th>
                            </tr>
                        </thead>
                        <tbody>
                            <tr>
                                {row.horaire.map(el =>
                                    <td style={{ textAlign: 'center' }}>{el}</td>
                                )}
                                {row.nomEnseignant.map(el =>
                                    <td style={{ textAlign: 'center' }}>{el}</td>
                                )}
                                {row.nbrHeures.map(el =>
                                    <td style={{ textAlign: 'center' }}>{el}</td>
                                )}
                                {row.honoraireEnseignant.map(el =>
                                    <td style={{ textAlign: 'center' }}>{el}</td>
                                )}
                            </tr>
                        </tbody>
                    </table>,
                    ignoreRowClick: true,
                    allowOverflow: true,
                    button: true
                },
                {
                    name: 'Modifier',
                    cell: row => <Link to={{
                        pathname: '/ModifCycle',
                        state: { idCycle: row.idCycle }
                    }}><button className='buttonModifier'><i style={{ color: 'white' }} class="fas fa-pencil-alt"></i></button></Link>,
                    ignoreRowClick: true,
                    allowOverflow: true,
                    button: true,
                },
                {
                    name: 'Desactiver',
                    cell: row => this.state.permissionGestionRoles.supprimer == 1 ? (<button className='buttonSupprimer' onClick={() => this.toggletwo(row.idCycle)}><i style={{ color: 'white' }} class="fas fa-trash-alt"></i></button>) : (null),
                    ignoreRowClick: true,
                    allowOverflow: true,
                    button: true,
                },
            ],
            nombreTotalElement: 0,
            filterElement: '',
            mycurrentPage: 1,
            resetFirstPage: false,
            nbrEltperPage: 5,
            modal: false,
            idCycle: '',
            pending: true,
            permissionGestionRoles: ''
        }
        this.newElement = this.newElement.bind(this);
        this.filterSearch = this.filterSearch.bind(this);
    }

    componentDidMount() {
        if (JSON.parse((localStorage.getItem('userPermissions'))) != null) {
            var userPermissions = JSON.parse((localStorage.getItem('userPermissions')));
            var anneeScolaire = userPermissions[0].anneeScolaire
            jQuery.ajax({
                url: process.env.REACT_APP_API_URL + 'Back_mediatraining/Cycles.php',
                type: 'POST',
                data: {
                    query: '',
                    limit: this.state.nbrEltperPage,
                    page: 1,
                    anneeScolaire: anneeScolaire
                },
                dataType: 'json',
                success: (code_html, statut) => {
                    console.log(code_html)
                    this.setState({
                        nombreTotalElement: code_html.total,
                        data: code_html.data,
                        pending: false
                    });
                }
            });
        }
    }

    newElement(page, totalRows) {
        this.setState({
            pending: true
        },
            function () {
                console.log(page)
                console.log(totalRows)
                jQuery.ajax({
                    url: process.env.REACT_APP_API_URL + 'Back_mediatraining/Cycles.php',
                    type: 'POST',
                    data: {
                        query: this.state.filterElement,
                        limit: this.state.nbrEltperPage,
                        page: page
                    },
                    dataType: 'json',
                    success: (code_html, statut) => {
                        console.log(code_html)
                        this.setState({
                            nombreTotalElement: code_html.total,
                            data: code_html.data,
                            pending: false
                        });
                    }
                });
            }.bind(this)
        )
    }

    perPage(currentRowsPerPage, currentPage) {
        this.setState(
            {
                nbrEltperPage: currentRowsPerPage,
                pending: true
            }
            , function () {
                console.log(currentRowsPerPage)
                console.log(currentPage)
                jQuery.ajax({
                    url: process.env.REACT_APP_API_URL + 'Back_mediatraining/Cycles.php',
                    type: 'POST',
                    data: {
                        query: this.state.filterElement,
                        limit: currentRowsPerPage,
                        page: currentPage
                    },
                    dataType: 'json',
                    success: (code_html, statut) => {
                        console.log(code_html)
                        this.setState({
                            nombreTotalElement: code_html.total,
                            data: code_html.data,
                            pending: false
                        });
                    }
                });
            }
        );
    }

    filterSearch(event) {
        this.setState({
            filterElement: event.target.value,
            resetFirstPage: !this.state.resetFirstPage,
            pending: true
        },
            function () {
                jQuery.ajax({
                    url: process.env.REACT_APP_API_URL + 'Back_mediatraining/Cycles.php',
                    type: 'POST',
                    data: {
                        query: this.state.filterElement,
                        limit: this.state.nbrEltperPage,
                        page: 1
                    },
                    dataType: 'json',
                    success: (code_html, statut) => {
                        console.log(code_html)
                        this.setState({
                            nombreTotalElement: code_html.total,
                            data: code_html.data,
                            pending: false
                        });
                    }
                })
            }
        );
    }

    toggletwo = (idCycle) => {
        this.setState(prevState => ({
            modal: !prevState.modal,
            idCycle: idCycle
        }));
    }

    toggle = () => {
        this.setState(prevState => ({
            modal: !prevState.modal
        }));
    }

    deleteCycle() {
        var userPermissions = JSON.parse((localStorage.getItem('userPermissions')));
        var userId = userPermissions[0].idUser
        jQuery.ajax({
            url: process.env.REACT_APP_API_URL + "Back_mediatraining/DeleteCycle.php?id=" + this.state.idCycle + "&userId=" + userId,
            type: 'POST',
            dataType: 'json',
            success: (code_html, statut) => {
                this.setState({
                    modal: false
                }, () => {
                    jQuery.ajax({
                        url: process.env.REACT_APP_API_URL + 'Back_mediatraining/Cycles.php',
                        type: 'POST',
                        data: {
                            query: '',
                            limit: this.state.nbrEltperPage,
                            page: 1
                        },
                        dataType: 'json',
                        success: (code_html, statut) => {
                            console.log(code_html)
                            this.setState({
                                nombreTotalElement: code_html.total,
                                data: code_html.data
                            });
                        }
                    });
                })
            }
        });
    }

    render() {
        return (
            <div id="page-wrapper" style={{ minHeight: '263px', marginLeft: '0px' }}>
                <div className="container-fluid">
                    <div className="row bg-title">
                        <div className="col-lg-12">
                            <h4 className="page-title">Cycles</h4>
                        </div>
                        {/* /.col-lg-12 */}
                    </div>
                    {/* /.row */}
                    <div class="row">
                        <div class="col-sm-12">
                            <div class="white-box">
                                <div style={{ display: 'flex', justifyContent: 'flex-end', alignItems: 'center', marginBottom: '10px', marginRight: '20px' }}>
                                    <Link to="/AjoutCycle" > <button style={{ background: '#3A3F51', border: '1px solid #3A3F51', borderRadius: '5px' }} type="button" className="btn btn-info"> <i class="fas fa-plus"></i>Cycle</button></Link>
                                </div>
                                <div className="mb-0 form-group">
                                    <div className="input-group-alternative input-group">
                                        <div className="input-group-prepend">
                                            <span className="input-group-text">
                                                <i className="fas fa-search"></i>
                                            </span>
                                            <input onChange={(currentRowsPerPage, currentPage) => this.filterSearch(currentRowsPerPage, currentPage)} placeholder="Search" type="text" className="search" />
                                        </div>
                                    </div>
                                </div>
                                <DataTable
                                    columns={this.state.columns}
                                    data={this.state.data}
                                    highlightOnHover={true}
                                    pagination={true}
                                    paginationServer={true}
                                    onChangePage={(page, totalRows) => this.newElement(page, totalRows)}
                                    paginationTotalRows={this.state.nombreTotalElement}
                                    paginationPerPage={this.state.nbrEltperPage}
                                    paginationRowsPerPageOptions={[5, 10, 15, 20, 25]}
                                    onChangeRowsPerPage={(currentRowsPerPage, currentPage) => this.perPage(currentRowsPerPage, currentPage)}
                                    paginationResetDefaultPage={this.state.resetFirstPage}
                                    paginationComponentOptions={{ rowsPerPageText: 'Elements par page:', rangeSeparatorText: 'de', noRowsPerPage: false }}
                                    progressPending={this.state.pending}
                                />
                            </div>
                        </div>
                    </div>
                </div>
                <Modal isOpen={this.state.modal} toggle={this.toggle} className={this.props.className} fade={false}>
                    <ModalHeader toggle={this.toggle}> <div style={{ display: 'flex', justifyContent: 'center', width: '465px' }}><img width='30%' src="./images/deleteModal.png" alt=".." /></div></ModalHeader>
                    <ModalBody>
                        <div style={{ fontFamily: 'Montserrat, sans-serif', FontSize: '14px', FontWeight: '700', LineHeight: '18.375px' }}>
                            <div style={{ display: 'flex', justifyContent: 'center' }}>
                                <p>Êtes-Vous sûr de vouloir supprimer ce cycle ?</p>
                            </div>
                            <div className='hvr-push' style={{ display: 'flex', justifyContent: 'space-around', marginBottom: '15px' }}>
                                <div><button onClick={() => this.deleteCycle()} style={{ backgroundColor: '#00FF7F', borderRadius: '5px', color: 'white' }}><i style={{ color: 'white' }} class="far fa-thumbs-up"></i>Oui</button></div>
                                <div><button onClick={() => this.toggle()} style={{ backgroundColor: '#D9534F', borderRadius: '5px', color: 'white' }}><i style={{ color: 'white' }} class="far fa-thumbs-down"></i>Non</button></div>
                            </div>
                        </div>
                    </ModalBody>
                </Modal>
            </div>
        );
    }
}

export default Cycles;
