    }

    clearStorage = () => {
        localStorage.clear();
        this.props.history.push('/');
    }

    render() {
        return (
            <div id="wrapper">
                {/* Navigation */}
                <nav className="navbar navbar-default navbar-static-top" style={{ marginBottom: 0 }}>
                    <div className="navbar-header" style={{ backgroundColor: '#3a3f51' }}> <a className="navbar-toggle hidden-sm hidden-md hidden-lg " href="javascript:void(0)" data-toggle="collapse" data-target=".navbar-collapse"><i className="ti-menu" /></a>
                        <Link to='/Menu'><div className="top-left-part"><a className="logo"><img style={{ backgroundColor: 'white' }} className="img-circle" width='30px' height='30px' src="images/art_metier.png" alt="Art_Metier" />&nbsp;<span style={{ fontSize: '13px' }} className="hidden-xs">Arts et Métiers</span></a></div></Link>
