import React, { Component } from 'react'
import axios from 'axios'
import jQuery from 'jquery'
import Select from 'react-select';
import { Link, BrowserRouter as Router, Route, Switch, withRouter, HashRouter, Redirect } from 'react-router-dom'
import { Button, Modal, ModalHeader, ModalBody, ModalFooter } from 'reactstrap';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import { Slide, Zoom, Flip, Bounce } from 'react-toastify';

class ModifArticle extends Component {
    constructor(props) {
        super(props);
        this.state = {
            familles: [],
            selectedFamille: '',
            sousFamilles: [],
            selectedSousFamille: '',
            rayons: [],
            societes: [],
            arrayArticles: [],
            ligneArticleSupp: '',
            idArticleSupp: '',
            arrayIdArtSupp: []
        }
    }

    componentDidMount() {
        if (JSON.parse((localStorage.getItem('userPermissions'))) != null) {
            var idSousFamilleArt = this.props.location.state.idSousFamilleArt;
            axios.get(process.env.REACT_APP_API_URL + 'Back_magasin/GetArticleById.php?id=' + idSousFamilleArt).then(res => {
                this.setState({
                    selectedFamille: res.data[0].famille,
                    selectedSousFamille: res.data[0].sousFamille,
                    arrayArticles: res.data
                })
            })
            axios.get(process.env.REACT_APP_API_URL + 'Back_magasin/SelectFamilles.php').then(res => {
                console.log(res)
                this.setState({
                    familles: res.data
                })
            })
            axios.get(process.env.REACT_APP_API_URL + 'Back_magasin/SelectRayons.php').then(res => {
                console.log(res)
                this.setState({
                    rayons: res.data
                })
            })
            axios.get(process.env.REACT_APP_API_URL + 'Back_magasin/SelectSocietes.php').then(res => {
                console.log(res)
                this.setState({
                    societes: res.data
                })
            })
        }
        else {
            document.location = '/'
        }
    }

    updFamille = selectedFamille => {
        if (selectedFamille != null) {
            this.setState({ selectedFamille: selectedFamille }, () => {
                jQuery.ajax({
                    url: process.env.REACT_APP_API_URL + 'Back_magasin/SelectSousFamilles.php',
                    type: 'POST',
                    data: {
                        familleId: selectedFamille.value
                    },
                    dataType: 'json',
                    success: (code_html, statut) => {
                        this.setState({
                            sousFamilles: code_html
                        });
                    }
                });
            });
        }
        else {
            this.setState({ selectedFamille: '' });
        }
    }

    updSousFamille = selectedSousFamille => {
        if (selectedSousFamille != null) {
            this.setState({ selectedSousFamille: selectedSousFamille });
        }
        else {
            this.setState({ selectedSousFamille: '' });
        }
    }

    updRayon = (selectedRayon, index) => {
        var rayon = selectedRayon;
        this.setState({
            arrayArticles: this.state.arrayArticles.map((el, id) => (id === index ? Object.assign(el, { rayon: rayon }) : el))
        });
    }

    updCode = (code, index) => {
        var code = code;
        this.setState({
            arrayArticles: this.state.arrayArticles.map((el, id) => (id === index ? Object.assign(el, { code: code }) : el))
        });
    }

    updEan = (ean, index) => {
        var ean = ean;
        this.setState({
            arrayArticles: this.state.arrayArticles.map((el, id) => (id === index ? Object.assign(el, { ean: ean }) : el))
        });
    }

    updLibelle = (libelle, index) => {
        var libelle = libelle;
        this.setState({
            arrayArticles: this.state.arrayArticles.map((el, id) => (id === index ? Object.assign(el, { libelle: libelle }) : el))
        });
    }

    updSociete = (selectedSociete, index) => {
        var societe = selectedSociete;
        this.setState({
            arrayArticles: this.state.arrayArticles.map((el, id) => (id === index ? Object.assign(el, { societe: societe }) : el))
        });
    }

    updLigneArticle = () => {
        var element = { rayon: '', code: '', ean: '', libelle: '', societe: '' }
        this.setState({
            arrayArticles: [...this.state.arrayArticles, element]
        })
    }

    toggleArticle = (index, idArticleSupp) => {
        this.setState(prevState => ({
            modal: !prevState.modal,
            ligneArticleSupp: index,
            idArticleSupp: idArticleSupp
        }));
    }

    deleteArticle = () => {
        var idArticleSupp = this.state.idArticleSupp
        this.setState({
            arrayIdArtSupp: [...this.state.arrayIdArtSupp, idArticleSupp]
        });
        this.state.arrayArticles.splice(this.state.ligneArticleSupp, 1)
        this.setState(prevState => ({
            modal: !prevState.modal
        }));
    }

    nonDeleteArticle = () => {
        this.setState(prevState => ({
            modal: !prevState.modal
        }));
    }

    render() {
        return (
            <div id="page-wrapper" style={{ minHeight: '263px', marginLeft: '0px' }}>
                <div className="container-fluid">
                    <div className="row bg-title">
                        <div className="col-lg-12">
                            <h4 className="page-title">Modification article</h4>
                        </div>
                        {/* /.col-lg-12 */}
                    </div>
                    {/* /.row */}
                    <div className="row">
                        <div className="col-sm-12">
                            <div className="white-box">
                                <div style={{ display: 'flex', justifyContent: 'flex-start', alignItems: 'center', marginBottom: '10px', marginRight: '20px' }}>
                                    <Link to="/Articles" > <button style={{ background: '#DC143C', border: '1px solid #DC143C', borderRadius: '5px' }} type="button" className="btn btn-info"> <i style={{ paddingRight: '5px' }} className="fas fa-arrow-circle-left"></i>Retour</button></Link>
                                </div>
                                <div className="row">
                                    <div className="col-md-6">
                                        <div className="form-group">
                                            <label>Famille</label>
                                            <Select
                                                value={this.state.selectedFamille}
                                                isClearable={true}
                                                isSearchable={true}
                                                onChange={this.updFamille}
                                                options={this.state.familles} />
                                        </div>
                                    </div>
                                    <div className="col-md-6">
                                        <div className="form-group">
                                            <label>Sous-famille</label>
                                            <Select
                                                value={this.state.selectedSousFamille}
                                                isClearable={true}
                                                isSearchable={true}
                                                onChange={this.updSousFamille}
                                                options={this.state.sousFamilles}
                                            />
                                        </div>
                                    </div>
                                </div>
                                <div className="row">
                                    <table style={{ width: '100%', marginTop: '10px' }} className="table table-bordered">
                                        <thead>
                                            <tr>
                                                <th style={{ textAlign: 'center' }} scope="col">Rayon</th>
                                                <th style={{ textAlign: 'center' }} scope="col">Code</th>
                                                <th style={{ textAlign: 'center' }} scope="col">EAN</th>
                                                <th style={{ textAlign: 'center' }} scope="col">Libellé</th>
                                                <th style={{ textAlign: 'center' }} scope="col">Societé</th>
                                            </tr>
                                        </thead>
                                        {this.state.arrayArticles.map((el, index) =>
                                            <tbody>
                                                <tr>
                                                    <td className="col-md-3"><Select
                                                        value={el.rayon}
                                                        isClearable={true}
                                                        isSearchable={true}
                                                        onChange={(e) => this.updRayon(e, index)}
                                                        options={this.state.rayons}
                                                    />  </td>
                                                    <td style={{ textAlign: 'center' }} className="col-md-1"><input style={{ width: '100%' }} type="text" value={el.code} onChange={(e) => this.updCode(e, index)} /></td>
                                                    <td style={{ textAlign: 'center' }} className="col-md-2"><input style={{ width: '100%' }} type="text" value={el.ean} onChange={(e) => this.updEan(e, index)} /></td>
                                                    <td style={{ textAlign: 'center' }} className="col-md-3"><input style={{ width: '100%' }} type="text" value={el.libelle} onChange={(e) => this.updLibelle(e, index)} /></td>
                                                    <td className="col-md-3"><Select
                                                        value={el.societe}
                                                        isClearable={true}
                                                        isSearchable={true}
                                                        onChange={(e) => this.updSociete(e, index)}
                                                        options={this.state.societes}
                                                    />  </td>
                                                </tr>
                                            </tbody>
                                        )}
                                    </table>
                                </div>
                            </div>
                            <Modal isOpen={this.state.modal} toggle={this.nonDeleteArticle} className={this.props.className} fade={false}>
                                <ModalHeader toggle={this.nonDeleteArticle}> <div style={{ display: 'flex', justifyContent: 'center', width: '465px' }}><img width='30%' src="./images/deleteModal.png" alt=".." /></div></ModalHeader>
                                <ModalBody>
                                    <div style={{ fontFamily: 'Montserrat, sans-serif', FontSize: '14px', FontWeight: '700', LineHeight: '18.375px' }}>
                                        <div style={{ display: 'flex', justifyContent: 'center' }}>
                                            <p>Êtes-Vous sûr de vouloir supprimer cette ligne ?</p>
                                        </div>
                                        <div className='hvr-push' style={{ display: 'flex', justifyContent: 'space-around', marginBottom: '15px' }}>
                                            <div><button onClick={() => this.deleteArticle()} style={{ backgroundColor: '#00FF7F', borderRadius: '5px', color: 'white' }}><i style={{ color: 'white' }} className="far fa-thumbs-up"></i>Oui</button></div>
                                            <div><button onClick={() => this.nonDeleteArticle()} style={{ backgroundColor: '#D9534F', borderRadius: '5px', color: 'white' }}><i style={{ color: 'white' }} className="far fa-thumbs-down"></i>Non</button></div>
                                        </div>
                                    </div>
                                </ModalBody>
                            </Modal>
                        </div>
                    </div>
                </div>
                <ToastContainer transition={Flip} enableMultiContainer containerId={'A'} position={toast.POSITION.TOP_RIGHT} autoClose={2500} />
            </div>
        );
    }
}
export default withRouter(ModifArticle);
