import React, { Component } from 'react'
import jQuery from 'jquery'
import ReactApexChart from 'react-apexcharts'
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";

class StatDemandesTravailleurs extends Component {
    constructor(props) {
        super(props);
        this.state = {
            series: [{
                name: 'Nombre de travailleurs par demande',
                data: []
            }],
            options: {
                chart: {
                    type: 'bar',
                    height: 350,
                    stacked: true,
                    toolbar: {
                        show: true
                    },
                    zoom: {
                        enabled: true
                    }
                },
                responsive: [{
                    breakpoint: 480,
                    options: {
                        legend: {
                            position: 'bottom',
                            offsetX: -10,
                            offsetY: 0
                        }
                    }
                }],
                plotOptions: {
                    bar: {
                        horizontal: false,
                    },
                },
                xaxis: {
                    type: 'text',
                    categories: []
                },
                legend: {
                    position: 'right',
                    offsetY: 40
                },
                fill: {
                    opacity: 1
                }
            },
            startDate: new Date(),
            endDate: new Date()
        };
        this.ajout = this.ajout.bind(this);
    }

    handleStart = d => {
        this.setState({
            startDate: d
        });
    };

    handleEnd = d => {
        this.setState({
            endDate: d
        });
    };

    ajout() {
        const offset = this.state.startDate.getTimezoneOffset();
        var yourDate = new Date(this.state.startDate.getTime() + (offset * 60 * 1000));
        var startDate = yourDate.toISOString().split('T')[0];
        console.log(startDate)
        /////////////////////
        const offseet = this.state.endDate.getTimezoneOffset();
        var yourDatee = new Date(this.state.endDate.getTime() + (offseet * 60 * 1000));
        var endDate = yourDatee.toISOString().split('T')[0];
        console.log(endDate)
        /////////////////////
        jQuery.ajax({
            url: process.env.REACT_APP_API_URL + 'Back_snaii/StatDemandesServices.php',
            type: 'POST',
            data: {
                startDate: startDate,
                endDate: endDate
            },
            dataType: 'json',
            success: function (code_html, statut) {
                console.log(code_html)
                this.setState({
                    options: {
                        ...this.state.options,
                        xaxis: {
                            ...this.state.options.xaxis,
                            categories: code_html[0]
                        }
                    }
                })
                var newSerieTravailleursParDemande = []
                let nombreDeTravailleursParDemande = code_html[1];
                newSerieTravailleursParDemande.push({ data: nombreDeTravailleursParDemande, name: 'Nombre de travailleurs par demande' })
                this.setState({
                    series: newSerieTravailleursParDemande
                })
            }.bind(this)
        });
    }

    render() {
        return (
            <div id="page-wrapper" style={{ minHeight: '263px' }}>
                <div className="container-fluid">
                    <div className="row bg-title">
                        <div className="col-lg-12">
                            <h4 className="page-title">Statistique travailleurs par demande</h4>
                        </div>
                        {/* /.col-lg-12 */}
                    </div>
                    {/* /.row */}
                    <div class="row">
                        <div class="col-sm-12">
                            <div class="white-box">
                                <div style={{ display: 'flex', justifyContent: 'center' }} class="row">
                                    <div class="col-md-3">
                                        <div class="form-group">
                                            <label style={{ marginRight: '5px' }}>Date debut</label>
                                            <DatePicker class="form-control ddate" style={{ border: 'none' }}
                                                selected={this.state.startDate}
                                                onChange={this.handleStart}
                                            />
                                        </div>
                                    </div>
                                    <div class="col-md-3">
                                        <div class="form-group">
                                            <label style={{ marginRight: '5px' }}>Date fin</label>
                                            <DatePicker class="form-control ddate" style={{ border: 'none' }}
                                                selected={this.state.endDate}
                                                onChange={this.handleEnd}
                                            />
                                        </div>
                                    </div>
                                </div>
                                <div style={{ display: 'flex', justifyContent: 'center', marginBottom: '10px' }}>
                                    <button style={{ width: '15%', borderRadius: '5px' }} type="button" className="btn btn-success" onClick={this.ajout.bind(this)}>Confirmer</button>
                                </div>
                                {this.state.series[0].data != '' ?
                                    (<div class="row">
                                        <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center' }}>
                                            <div id="chart">
                                                <ReactApexChart options={this.state.options} series={this.state.series} type="bar" width={700} height={450} />
                                            </div>
                                        </div>
                                    </div>) : (null)}
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        );
    }
}

export default StatDemandesTravailleurs;
