import React, { Component } from 'react'
import axios from 'axios'
import jQuery from 'jquery'
import { withRouter } from 'react-router-dom'
import Select from 'react-select';
import { Button, Modal, ModalHeader, ModalBody, ModalFooter } from 'reactstrap';

class PaiementsEnseignant extends Component {
    constructor(props) {
        super(props);
        this.state = {
            listTypeEnseign: [],
            listTypePay: [],
            honoraireEnseign: [{ typeEnseign: '', typePay: '', honoraire: '' }],
            indexHonEnseignASupprimer: '',
            modal: false
        }
    }

    componentDidMount() {
        axios.get(process.env.REACT_APP_API_URL + 'Back_mediatraining/SelectTypeEns.php').then(res => {
            console.log(res)
            this.setState({
                listTypeEnseign: res.data
            })
        })
        axios.get(process.env.REACT_APP_API_URL + 'Back_mediatraining/SelectTypePaiement.php').then(res => {
            console.log(res)
            this.setState({
                listTypePay: res.data
            })
        })
    }

    additionLign = () => {
        let element = { typeEnseign: '', typePay: '', honoraire: '' }
        this.setState({
            honoraireEnseign: [...this.state.honoraireEnseign, element]
        })
    }

    addTypeEnseign = (selectedTypeEnseign, indexTypeEnseign) => {
        let typeEnseign = selectedTypeEnseign;
        this.setState({
            honoraireEnseign: this.state.honoraireEnseign.map((el, id) => (id === indexTypeEnseign ? Object.assign(el, { typeEnseign: typeEnseign }) : el))
        });
    }

    addTypePay = (selectedTypPay, indexTypePay) => {
        let typePay = selectedTypPay;
        this.setState({
            honoraireEnseign: this.state.honoraireEnseign.map((el, id) => (id === indexTypePay ? Object.assign(el, { typePay: typePay }) : el))
        });
    }

    addHonoraire = (event, indexHonoraire) => {
        let honoraire = event.target.value;
        this.setState({
            honoraireEnseign: this.state.honoraireEnseign.map((el, id) => (id === indexHonoraire ? Object.assign(el, { honoraire: honoraire }) : el))
        });
    }

    toggleHonEnseign = (indexHonEnseign) => {
        this.setState(prevState => ({
            modal: !prevState.modal,
            indexHonEnseignASupprimer: indexHonEnseign
        }));
    }

    deleteHonEnseign = () => {
        this.state.honoraireEnseign.splice(this.state.indexHonEnseignASupprimer, 1)
        this.setState(prevState => ({
            modal: !prevState.modal
        }));
    }

    nonDeleteHonEnseign = () => {
        this.setState(prevState => ({
            modal: !prevState.modal
        }));
    }

    ajout = () => {
        jQuery.ajax({
            url: process.env.REACT_APP_API_URL + 'Back_mediatraining/AddPaiementsEnseignant.php',
            type: 'POST',
            data: {
                honoraireEnseign: this.state.honoraireEnseign
            },
            dataType: 'json',
            success: (code_html, statut) => {
                if (code_html.Result == "OK") {
                    this.props.history.push('/Enseignants');
                }
                else if (code_html.Result == "KO") {
                    alert("erreur,l'un de vos champs est vide!!");
                }
            }
        });
    }

    render() {
        return (
            <div id="page-wrapper" style={{ minHeight: '263px' }}>
                <div className="container-fluid">
                    <div className="row bg-title">
                        <div className="col-lg-12">
                            <h4 className="page-title">Paiements enseignant</h4>
                        </div>
                        {/* /.col-lg-12 */}
                    </div>
                    {/* /.row */}
                    <div class="row">
                        <div class="col-sm-12">
                            <div class="white-box">
                                <div class="row">
                                    <table style={{ width: '100%', marginTop: '10px' }} class="table table-bordered">
                                        <thead>
                                            <tr>
                                                <th style={{ textAlign: 'center' }} scope="col">Type enseignement</th>
                                                <th style={{ textAlign: 'center' }} scope="col">Type paiement</th>
                                                <th style={{ textAlign: 'center' }} scope="col">Honoraire</th>
                                                <th style={{ textAlign: 'center', width: '15%' }} scope="col">Suppression</th>
                                            </tr>
                                        </thead>
                                        {this.state.honoraireEnseign.map((el, index) =>
                                            <tbody>
                                                <tr>
                                                    <td style={{ width: '42%' }}><Select
                                                        value={el.typeEnseign}
                                                        isClearable={true}
                                                        isSearchable={true}
                                                        onChange={(e) => this.addTypeEnseign(e, index)}
                                                        options={this.state.listTypeEnseign}
                                                    />  </td>
                                                    <td style={{ width: '42%' }}><Select
                                                        value={el.typePay}
                                                        isClearable={true}
                                                        isSearchable={true}
                                                        onChange={(e) => this.addTypePay(e, index)}
                                                        options={this.state.listTypePay}
                                                    />  </td>
                                                    <td style={{ textAlign: 'center' }}><input type="text" placeholder='Honoraire' value={el.honoraire} onChange={(e) => this.addHonoraire(e, index)} style={{ textAlign: 'center', height: '38px', borderRadius: '5px' }} /></td>
                                                    <td style={{ textAlign: 'center' }}><button style={{ width: '25%' }} className='buttonSupprimer' onClick={() => this.toggleHonEnseign(index)}><i style={{ color: 'white' }} class="fas fa-trash-alt"></i></button></td>
                                                </tr>
                                            </tbody>
                                        )}
                                    </table>
                                    <div style={{ display: 'flex', justifyContent: 'flex-start', marginLeft: '15px' }}>
                                        <Button style={{ borderRadius: '5px' }} onClick={this.additionLign} color="secondary"><i class="fas fa-plus-circle"></i>Ajouter ligne</Button>
                                    </div>
                                </div>
                                <div style={{ display: 'flex', justifyContent: 'center', marginBottom: '10px' }}>
                                    <button style={{ width: '15%', borderRadius: '5px' }} type="button" className="btn btn-success" onClick={this.ajout}>Confirmer</button>
                                </div>
                                <Modal isOpen={this.state.modal} toggle={this.nonDeleteHonEnseign} className={this.props.className} fade={false}>
                                    <ModalHeader toggle={this.nonDeleteHonEnseign}> <div style={{ display: 'flex', justifyContent: 'center', width: '465px' }}><img width='30%' src="./images/deleteModal.png" alt=".." /></div></ModalHeader>
                                    <ModalBody>
                                        <div style={{ fontFamily: 'Montserrat, sans-serif', FontSize: '14px', FontWeight: '700', LineHeight: '18.375px' }}>
                                            <div style={{ display: 'flex', justifyContent: 'center' }}>
                                                <p>Êtes-Vous sûr de vouloir supprimer cette ligne ?</p>
                                            </div>
                                            <div className='hvr-push' style={{ display: 'flex', justifyContent: 'space-around', marginBottom: '15px' }}>
                                                <div><button onClick={() => this.deleteHonEnseign()} style={{ backgroundColor: '#00FF7F', borderRadius: '5px', color: 'white' }}><i style={{ color: 'white' }} class="far fa-thumbs-up"></i>Oui</button></div>
                                                <div><button onClick={() => this.nonDeleteHonEnseign()} style={{ backgroundColor: '#D9534F', borderRadius: '5px', color: 'white' }}><i style={{ color: 'white' }} class="far fa-thumbs-down"></i>Non</button></div>
                                            </div>
                                        </div>
                                    </ModalBody>
                                </Modal>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        );
    }
}

export default withRouter(PaiementsEnseignant);
