    ajoutRegInscripCycle = () => {
        var sommePaiement = 0
        sommePaiement = parseInt(this.state.mntTotalPayer)
        var sommeReg = 0
        for (var i = 0; i < this.state.tabModPay.length; i++) {
            sommeReg += parseInt(this.state.tabModPay[i].mntModPay)
        }
        if (sommePaiement !== sommeReg) {
            toast.error('⛔ Veuillez vérifier les sommes saisie !', { containerId: 'A' });
        }
        else {
            var userPermissions = JSON.parse((localStorage.getItem('userPermissions')));
            var anneeScolaire = userPermissions[0].anneeScolaire
            var userId = userPermissions[0].idUser
            var arrayModPay = []
            for (var i = 0; i < this.state.tabModPay.length; i++) {
                arrayModPay.push(this.state.tabModPay[i].modPay.value)
            }
            // convert date paiement
            let datePaiement = this.state.datePaiement
            let month = '' + (datePaiement.getMonth() + 1);
            let day = '' + datePaiement.getDate();
            let year = datePaiement.getFullYear();
            if (month.length < 2)
                month = '0' + month;
            if (day.length < 2)
                day = '0' + day;
            var convertDatePaiement = [year, month, day].join('-');
            //
            jQuery.ajax({
                url: process.env.REACT_APP_API_URL + 'Back_mediatraining/AddRegInscripCycl.php',
                type: 'POST',
                data: {
                    datePaiement: convertDatePaiement,
                    reglInsCycle: this.state.reglInsCycle,
                    idInscrip: this.state.reglInsCycle.idInscrip,
                    modPay: this.state.tabModPay,
                    arrayIdModPay: arrayModPay,
                    anneeScolaire: anneeScolaire,
                    userId: userId
                },
                dataType: 'json',
                success: (code_html, statut) => {
                    var idIns = code_html.idIns
                    jQuery.ajax({
                        url: process.env.REACT_APP_API_URL + 'Php_client_jasper/Report_jasper_recu_regl_cycle_inscrip.php',
                        type: 'POST',
                        data: {
                            idIns: idIns
                        },
                        dataType: 'json',
                        success: (code_html, statut) => {
                            var server_path = code_html.server_path;
                            var file_path = code_html.file_path
                            var path = server_path + file_path;
                            window.open(path, "_blank")
                        }
                    });
                    //Test si photo vide
                    if (this.state.photoVerso != null) {
                        let files = this.state.photoVerso;
                        let reader = new FileReader();
                        reader.readAsDataURL(files[0])
                        reader.onload = (e) => {
                            console.log(e)
                            jQuery.ajax({
                                url: process.env.REACT_APP_API_URL + 'Back_mediatraining/UploadphotoInscripCycle.php',
                                type: 'POST',
                                data: {
                                    file: e.target.result,
                                    nomfile: this.state.photoVerso[0].name
                                },
                                dataType: 'json',
                                success: (code_html, statut) => {
                                    this.props.history.push('/ReglApprenantCycle');
                                }
                            }
                            )
                        }
                    }
                }
            });
        }
    }

