import React, { Component } from "react";
import jQuery from "jquery";
import Select from "react-select";
import { withRouter } from "react-router-dom";

class ModifNivNatureNote extends Component {
  constructor(props) {
    super(props);
    this.state = {
      listNiveau: [],
      selectedNiveau: '',
      selectedMatiere: '',
      selectedNatNote: ''
    };
  }

  componentDidMount() {
    if (JSON.parse(localStorage.getItem("userPermissions")) != null) {
      var niveauNoteId = this.props.location.state.niveauNoteId;
      var userPermissions = JSON.parse(localStorage.getItem("userPermissions"));
      var anneeScolaire = userPermissions[0].anneeScolaire;
      jQuery.ajax({
        url:
          process.env.REACT_APP_API_URL +
          "Back_mediatraining/GetNivNatNoteById.php",
        type: "POST",
        data: {
          niveauNoteId: niveauNoteId,
        },
        dataType: "json",
        success: (code_html, statut) => {
          console.log(code_html);
          this.setState({
            selectedNiveau: code_html[0],
            selectedMatiere: code_html[1],
            selectedNatNote: code_html[2]
          });
        },
      });
