import React, { Component, Fragment } from 'react'
import axios from 'axios'
import jQuery from 'jquery'
import { withRouter } from 'react-router-dom'
import Select from 'react-select';
import { RadioGroup, Radio } from 'react-radio-group'
import Checkbox from 'rc-checkbox';
import 'rc-checkbox/assets/index.css';
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";

const groupStyles = {
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'space-between',
};
const groupBadgeStyles = {
    backgroundColor: '#EBECF0',
    borderRadius: '2em',
    color: '#172B4D',
    display: 'inline-block',
    fontSize: 12,
    fontWeight: 'normal',
    lineHeight: '1',
    minWidth: 1,
    padding: '0.16666666666667em 0.5em',
    textAlign: 'center',
};

const formatGroupLabel = data => (
    <div style={groupStyles}>
        <span>{data.label}</span>
        <span style={groupBadgeStyles}>{data.options.length}</span>
    </div>
);

class InscripCycle extends Component {
    constructor(props) {
        super(props);
        this.state = {
            listConnaissance: [],
            numInscripCycle: '',
            numCarteAdh: '',
            listTypeFormat: [{ value: 1, label: 'Societé' }, { value: 2, label: 'Particulier' }],
            selectedTypeFiche: 2,
            nbrPersonne: 0,
            matFisc: '',
            nbrPersTenu: 0,
            societe: '',
            responsable: '',
            mobileResp: '',
            age: '',
            lieuResid: [],
            seletedLieuResid: '',
            listNivBase: [],
            selectedNivBase: '',
            facebook: '',
            fonction: '',
            selectedTypeFormat: 2,
            motivation: '',
            numIdentite: '',
            prenom: '',
            nom: '',
            disabled: false,
            connaissChoisi: [],
            listCycle: [],
            selectedCycle: [],
            email: '',
            tel: '',
            fax: '',
            photoVerso: null,
            nbrHeure: 0,
            blockReglement: false,
            carteFid: '',
            tenu: '',
            propCycle: '',
            reglInsCycle: [],
            dateCarte: new Date(),
            dateTenu: new Date(),
            dateMntCycle: new Date(),
            societeCoch: false,
            tva: ''
        }
    }

    componentDidMount() {
        var userPermissions = JSON.parse((localStorage.getItem('userPermissions')));
        var anneeScolaire = userPermissions[0].anneeScolaire
        jQuery.ajax({
            url: process.env.REACT_APP_API_URL + 'Back_mediatraining/NumInscripCycle.php',
            type: 'POST',
            data: {
                anneeScolaire: anneeScolaire
            },
            dataType: 'json',
            success: (code_html, statut) => {
                this.setState({
                    numInscripCycle: "00000" + (parseInt(code_html) + 1).toString()
                })
            }
        });
        jQuery.ajax({
            url: process.env.REACT_APP_API_URL + 'Back_mediatraining/NumAdheCycle.php',
            type: 'POST',
            data: {
                anneeScolaire: anneeScolaire
            },
            dataType: 'json',
            success: (code_html, statut) => {
                this.setState({
                    numCarteAdh: "00" + (parseInt(code_html) + 1).toString(),
                    carteFid: "00" + (parseInt(code_html) + 1).toString()
                })
            }
        });
        axios.get(process.env.REACT_APP_API_URL + 'Back_mediatraining/SelectResid.php').then(res => {
            this.setState({
                lieuResid: res.data
            })
        })
        axios.get(process.env.REACT_APP_API_URL + 'Back_mediatraining/SelectNivBase.php').then(res => {
            console.log(res)
            this.setState({
                listNivBase: res.data
            })
        })
        axios.get(process.env.REACT_APP_API_URL + 'Back_mediatraining/SelectConnaissance.php').then(res => {
            console.log(res)
            this.setState({
                listConnaissance: res.data
            })
        })
        jQuery.ajax({
            url: process.env.REACT_APP_API_URL + 'Back_mediatraining/SelectDetailCycle.php',
            type: 'POST',
            data: {
                anneeScolaire: anneeScolaire
            },
            dataType: 'json',
            success: (code_html, statut) => {
                this.setState({
                    listCycle: code_html
                })
            }
        });
    }

    addType = (selectedTypeFiche) => {
        this.setState({ selectedTypeFiche: selectedTypeFiche });
    }

    addCarteFid = (e) => {
        console.log('Checkbox checked:', (e.target.checked));
        if (e.target.checked === true) {
            this.setState({
                carteFid: ''
            });
        }
        else {
            var numCarteFid = "00" + (parseInt(this.state.numCarteAdh) + 1).toString()
            this.setState({
                carteFid: numCarteFid
            });
        }
    }

    addTenu = (e) => {
        console.log('Checkbox checked:', (e.target.checked));
        if (e.target.checked === true) {
            this.setState({
                tenu: true
            });
        }
        else {
            this.setState({
                tenu: false
            });
        }
    }

    addNbrPersonne = (event) => {
        this.setState({
            nbrPersonne: event.target.value
        });
    }

    addMatFisc = (event) => {
        this.setState({
            matFisc: event.target.value
        });
    }

    addNbrPersTenu = (event) => {
        this.setState({
            nbrPersTenu: event.target.value
        });
    }

    addSociete = (event) => {
        this.setState({
            societe: event.target.value
        });
    }

    addResponsable = (event) => {
        this.setState({
            responsable: event.target.value
        });
    }

    addMobile = (event) => {
        this.setState({
            mobileResp: event.target.value
        });
    }

    addAge = (event) => {
        this.setState({
            age: event.target.value
        });
    }

    addLieuResid = (seletedLieuResid) => {
        this.setState({ seletedLieuResid: seletedLieuResid });
    }

    addNivBase = selectedNivBase => {
        this.setState({ selectedNivBase });
    };

    addEmail = (event) => {
        this.setState({
            email: event.target.value
        });
    }

    addTel = (event) => {
        this.setState({
            tel: event.target.value
        });
    }

    addFax = (event) => {
        this.setState({
            fax: event.target.value
        });
    }

    addFacebook = (event) => {
        this.setState({
            facebook: event.target.value
        });
    }

    addFct = (event) => {
        this.setState({
            fonction: event.target.value
        });
    }

    addPhotoVerso(e) {
        this.setState({ photoVerso: e.target.files })
    }

    addTypeFormat = (selectedTypeFormat) => {
        this.setState({ selectedTypeFormat: selectedTypeFormat });
    }

    addMotivation = (event) => {
        this.setState({
            motivation: event.target.value
        });
    }

    addNumIdentite = (event) => {
        this.setState({
            numIdentite: event.target.value
        });
    }

    addPrenom = (event) => {
        this.setState({
            prenom: event.target.value
        });
    }

    addNom = (event) => {
        this.setState({
            nom: event.target.value
        });
    }

    addConnaiss = (e, el) => {
        console.log('Checkbox checked:', (e.target.checked));
        if (e.target.checked === true) {
            let element = el.value
            this.setState({
                connaissChoisi: [...this.state.connaissChoisi, element]
            });
        }
        else {
            let element = el.value
            let indice = this.state.connaissChoisi.indexOf(element)
            this.state.connaissChoisi.splice(indice, 1)
        }
    }

    addCycle = selectedCycle => {
        this.setState({ selectedCycle });
    };

    addNbrHeure = (event) => {
        this.setState({
            nbrHeure: event.target.value
        });
    }

    addPropCycle = (event) => {
        this.setState({
            propCycle: event.target.value
        });
    }


    addTva = (e) => {
        console.log('Checkbox checked:', (e.target.checked));
    }

    ajoutInscripCycle = () => {
        var userPermissions = JSON.parse((localStorage.getItem('userPermissions')));
        var anneeScolaire = userPermissions[0].anneeScolaire
        var userId = userPermissions[0].idUser
        // Test si photo vide
        var pathPhotoVerso = '';
        if (this.state.photoVerso != null) {
            pathPhotoVerso = "images/Photo_eleves_cycles/" + this.state.photoVerso[0].name
        }
        var detailCycles = []
        for (var y = 0; y < this.state.selectedCycle.length; y++) {
            detailCycles.push(this.state.selectedCycle[y].value)
        }
        detailCycles = detailCycles.toString()
        var conaissanceChoix = this.state.connaissChoisi.toString()
        if (this.state.selectedTypeFiche == 1) {
            jQuery.ajax({
                url: process.env.REACT_APP_API_URL + 'Back_mediatraining/AddInscripCycle.php',
                type: 'POST',
                data: {
                    numIdentite: this.state.numIdentite,
                    prenom: this.state.prenom,
                    numInscripCycle: this.state.numInscripCycle,
                    numCarteFid: this.state.carteFid,
                    matFisc: this.state.matFisc,
                    nbrPersonne: this.state.nbrPersonne,
                    nbrPersTenu: this.state.nbrPersTenu,
                    nom: this.state.societe,
                    responsable: this.state.responsable,
                    mobileResp: this.state.mobileResp,
                    age: this.state.age,
                    lieuResidId: this.state.seletedLieuResid.value,
                    niveauBase: this.state.selectedNivBase.value,
                    email: this.state.email,
                    tel: this.state.tel,
                    fax: this.state.fax,
                    facebook: this.state.facebook,
                    fctActu: this.state.fonction,
                    pathPhoto: pathPhotoVerso,
                    typeFormat: this.state.selectedTypeFormat,
                    typeFiche: this.state.selectedTypeFiche,
                    nbrHeure: this.state.nbrHeure,
                    motivation: this.state.motivation,
                    connaissChoisi: conaissanceChoix,
                    cycleChoisi: detailCycles,
                    anneeScolaire: anneeScolaire,
                    userId: userId
                },
                dataType: 'json',
                success: (code_html, statut) => {
                    if (code_html.Result == "OK") {
                        this.setState({
                            blockReglement: true,
                            reglInsCycle: code_html.regCycl,
                            societeCoch: code_html.regCycl[0].societeCoch,
                            tva: code_html.regCycl[0].tva
                        });
                    }
                    else {
                        alert("prbl lors de l'insertion")
                    }
                }
            });
        }
        else if (this.state.selectedTypeFiche == 2) {
            var persAvecTenu = 0
            if (this.state.tenu == true) {
                var persAvecTenu = 1
            }
            jQuery.ajax({
                url: process.env.REACT_APP_API_URL + 'Back_mediatraining/AddInscripCycle.php',
                type: 'POST',
                data: {
                    responsable: this.state.responsable,
                    mobileResp: this.state.mobileResp,
                    nbrHeure: this.state.nbrHeure,
                    numInscripCycle: this.state.numInscripCycle,
                    numCarteFid: this.state.carteFid,
                    numIdentite: this.state.numIdentite,
                    prenom: this.state.prenom,
                    nom: this.state.nom,
                    age: this.state.age,
                    nbrPersonne: 1,
                    nbrPersTenu: persAvecTenu,
                    lieuResidId: this.state.seletedLieuResid.value,
                    niveauBase: this.state.selectedNivBase.value,
                    email: this.state.email,
                    tel: this.state.tel,
                    fax: this.state.fax,
                    facebook: this.state.facebook,
                    fctActu: this.state.fonction,
                    pathPhoto: pathPhotoVerso,
                    typeFormat: this.state.selectedTypeFormat,
                    typeFiche: this.state.selectedTypeFiche,
                    motivation: this.state.motivation,
                    connaissChoisi: conaissanceChoix,
                    cycleChoisi: detailCycles,
                    anneeScolaire: anneeScolaire,
                    userId: userId
                },
                dataType: 'json',
                success: (code_html, statut) => {
                    if (code_html.Result == "OK") {
                        this.setState({
                            blockReglement: true,
                            reglInsCycle: code_html.regCycl,
                            societeCoch: code_html.regCycl[0].societeCoch,
                            tva: code_html.regCycl[0].tva
                        }
                        );
                    }
                    else {
                        alert("prbl lors de l'insertion")
                    }
                }
            });
        }
        // Test si photo vide
        // if (this.state.photoVerso != null) {
        //     let files = this.state.photoVerso;
        //     console.warn('data files', files)
        //     let reader = new FileReader();
        //     reader.readAsDataURL(files[0])
        //     reader.onload = (e) => {
        //         console.log(e)
        //         jQuery.ajax({
        //             url: process.env.REACT_APP_API_URL + 'Back_mediatraining/UploadphotoInscripCycle.php',
        //             type: 'POST',
        //             data: {
        //                 file: e.target.result,
        //                 nomfile: this.state.photoVerso[0].name
        //             },
        //             dataType: 'json',
        //             success: (code_html, statut) => {
        //             }
        //         }
        //         )
        //     }
        // }
    }

    render() {
        if (this.state.blockReglement == true) {
            if ((this.state.reglInsCycle[0].carte != null) && (this.state.reglInsCycle[0].tenu != null)) {
                var paiementInscrip = (
                    <Fragment>
                        <h3 style={{ paddingLeft: '15px' }}>Paiement reglement :</h3>
                        <table style={{ marginTop: '10px' }} className="table table-bordered">
                            <thead>
                                <tr>
                                    <th colSpan="3" style={{ textAlign: 'center' }} scope="col">Acquittement des frais</th>
                                </tr>
                                <tr>
                                    <th style={{ textAlign: 'center' }} scope="col">Infos Cycle</th>
                                    <th style={{ textAlign: 'center' }} scope="col">Date</th>
                                    <th style={{ textAlign: 'center' }} scope="col">Montant</th>
                                </tr>
                            </thead>
                            <tbody>
                                {this.state.reglInsCycle.map((el, index) =>
                                    <Fragment>
                                        <tr>
                                            <td style={{ textAlign: 'center' }}>{"Carte Num° " + el.numCarteFid}</td>
                                            <td style={{ textAlign: 'center' }}><DatePicker class="form-control ddate" selected={this.state.dateCarte} style={{ border: 'none' }} /></td>
                                            <td style={{ textAlign: 'center' }}><input type="number" className="form-control" value={el.carte} onChange={this.updMntPayCarte} style={{ borderRadius: '5px', border: 'solid 1px', borderColor: '#B3B3B3', textAlign: 'center' }} /></td>
                                        </tr>
                                        <tr>
                                            <td style={{ textAlign: 'center' }}>Tenu</td>
                                            <td style={{ textAlign: 'center' }}><DatePicker class="form-control ddate" selected={this.state.dateTenu} style={{ border: 'none' }} /></td>
                                            <td style={{ textAlign: 'center' }}><input type="number" className="form-control" value={el.tenu} onChange={this.updMntPayTenu} style={{ borderRadius: '5px', border: 'solid 1px', borderColor: '#B3B3B3', textAlign: 'center' }} /></td>
                                        </tr>
                                        <tr>
                                            <td style={{ textAlign: 'center' }}><p>{"cycle: " + el.cycle}</p><p>{"Période: " + el.dateD + "---" + el.dateF + " " + el.horaire}</p></td>
