                                        </tr>
                                        <tr>
                                            <td style={{ textAlign: 'center' }}><p>{"cycle: " + el.cycle}</p><p>{"Période: " + el.dateD + "---" + el.dateF + " " + el.horaire}</p></td>
                                            <td style={{ textAlign: 'center' }}><DatePicker class="form-control ddate" selected={this.state.dateMntCycle} style={{ border: 'none' }} /></td>
                                            <td style={{ textAlign: 'center' }}><input type="number" className="form-control" value={el.mnt} onChange={this.updMntPayCycle} style={{ borderRadius: '5px', border: 'solid 1px', borderColor: '#B3B3B3', textAlign: 'center', textAlign: 'center' }} /></td>
                                        </tr>
                                    </Fragment>)}
