import React, { Component } from 'react'
import axios from 'axios'
import jQuery from 'jquery'
import { withRouter } from 'react-router-dom'
import Select from 'react-select';

class AjoutUtilisateur extends Component {
    constructor(props) {
        super(props);
        this.state = {
            nomUtilisateur: '',
            Roles: [],
            selectedRole: ''
        }
        this.addnomUtilisateur = this.addnomUtilisateur.bind(this);
        this.ajout = this.ajout.bind(this)
    }

    componentDidMount() {
        axios.get(process.env.REACT_APP_API_URL + 'Back_agora/RolesSelect.php').then(res => {
            console.log(res)
            this.setState({
                Roles: res.data
            })
        })
    }

    addnomUtilisateur(event) {
        this.setState({
            nomUtilisateur: event.target.value
        });
    }

    addRole = selectedRole => {
        this.setState({ selectedRole });
        console.log(`Role selected:`, selectedRole);
    };

    ajout() {
        jQuery.ajax({
            url: process.env.REACT_APP_API_URL + 'Back_agora/AddUtilisateur.php',
            type: 'POST',
            data: {
                nomUtilisateur: this.state.nomUtilisateur,
                idRole: this.state.selectedRole.value
            },
            dataType: 'json',
            success: (code_html, statut) => {
                if (code_html.Result == "OK") {
                    this.props.history.push('/Utilisateurs');
                }
                else if (code_html.Result == "KO") {
                    alert("erreur,l'un de vos champs est vide!!");
                }
            }
        });
    }

    render() {
        return (
            <div id="page-wrapper" style={{ minHeight: '263px' }}>
                <div className="container-fluid">
                    <div className="row bg-title">
                        <div className="col-lg-12">
                            <h4 className="page-title">Ajout utilisateur</h4>
                        </div>
                        {/* /.col-lg-12 */}
                    </div>
                    {/* /.row */}
                    <div class="row">
                        <div class="col-sm-12">
                            <div class="white-box">
                                <div class="row">
                                    <div class="col-md-6">
                                        <div class="form-group">
                                            <label>Nom utilisateur</label>
                                            <input type="text" class="form-control" placeholder="Nom utilisateur" onChange={this.addnomUtilisateur.bind(this)} style={{ borderRadius: '5px' }} />
                                        </div>
                                    </div>
                                    <div class="col-md-6">
                                        <div class="form-group">
                                            <label>Role</label>
                                            <Select
                                                isClearable={true}
                                                isSearchable={true}
                                                onChange={this.addRole}
                                                options={this.state.Roles}
                                            />
                                        </div>
                                    </div>
                                </div>
                                <div style={{ display: 'flex', justifyContent: 'center', marginBottom: '10px' }}>
                                    <button style={{ width: '15%', borderRadius: '5px' }} type="button" className="btn btn-success" onClick={this.ajout.bind(this)}>Confirmer</button>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        );
    }
}

export default withRouter(AjoutUtilisateur);
