import React, { Component } from 'react'
import axios from 'axios'
import jQuery from 'jquery'
import { Link, BrowserRouter as Router, Route, Switch, withRouter, HashRouter, Redirect } from 'react-router-dom'
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import { Slide, Zoom, Flip, Bounce } from 'react-toastify';
import Select from 'react-select';

class Planning extends Component {
    constructor(props) {
        super(props);
        this.state = {
            marchandiseurs: [],
            selectedMarchandiseur: '',
            dateDebut: '',
            dateFin: '',
            arrayPlanif: [],
            magasins: []
        }
    }

    componentDidMount() {
        if (JSON.parse((localStorage.getItem('userPermissions'))) != null) {
            axios.get(process.env.REACT_APP_API_URL + 'Back_magasin/SelectMarchandiseurs.php').then(res => {
                this.setState({
                    marchandiseurs: res.data
                })
            })
        }
        else {
            document.location = '/'
        }
    }

    addDateDebut = d => {
        this.setState({
            dateDebut: d
        });
    };

    addDateFin = d => {
        this.setState({
            dateFin: d
        });
    };

    planif = () => {
        if (this.state.dateDebut != "" && this.state.dateFin != "") {
            // date debut
            var dateDebut = this.state.dateDebut
            let monthOne = '' + (dateDebut.getMonth() + 1);
            let dayOne = '' + dateDebut.getDate();
            let yearOne = dateDebut.getFullYear();
            if (monthOne.length < 2)
                monthOne = '0' + monthOne;
            if (dayOne.length < 2)
                dayOne = '0' + dayOne;
            // date fin
            var convertDateDebut = [yearOne, monthOne, dayOne].join('-');
            var dateFin = this.state.dateFin
            let monthTwo = '' + (dateFin.getMonth() + 1);
            let dayTwo = '' + dateFin.getDate();
            let yearTwo = dateFin.getFullYear();
            if (monthTwo.length < 2)
                monthTwo = '0' + monthTwo;
            if (dayTwo.length < 2)
                dayTwo = '0' + dayTwo;
            var convertDateFin = [yearTwo, monthTwo, dayTwo].join('-');
            //
            jQuery.ajax({
                url: process.env.REACT_APP_API_URL + 'Back_magasin/Planning.php',
                type: 'POST',
                data: {
                    dateDebut: convertDateDebut,
                    dateFin: convertDateFin
                },
                dataType: 'json',
                success: (code_html, statut) => {
                    this.setState({
                        arrayPlanif: code_html
                    });
                }
            });
        }
        else {
            toast.error('⛔ Veuillez Verifier choisir une date debut et fin !!', { containerId: 'A' });
        }
    }

    addMarchandiseur = selectedMarchandiseur => {
        if (selectedMarchandiseur != null) {
            this.setState({ selectedMarchandiseur: selectedMarchandiseur });
        }
        else {
            this.setState({ selectedMarchandiseur: '' });
        }
    }

    render() {
        return (
            <div id="page-wrapper" style={{ minHeight: '263px', marginLeft: '0px' }}>
                <div className="container-fluid">
                    <div className="row bg-title">
                        <div className="col-lg-12">
                            <h4 className="page-title">Planning</h4>
                        </div>
                        {/* /.col-lg-12 */}
                    </div>
                    {/* /.row */}
                    <div className="row">
                        <div className="col-sm-12">
                            <div className="white-box">
                                <div className="row">
                                    <div className="col-md-6">
                                        <div className="form-group">
                                            <label>Date debut</label>
                                            <DatePicker class="form-control ddate" style={{ border: 'none' }}
                                                selected={this.state.dateDebut}
                                                onChange={this.addDateDebut}
                                                dateFormat="dd/MM/yyy"
                                            />
                                        </div>
                                    </div>
                                    <div className="col-md-6">
                                        <div className="form-group">
                                            <label>Date fin</label>
                                            <DatePicker class="form-control ddate" style={{ border: 'none' }}
                                                selected={this.state.dateFin}
                                                onChange={this.addDateFin}
                                                dateFormat="dd/MM/yyy"
                                            />
                                        </div>
                                    </div>
                                </div>
                                <div style={{ display: 'flex', justifyContent: 'center' }}>
                                    <div>
                                        <button style={{ borderRadius: '5px', backgroundColor: '#761C19', borderColor: '#761C19' }} type="button" className="btn btn-success" onClick={this.planif}>Planifier</button>
                                    </div>
                                </div>
                                {this.state.arrayPlanif.length > 0 ?
                                    (<div style={{ marginTop: '10px' }} className="row">
                                        <table className="table table-bordered">
                                            <thead >
                                                <tr>
                                                    <th style={{ textAlign: 'left' }} scope="col">Date</th>
                                                    <th style={{ textAlign: 'left' }} scope="col">Marchandiseur</th>
                                                    <th style={{ textAlign: 'left' }} scope="col">Magasin</th>
                                                </tr>
                                            </thead>
                                            <tbody>
                                                {this.state.arrayPlanif.map(el =>
                                                    <tr>
                                                        <td>
                                                            {el}
                                                        </td>
                                                        <td style={{ textAlign: 'center' }}>
                                                            <div>
                                                                <table className="table table-bordered">
                                                                    <tbody>
                                                                        <tr>
                                                                            <td>
                                                                                <div>
                                                                                    <Select
                                                                                        isClearable={true}
                                                                                        isSearchable={true}
                                                                                        onChange={(e) => this.addMarchandiseur(e)}
                                                                                        options={this.state.marchandiseurs}
                                                                                    />
                                                                                </div>
                                                                            </td>
                                                                            <td>
                                                                                <div>
                                                                                    <Select
                                                                                        isClearable={true}
                                                                                        isSearchable={true}
                                                                                        onChange={(e) => this.addMagasin(e)}
                                                                                        options={this.state.magasins}
                                                                                    />
                                                                                </div>
                                                                            </td>
                                                                            <td>
                                                                                <button style={{ backgroundColor: '#D9534F', borderRadius: '5px' }} onClick={() => this.toggleLigneMarchandiseur()}><i style={{ color: 'white' }} className="fas fa-trash-alt"></i></button>
                                                                            </td>
                                                                        </tr>
                                                                    </tbody>
                                                                </table>
                                                            </div>
                                                        Ajout
                                                        <i onClick={() => this.additionMarchandiseur()} className="fas fa-plus-circle"></i>
                                                        </td>
                                                    </tr>
                                                )}
                                            </tbody>
                                        </table>
                                    </div>) :
                                    (null)}
                            </div>
                        </div>
                    </div>
                    <ToastContainer transition={Flip} enableMultiContainer containerId={'A'} position={toast.POSITION.TOP_RIGHT} autoClose={2500} />
                </div>
            </div>
        );
    }
}

export default withRouter(Planning);
